INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("オークチーフ", 13, "【物理】特異な力で変質したオークリーダー。\n巨大な盾で、攻防一体の一撃を繰り出す。\n\n・物理攻撃力ダウン効果の影響を受けやすい。\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・オークチーフから受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、最も近い敵３キャラに\n　オークチーフの物理攻撃力に応じた物理固定ダ\n　メージを与え、スタン状態を付与する。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・最も近い敵２キャラのＴＰを小ダウンさせ、\n　ＨＰ吸収を特大ダウンさせる。さらに自身の\n　ＴＰを小回復し、行動速度を中アップさせる。", 270, 0, 1, 300701, 300701, 0, 1.5, 0, 1000, 0);
