INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("オークチーフ", 13, "【物理】特異な力で変質したオーク\nリーダー。巨大な盾で、攻防一体の\n一撃を繰り出す。\n\n・ユニオンバーストは、前方\n　３キャラに物理大ダメージを\n　与え、さらにスタンさせる。\n\n・前方２キャラの物理防御力を\n　大ダウン。", 270, 0, 1, 300703, 300703, 0, 1.5, 0, 1000, 0);
