INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("ハイ・ガルグ", 16, "【物理】ルナの魔力を帯び変異した獰猛な魔獣。\n強靭な歯牙は歴戦の冒険者さえ容易く切り裂く。\n\n・ユニオンバーストは敵全体に物理大ダメージを\n　与え、さらに自分のＨＰを回復する。\n・後方範囲内の敵に物理ダメージを与え、スタン\n　させる。\n　さらに敵全体の物理防御力を中ダウンさせる。\n・前から３キャラに物理ダメージを与え、\n　毒状態にする。", 270, 0, 1, 305300, 305300, 0, 1.8, 0, 1000, 0);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("アイスガルグ", 16, "【物理】雪山に棲む獰猛な魔狼。\n強靭な歯牙は歴戦の冒険者さえ\n容易く氷塊の墓標へ変える。\n\n・ユニオンバーストは敵全体に物\n　理大ダメージを与える。\n\n・一番後ろの敵キャラを中心とし\n　て、範囲内の敵すべてに物理小\n　ダメージを与え、スタンさせる。\n　さらに敵全体の物理防御力を中\n　ダウンさせる。\n\n・前方範囲内の敵すべてに物理小\n　ダメージを与え、毒状態にする。", 270, 0, 1, 305301, 305301, 0, 1.8, 0, 1000, 0);
