INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("アンチビースト", 16, "【物理】愛の力を求め顕現した、負の魔法生物。\n皆の心の中の愛の力を歪め世界に災いをもたらす。\n\n・ユニオンバーストは、異次元より取り出した\n　アイテムで攻撃を仕掛ける。アイテムによって\n　効果が変化する。\n　ハンマーの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、物理攻撃力を\n　大幅にダウンさせる。\n　シンバルの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、魔法攻撃力を\n　大幅にダウンさせる。\n　ボムの場合、敵全体にキャラの最大ＨＰに\n　応じた物理割合ダメージを与え、\n　呪い状態にする。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・一番前の敵を中心に、範囲内の敵すべてに\n　物理中ダメージを与え、さらにノックバック\n　させる。\n・一定範囲内の敵すべてに物理大ダメージを\n　与える。", 270, 0, 1, 310301, 310300, 0, 1.8, 0, 3000, 0);
