INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("ラウドラパーン", 16, "【ＭＯＤＥ１】ニュージェネレーションズの\nシャドウを召喚。森で出会った大ウサギ。\n３人は夢をあきらめない心を貫き通せるか、\n大ウサギは各個の弱さを見せつける。\n\n・一番前のキャラに物理ダメージを与える。", 270, 0, 1, 310504, 310500, 0, 1.6, 0, 3000, 0);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("ラウドラパーン", 16, "【ＭＯＤＥ２】森の人気者の歌声に魔物たちが\n大挙する。血沸き肉躍る野生の宴会が今、\n開幕する。\n\n・ブルーゴーレム、スリーピィオウル、\n　ツキツキを倒すと次のＭＯＤＥに移行する。\n\n・ラウドラパーンは、一番前のキャラに\n　物理ダメージを与える。", 270, 0, 1, 310505, 310500, 0, 1.6, 0, 3000, 0);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("ラウドラパーン", 16, "【ＭＯＤＥ３】宴も終焉、大兎が最後の力を振り\n絞る。咆哮にも似た壊震の歌声が、３人の夢道を\n阻む。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、\n　ノックバックさせる。その後、目を\n　回している間、自身の物理・魔法防御力が\n　大幅にダウンする。\n・一番前のキャラを中心として、一定範囲に\n　物理中ダメージを与え、さらに行動速度を\n　ダウンさせる。\n・前から３キャラに物理小ダメージを与え、\n　さらに物理防御力を大ダウンさせる。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる。", 270, 0, 1, 310506, 310500, 0, 1.6, 0, 3000, 0);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("バニフォルテ", 16, "【物理】世にも奇妙な歌う大ウサギ\nの魔獣。その歌声はとても褒められ\nたものではなく、甚だ迷惑である。\n\n・ユニオンバーストは、敵全体に\n　キャラの最大ＨＰに応じた物理割\n　合ダメージを与え、ノックバック\n　させる。その後、目を回している\n　間、自身の物理防御力と魔法防御\n　力を特大ダウン。\n\n・一番前のキャラを中心として、\n　一定範囲に物理中ダメージを与\n　え、さらに行動速度を特大ダウン\n　とスタンさせる。\n\n・前から３キャラに物理小ダメージ\n　を与え、さらに物理防御力を大ダ\n　ウンさせる。", 270, 0, 1, 310507, 310507, 0, 1.6, 0, 3000, 0);
