INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("忍びの頭領ハンゾウ", 16, "【ＭＯＤＥ２】秘伝の口寄せで忍蛙に化け、\nガマの猛毒をあたり一帯に噴霧する。その猛毒は\nひと吸いで全身を蝕み、徐々に躰を爛れ落とす。\n\n・バトル開始後、監査官が登場する。監査官がダ\n　メージを受けると、その直後に忍びの頭領ハン\n　ゾウの物理防御力と魔法防御力を特大ダウンさ\n　せる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与える。\n・大ガマに変化して猛毒を吐き、敵全体を猛毒状\n　態にする。この攻撃は必ず命中する。\n・敵全体に物理中ダメージを与え、さらに敵全体\n　をスタンさせる。", 270, 0, 1, 310805, 310800, 0, 2.8, 0, 3000, 0);
