INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("ゴーストブラザーズ", 16, "ゴーストファーザーの側近の手下。\n鉄砲玉たちと集めた上質な精気を首領へ献上する。\n\n・ゴーストブラザーズは攻撃の対象にならない。\n・ゴーストファーザーのＴＰを回復させる。\n　ゴーストファーザーが行動不能状態のときは、\n　ＴＰを回復しない。", 270, 0, 2, 311702, 311700, 0, 999.0, 0, 3000, 0);
