INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("グレートトゥンヌス", 16, "【ＭＯＤＥ１】数多の漁師を波間に沈めた海の王。\n黄金に輝くヒレで大漁船の竜骨をも圧し折る。\nその様はさながら大海原の解体ショー\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与える。\n　さらにノックバックさせる。\n・前方範囲内の敵すべてに物理大ダメージを与え、\n　さらに後ろから２キャラに物理中ダメージを\n　与える。また、自身の物理攻撃力を小アップ\n　させる。", 270, 0, 1, 312304, 312302, 0, 0.0, 0, 3000, 0);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("グレートトゥンヌス", 16, "【ＭＯＤＥ２】海を駆ける怪魚はさらに力を増す。\nひと泳ぎで自身を鼓舞するかのように大渦をおこ\nし大海を分かつ巨大な砲弾となって敵に激突する。\n\n・ユニオンバーストは、前から３キャラに\n　物理特大ダメージを与え\n　さらにノックバックさせる。\n・自身の物理攻撃力を大アップ、行動速度を\n　中アップ、ＴＰを小アップさせる。", 270, 0, 1, 312305, 312305, 0, 1.0, 0, 3000, 0);
