INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("黒風の精霊", 16, "【魔法】使役者の真の力を試す黒き風の精霊。\nそこには悪意はなく、純粋に己の内に秘めた想い\nと絆が試される。\n\n・ユニオンバーストは、敵全体に黒風の精霊\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　魔法割合ダメージを与える。さらに、\n　残りＨＰが最も低い１キャラにカウントダウン\n　を付与し、５秒後に最大ＨＰの80%の魔法\n　割合ダメージを与える。\n　黒風の精霊がユニオンバーストを使用してから\n　一定時間が経過するまでは再度ユニオン\n　バーストを使用しない。", 270, 0, 2, 312701, 312700, 0, 2.7, 0, 3000, 0);
