INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("黒風の精霊", 16, "【ＭＯＤＥ１】第一の試練。黒き風の精霊の暴風\nの継ぎ目を見切れば、自ずと己の進むべき道が\n切り拓かれん。\n\n・敵全体に物理ダメージとスタンを与え、ノック\n　バックさせる。\n　黒風の精霊が眠っている間、自身の物理防御力\n　が大幅にダウンし、その間に100000以上\n　のダメージを与えると、防御力ダウンが延長さ\n　れ行動速度もダウンする。", 270, 0, 2, 312704, 312700, 0, 2.7, 0, 3000, 0);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("黒風の精霊", 16, "【ＭＯＤＥ２】第二の試練。己の弱き心と対峙\nせよ。いつも答えは己の内にある。今一度、仲間\nの力を信じ立ち向かえ。\n\n・前から２キャラに物理小ダメージを与え、さら\n　にノックバックさせ、スタンを与える。", 270, 0, 2, 312705, 312705, 0, 2.7, 0, 3000, 0);
