INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("魔物の姫", 16, "【物理】辺境の城に座する、孤高な魔物の姫君。\n悲哀なる恋慕を胸に、自らに挑む者を打ち砕く。\n\n・ユニオンバーストは、前から２キャラに\n　最大ＨＰに応じた物理割合ダメージを与える。\n　力を溜めることに成功した場合、ダメージが\n　アップし、敵全体を畏怖状態にする。\n　畏怖状態のキャラは、一定時間攻撃およびＨＰ\n　回復のユニオンバーストとスキル、強化通常攻\n　撃の効果がダウン。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・物理防御力と魔法防御力が大幅にダウンした後、\n　一定時間、力を溜める。\n　力が溜まると、ＴＰが特大回復する。\n　力を溜めている間に魔物の姫が7000以上の\n　ダメージを受けると、力を溜めることに失敗し、\n　ＴＰが大ダウン。 \n・目の前の敵１キャラに物理ダメージを与える。\n　この攻撃は物理攻撃力アップ・ダウン効果の\n　影響を受けやすい。\n・前から３キャラに最大ＨＰに応じた物理割合\n　ダメージを与える。\n　この攻撃は物理攻撃力アップ・ダウン効果の\n　影響を受けやすい。", 270, 0, 1, 312801, 312800, 0, 1.8, 0, 3000, 0);
