INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("バースト・タイムラーベ", 16, "【魔法】ホマレ所蔵の魔道具が、臨界を超え暴走\nした姿。本来の役目も忘れ、冒険者を混沌へと\n導く水先案内人と化す。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、さらに行動速度を中ダウン、魔法防御\n　力を中ダウンさせる。このスキルの状態異常は\n　ダメージを無効化しても付与される。\n・自分の魔法攻撃力を中アップ、行動速度を大\n　アップさせる。\n・向かって左側のターゲットのブレイク時、\n　物理/魔法防御力が中ダウン。\n・中央下部のターゲットのブレイク時、\n　物理/魔法防御力が大ダウン。\n・中央上部のターゲットのブレイク時、\n　物理/魔法防御力が大ダウン。\n・向かって右側のターゲットのブレイク時、\n　物理/魔法防御力が特大ダウン。", 270, 0, 2, 313101, 313100, 0, 1.4, 0, 3000, 0);
