INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("ランファ", 16, "天稟の才と過敏な感覚に懊悩する、幽寂の歌姫。\n争いは好まないが、願いと仲間のため奮戦する。\n\n・ユニオンバーストは、前方範囲内の敵に魔法中\n　ダメージを与え、結晶化状態にする。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n　結晶化状態では行動不能になり、継続ダメージ\n　を受ける。\n　結晶化状態はＨＰが全回復するか、一定時間経\n　過すると解除される。\n・前方範囲内の敵に猛毒状態を付与する。\n　結晶化状態の敵には追加の魔法大ダメージを与\n　える。\n・前から１番目のキャラに魔法小ダメージ。", 270, 0, 2, 313600, 313600, 0, 3.0, 0, 3001, 0);
