INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("得意な宿題", 16, "まずは手始めに得意な宿題を終わらせよう。\nここでつまづいてはいられない。夏休みの宿題は\nほかにもまだまだあるぞ！！\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。このスキル\n　によるダメージでは、キャラのＴＰが\n　回復しない。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。", 270, 0, 1, 314001, 314001, 0, 1.0, 0, 3000, 0);
