INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("アンチビースト・トリニティ", 16, "【魔法】魔法少女たちのパワーを吸い取って進化\nしたアンチビースト。ランドソルはこのまま魔の\n手に堕ちてしまうのか！？負けるなノワールズ！\n\n・ユニオンバーストは、自身に近い敵３キャラの\n　うち、ＨＰが80％未満のキャラを誘惑状態に\n　する。その後、自身に近い敵３キャラにキャラ\n　の最大ＨＰに応じた魔法割合ダメージを与える。\n　誘惑状態の敵にはダメージがアップする。\n・アンチビーストの下僕を召喚し敵全体をノック\n　バックさせる。アンチビーストの下僕は、一定\n　時間経過すると最も近い敵１キャラを誘惑状態\n　にしたあと、消滅する。ただしアンチビースト\n　の下僕のＨＰが一定値以下になった場合は、\n　パーティ全体の物理攻撃力と魔法攻撃力を特大\n　アップさせたあと、消滅する。\n・自身に近い敵２キャラに魔法小ダメージを与え\n　る。さらに、自身に物理無効バリアを展開する。\n・自身に近い敵２キャラにキャラの最大ＨＰに応\n　じた魔法割合ダメージを与える。", 270, 0, 2, 314401, 314400, 0, 1.6, 0, 3000, 0);
