INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("ひとりぼっちの聖夜の妖精", 16, "【物理】遠い昔に人々から忘れられた悲しき妖精。\n信仰を失い、寂しさを紛らわすかのように悪戯を\n働き人目を集めようとする。コンテスト会場の混\n乱を治め、再び信仰を取り戻せるのか！？\n\n・ユニオンバーストは、敵全体にひとりぼっちの\n　聖夜の妖精の物理攻撃力とキャラの最大ＨＰに\n　応じた物理割合ダメージを与える。\n・前から３キャラにひとりぼっちの聖夜の妖精の\n　魔法攻撃力とキャラの最大ＨＰに応じた魔法割\n　合ダメージを与え、大きく吹き飛ばす。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・ひとりぼっちの聖夜の妖精自身の物理/魔法防\n　御力を極大アップ。", 270, 0, 1, 314501, 314500, 0, 1.6, 0, 3000, 0);
