INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("グレーターゴーレム", 16, "【物理】同族のゴーレムを次々に呼び寄せる\n岩石の魔物。大地を揺さぶる巨腕が冒険者たちに\n襲い掛かる。\n\n・主人公が目覚めていないため、バトル開始時\n　に全キャラクターのユニオンバーストが発動\n　不能状態になる。\n・ぺコリーヌが目覚めていないため、本バトルで\n　はペコリーヌ（同名キャラ含む）が編成不可。\n・ユニオンバーストは、前から4キャラにグレー\n　ターゴーレムの物理攻撃力とキャラの最大ＨＰ\n　に応じた物理割合ダメージを与える。\n・敵全体をノックバックさせ、ゴーレムを召喚す\n　る。さらに自分の物理攻撃力を中アップさせ\n　る。\n・前から１番目のキャラに物理大ダメージ。", 270, 0, 1, 314701, 314700, 0, 1.6, 0, 3000, 0);
