INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("解放されし財宝の守護者", 16, "【物理】大海賊の財宝を守る高度な自動人形。\n数多の試練を乗り越えた冒険者の前に立ち塞がる。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与え、自身の物\n　理攻撃力を大アップさせる。\n・最も近い敵１キャラを中心にした小範囲内の敵\n　すべてに物理大ダメージを与え、物理防御力を\n　大ダウンさせる。\n・自身の物理防御力と魔法防御力を極大アップさ\n　せる。財宝の守護者が毒、猛毒、呪い、火傷、\n　呪詛の効果を受けていると、自身の物理防御力\n　と魔法防御力がアップする時間が短くなり、極\n　大アップではなく小アップさせる。\n・財宝の守護者自身のＨＰが40％を切ると、自\n　身の物理攻撃力を大アップさせ、さらに攻撃が\n　激しく凶暴になる。その後、敵全体にキャラの\n　最大ＨＰに応じた物理割合ダメージを与え、一\n　定時間、ユニオンバーストのみ使用できない状\n　態にする。このスキルの状態異常はダメージを\n　無効化しても付与される。\n・最も近い敵１キャラに物理大ダメージ。", 270, 0, 1, 316001, 316000, 0, 1.6, 0, 3000, 0);
