INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("ムーンレイゲート", 16, "【魔法】月の満ち欠けに応じて現れる、異なる二\nつの世界を繋ぐゲート。みゅ～ちゃんをゲートま\nで導いて、元の世界に戻してあげよう！\n\n・ユニオンバーストは、敵全体にムーンレイゲー\n　トの【月の光印】の数に応じた魔法割合ダメー\n　ジを与える。\n・敵全体をノックバックさせ行動速度を中ダウン\n　させる。さらに、敵全体にムーンレイゲートの\n　【月の光印】の数に応じた魔法割合ダメージを\n　与える。\n　その後ムーンレイゲートに【月の光印】を10\n　個付与する。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・敵全体にムーンレイゲートの【月の光印】の数\n　に応じた魔法小ダメージを与える。\n　さらにムーンレイゲートを中心とした範囲内の\n　敵全てにムーンレイゲートの【月の光印】の数\n　に応じた魔法小ダメージを与える。\n・【月の光印】は最大50個まで付与される。", 270, 0, 2, 316401, 316400, 0, 1.6, 0, 3000, 0);
