INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("穢れし湯ノ神・陽卵", 16, "【物理】『神酔』の力でタガが外れた温泉の神。\nその身に溜め込んだ罪と穢れを解き放ち、忘年会\n会場を大混乱の渦に陥れる。\n\n・ユニオンバーストは、敵全体に魔法特大ダメー\n　ジを与え、呪い状態を付与する。既に呪い状態\n　の敵には代わりに呪詛状態を付与する。呪詛に\n　よる継続ダメージは毎秒ごとに増加する。\n・敵全体に魔法小ダメージを与え、呪い状態を付\n　与する。\n・自身に近い敵３キャラに物理中ダメージを与え\n　る。敵のＨＰが30％未満の場合、混乱状態に\n　する。\n・自身に近い敵３キャラに物理中ダメージを与え\n　る。\n・穢れし湯ノ神・陽卵の付与する呪いおよび呪詛\n　のダメージでは、キャラのＴＰが回復しない。", 270, 0, 1, 318101, 318100, 0, 1.6, 0, 3000, 0);
