INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("願福寄災の招き猫", 16, "【魔法】自らの使命に執着し、呪物となり果てた\n招き猫。行き過ぎたその願いは、幸福だけでなく\n災いをも引き寄せる。\n\n・ユニオンバーストは、最も近い敵１キャラを中\n　心にした小範囲に魔法大ダメージを与え、スタ\n　ン状態にし、さらにＴＰを中ダウンさせる。こ\n　のスキルの状態異常はダメージを無効化しても\n　付与される。\n・敵全体に魔法中ダメージを与える。挑発を無視\n　して、最も近い敵１キャラを自身の目の前まで\n　引き寄せ、２番目以降に近い敵を小さく引き寄\n　せる。\n・最も近い敵１キャラを中心にした小範囲に魔法\n　小ダメージを与える。\n・残り時間が50秒を切ると自身を中心に、範囲\n　内の敵キャラの物理攻撃力と魔法攻撃力を中ダ\n　ウンさせるフィールドを展開する。", 270, 0, 2, 318301, 318300, 0, 1.6, 0, 3000, 0);
