INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("バジリスク・アロス", 16, "【魔法】突如雪山に現れたバジリスクの変異種。\n異相の巨体を羽ばたかせ、聖夜を控えた人々や\n魔物を絶望に突き落とす。\n\n・ユニオンバーストは、最も近い敵１キャラに魔\n　法特大ダメージを与える。さらに敵全体に魔法\n　中ダメージを与え、石化状態を付与する。この\n　スキルの状態異常はダメージを無効化しても付\n　与される。\n・敵全体に魔法中ダメージを与え、石化状態を付\n　与する。このスキルの状態異常はダメージを無\n　効化しても付与される。\n・最も近い敵１キャラに魔法特大ダメージを与え\n　る。\n・最も近い敵１キャラに魔法中ダメージを与える。", 270, 0, 2, 320200, 320200, 0, 1.6, 0, 3000, 0);
