INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("メテオ・イン・ドリーム", 16, "【物理】幻想的な宇宙空間。煌びやかな星の上。\nそして目の前には……迫りくる巨大隕石！？混乱\nの最中で脳裏をよぎるのは、心地良い枕の感触。\n\n・ユニオンバーストは、敵全体のバリアを解除し、\n　残りＨＰに応じた物理割合ダメージを与える。\n　さらにスタン状態を付与する。このスキルの状\n　態異常はダメージを無効化しても付与される。\n・敵全体に残りＨＰに応じた物理割合ダメージを\n　与え、短時間スタン状態を付与する。さらに残\n　りＨＰが20％未満の場合、長時間スタン状態\n　を付与する。このスキルの状態異常はダメージ\n　を無効化しても付与される。\n・自身を中心とした範囲内の敵すべてに残りＨＰ\n　に応じた物理割合ダメージを与え、ノックバッ\n　クさせる。さらに残りＨＰが30％未満の場合、\n　ステータスアップ効果を解除する。このスキル\n　の効果はダメージを無効化しても付与される。\n・後方範囲内の敵すべてに残りＨＰに応じた物理\n　割合ダメージを与え、ＴＰを中ダウンさせる。\n　さらに残りＨＰが30％未満の場合、物理攻撃\n　力と魔法攻撃力を中ダウンさせる。このスキル\n　の状態異常はダメージを無効化しても付与され\n　る。", 270, 0, 1, 320300, 320300, 0, 1.6, 0, 3000, 0);
