INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("エリマキレックス", 16, "【物理】ジオ・ゲヘナに棲む恐竜魔\n物。頭のエリマキは威嚇だけでなく\n刃のように変形し獲物を両断する。\n\n・バトル開始時、自身にＴＰダウン\n　50％カット効果を付与する。\n・エリマキレックスから受けるすべ\n　てのダメージで発生する、パー\n　ティキャラのＴＰ回復量を中ダ\n　ウンさせる。\n・ユニオンバーストは、敵全体に\n　エリマキレックスの物理攻撃力\n　に応じた物理割合ダメージを与\n　える。\n・前から２キャラに物理中ダメージ\n　を与え、ノックバックさせると同\n　時にスタンさせる。このスキルの\n　状態異常はダメージを無効化して\n　も付与される。", 270, 0, 1, 320600, 320600, 0, 1.6, 0, 3000, 0);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("奈落の聖女トミヱ", 16, "【物理】聖なる学舎の秘されし暗部を預かる獄卒。\n教義絶対を掲げる『鋼の聖女』の正義を守るべく\nまつろわぬ咎人に容赦なき服従と更生を強いる。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与える。自身\n　の物理攻撃力に応じて与えるダメージの割合が\n　増加する。\n・最も近い敵３キャラにキャラの残りＨＰに応じ\n　た物理割合ダメージを与える。自身の物理攻撃\n　力に応じて与えるダメージの割合が増加する。\n・自身の物理攻撃力を特大アップし、ＴＰを特大\n　回復する。自身のＨＰが低いほど、物理攻撃力\n　の上昇量がアップする。\n・最も近い敵１キャラに物理特大ダメージを与え\n　る。", 270, 0, 1, 320700, 320700, 0, 1.6, 0, 3000, 0);
