INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("捲土重来・ジーノ", 16, "【物理】清掃員に扮して虎視眈々と『グランド\nリームパーク』の乗っ取りを謀っていた元・カジ\nノ王。これまでの働きぶりはどこへやら、屈辱を\n晴らすべくパレード間近の園内で反旗を翻す。\n\n・ユニオンバーストは、最も近い敵１キャラに\n　自身の物理攻撃力とキャラの最大ＨＰに応じた\n　物理割合ダメージを与える。\n・自身のＨＰが50％以下になるとさらに攻撃が\n　激しく強力になる。\n・敵全体の物理防御力を大ダウンさせ、キャラの\n　ＨＰが70％未満の場合、毒状態を付与する。\n・敵全体に物理中ダメージを与える。毒状態の場\n　合、残りＨＰに応じた物理割合ダメージを与え\n　る。さらに敵全体のステータスアップ効果とバ\n　リアを解除する。", 270, 0, 1, 321200, 321200, 0, 1.6, 0, 3000, 0);
