INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES ("真夏のバーサークスライム", 16, "【物理】魔道具の影響で制御不能に陥ったスライ\nム。濃厚な粘液は夏の装いなどいとも容易く溶解\nし、秘められた素肌を炎天下に曝け出させる。\n\n・召喚体、分身を攻撃対象にしない。\n・ユニオンバーストは、最も近い１～５番目の敵\n　にそれぞれのキャラの現在ＨＰに応じた物理割\n　合ダメージを与え、キャラの最大ＨＰをバトル\n　終了まで15％減少させる。\n・最も近い１～３番目の敵にそれぞれ物理小ダ\n　メージを与える。さらに物理防御力を大ダウン\n　させる。このスキルの効果はダメージを無効化\n　しても付与される。\n・最も近い敵１キャラに物理特大ダメージを与え、\n　ＴＰを小ダウンさせる。このスキルはダメージ\n　を無効化してもＴＰをダウンさせる。", 270, 0, 1, 321300, 321300, 0, 1.6, 0, 3000, 0);
