INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", "エリコ", 7, 10.85, 129901, 100, 1, 100, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", "エリコ", 2, 11.15, 129901, 101, 1, 101, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", "エリコ", 5, 6.4, 129901, 102, 7, 102, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", "エリコ", 3, 0.0, 129901, 103, 3, 103, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", "エリコ", 8, 2.43, 129901, 104, 2, 104, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", "エリコ", 3, 9.08, 129901, 105, 4, 105, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", "エリコ", 6, 0.0, 129901, 106, 6, 106, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", "エリコ", 8, 10.6, 129901, 107, 3, 107, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", "エリコ", 4, 7.54, 129901, 108, 6, 108, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", "エリコ", 7, 11.5, 129901, 109, 2, 109, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", "エリコ", 1, 8.83, 129901, 110, 2, 110, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", "エリコ", 2, 6.68, 129901, 111, 1, 111, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", "エリコ", 2, 8.8, 129901, 112, 6, 112, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", "エリコ", 1, 8.9, 129901, 113, 2, 113, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", "エリコ", 5, 3.7, 129901, 114, 2, 114, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", "マコト", 2, 7.6, 129801, 200, 1, 200, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", "マコト", 1, 2.25, 129801, 201, 2, 201, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", "マコト", 1, 3.28, 129801, 202, 2, 202, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", "マコト", 1, 2.98, 129801, 203, 4, 203, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", "マコト", 1, 3.0, 129801, 204, 4, 204, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", "マコト", 2, 10.3, 129801, 205, 1, 205, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", "マコト", 1, 2.93, 129801, 206, 6, 206, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", "マコト", 1, 9.3, 129801, 207, 3, 207, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", "マコト", 1, 6.9, 129801, 208, 4, 208, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", "マコト", 2, 0.0, 129801, 209, 2, 209, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", "マコト", 2, 5.95, 129801, 210, 1, 210, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", "マコト", 1, 6.73, 129801, 211, 2, 211, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", "マコト", 2, 0.0, 129801, 212, 2, 212, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", "マコト", 2, 6.65, 129801, 213, 1, 213, "2030/12/17 14:59:59");
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 24, 0.0, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", "マコト", 1, 3.05, 129801, 214, 6, 214, "2030/12/17 14:59:59");
