INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (7, 10.85, "エリコ", "2030/12/17 14:59:59", 129901, 24, 100, 10142, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 1, 0.0, 0.0, "2024/03/31 12:00:00", 100);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (2, 11.15, "エリコ", "2030/12/17 14:59:59", 129901, 24, 101, 10142, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 1, 0.0, 0.0, "2024/03/31 12:00:00", 101);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (5, 6.4, "エリコ", "2030/12/17 14:59:59", 129901, 24, 102, 10142, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 7, 0.0, 0.0, "2024/03/31 12:00:00", 102);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (3, 0.0, "エリコ", "2030/12/17 14:59:59", 129901, 24, 103, 10142, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 3, 0.0, 0.0, "2024/03/31 12:00:00", 103);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (8, 2.43, "エリコ", "2030/12/17 14:59:59", 129901, 24, 104, 10142, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 2, 0.0, 0.0, "2024/03/31 12:00:00", 104);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (3, 9.08, "エリコ", "2030/12/17 14:59:59", 129901, 24, 105, 10142, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 4, 0.0, 0.0, "2024/03/31 12:00:00", 105);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (6, 0.0, "エリコ", "2030/12/17 14:59:59", 129901, 24, 106, 10142, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 6, 0.0, 0.0, "2024/03/31 12:00:00", 106);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (8, 10.6, "エリコ", "2030/12/17 14:59:59", 129901, 24, 107, 10142, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 3, 0.0, 0.0, "2024/03/31 12:00:00", 107);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (4, 7.54, "エリコ", "2030/12/17 14:59:59", 129901, 24, 108, 10142, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 6, 0.0, 0.0, "2024/03/31 12:00:00", 108);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (7, 11.5, "エリコ", "2030/12/17 14:59:59", 129901, 24, 109, 10142, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 2, 0.0, 0.0, "2024/03/31 12:00:00", 109);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (1, 8.83, "エリコ", "2030/12/17 14:59:59", 129901, 24, 110, 10142, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 2, 0.0, 0.0, "2024/03/31 12:00:00", 110);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (2, 6.68, "エリコ", "2030/12/17 14:59:59", 129901, 24, 111, 10142, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 1, 0.0, 0.0, "2024/03/31 12:00:00", 111);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (2, 8.8, "エリコ", "2030/12/17 14:59:59", 129901, 24, 112, 10142, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 6, 0.0, 0.0, "2024/03/31 12:00:00", 112);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (1, 8.9, "エリコ", "2030/12/17 14:59:59", 129901, 24, 113, 10142, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 2, 0.0, 0.0, "2024/03/31 12:00:00", 113);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (5, 3.7, "エリコ", "2030/12/17 14:59:59", 129901, 24, 114, 10142, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 2, 0.0, 0.0, "2024/03/31 12:00:00", 114);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (2, 7.6, "マコト", "2030/12/17 14:59:59", 129801, 24, 200, 10142, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 1, 0.0, 0.0, "2024/03/31 12:00:00", 200);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (1, 2.25, "マコト", "2030/12/17 14:59:59", 129801, 24, 201, 10142, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 2, 0.0, 0.0, "2024/03/31 12:00:00", 201);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (1, 3.28, "マコト", "2030/12/17 14:59:59", 129801, 24, 202, 10142, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 2, 0.0, 0.0, "2024/03/31 12:00:00", 202);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (1, 2.98, "マコト", "2030/12/17 14:59:59", 129801, 24, 203, 10142, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 4, 0.0, 0.0, "2024/03/31 12:00:00", 203);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (1, 3.0, "マコト", "2030/12/17 14:59:59", 129801, 24, 204, 10142, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 4, 0.0, 0.0, "2024/03/31 12:00:00", 204);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (2, 10.3, "マコト", "2030/12/17 14:59:59", 129801, 24, 205, 10142, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 1, 0.0, 0.0, "2024/03/31 12:00:00", 205);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (1, 2.93, "マコト", "2030/12/17 14:59:59", 129801, 24, 206, 10142, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 6, 0.0, 0.0, "2024/03/31 12:00:00", 206);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (1, 9.3, "マコト", "2030/12/17 14:59:59", 129801, 24, 207, 10142, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 3, 0.0, 0.0, "2024/03/31 12:00:00", 207);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (1, 6.9, "マコト", "2030/12/17 14:59:59", 129801, 24, 208, 10142, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 4, 0.0, 0.0, "2024/03/31 12:00:00", 208);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (2, 0.0, "マコト", "2030/12/17 14:59:59", 129801, 24, 209, 10142, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 2, 0.0, 0.0, "2024/03/31 12:00:00", 209);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (2, 5.95, "マコト", "2030/12/17 14:59:59", 129801, 24, 210, 10142, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 1, 0.0, 0.0, "2024/03/31 12:00:00", 210);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (1, 6.73, "マコト", "2030/12/17 14:59:59", 129801, 24, 211, 10142, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 2, 0.0, 0.0, "2024/03/31 12:00:00", 211);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (2, 0.0, "マコト", "2030/12/17 14:59:59", 129801, 24, 212, 10142, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 2, 0.0, 0.0, "2024/03/31 12:00:00", 212);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (2, 6.65, "マコト", "2030/12/17 14:59:59", 129801, 24, 213, 10142, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 1, 0.0, 0.0, "2024/03/31 12:00:00", 213);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (1, 3.05, "マコト", "2030/12/17 14:59:59", 129801, 24, 214, 10142, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 6, 0.0, 0.0, "2024/03/31 12:00:00", 214);
