INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 7, 24, 1, 100, 0.0, "2024/03/31 12:00:00", 129901, 10142, "エリコ", 10.85, "2030/12/17 14:59:59", 100, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 2, 24, 1, 101, 0.0, "2024/03/31 12:00:00", 129901, 10142, "エリコ", 11.15, "2030/12/17 14:59:59", 101, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 5, 24, 7, 102, 0.0, "2024/03/31 12:00:00", 129901, 10142, "エリコ", 6.4, "2030/12/17 14:59:59", 102, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 3, 24, 3, 103, 0.0, "2024/03/31 12:00:00", 129901, 10142, "エリコ", 0.0, "2030/12/17 14:59:59", 103, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 8, 24, 2, 104, 0.0, "2024/03/31 12:00:00", 129901, 10142, "エリコ", 2.43, "2030/12/17 14:59:59", 104, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 3, 24, 4, 105, 0.0, "2024/03/31 12:00:00", 129901, 10142, "エリコ", 9.08, "2030/12/17 14:59:59", 105, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 6, 24, 6, 106, 0.0, "2024/03/31 12:00:00", 129901, 10142, "エリコ", 0.0, "2030/12/17 14:59:59", 106, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 8, 24, 3, 107, 0.0, "2024/03/31 12:00:00", 129901, 10142, "エリコ", 10.6, "2030/12/17 14:59:59", 107, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 4, 24, 6, 108, 0.0, "2024/03/31 12:00:00", 129901, 10142, "エリコ", 7.54, "2030/12/17 14:59:59", 108, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 7, 24, 2, 109, 0.0, "2024/03/31 12:00:00", 129901, 10142, "エリコ", 11.5, "2030/12/17 14:59:59", 109, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 1, 24, 2, 110, 0.0, "2024/03/31 12:00:00", 129901, 10142, "エリコ", 8.83, "2030/12/17 14:59:59", 110, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 2, 24, 1, 111, 0.0, "2024/03/31 12:00:00", 129901, 10142, "エリコ", 6.68, "2030/12/17 14:59:59", 111, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 2, 24, 6, 112, 0.0, "2024/03/31 12:00:00", 129901, 10142, "エリコ", 8.8, "2030/12/17 14:59:59", 112, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 1, 24, 2, 113, 0.0, "2024/03/31 12:00:00", 129901, 10142, "エリコ", 8.9, "2030/12/17 14:59:59", 113, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 5, 24, 2, 114, 0.0, "2024/03/31 12:00:00", 129901, 10142, "エリコ", 3.7, "2030/12/17 14:59:59", 114, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 2, 24, 1, 200, 0.0, "2024/03/31 12:00:00", 129801, 10142, "マコト", 7.6, "2030/12/17 14:59:59", 200, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 1, 24, 2, 201, 0.0, "2024/03/31 12:00:00", 129801, 10142, "マコト", 2.25, "2030/12/17 14:59:59", 201, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 1, 24, 2, 202, 0.0, "2024/03/31 12:00:00", 129801, 10142, "マコト", 3.28, "2030/12/17 14:59:59", 202, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 1, 24, 4, 203, 0.0, "2024/03/31 12:00:00", 129801, 10142, "マコト", 2.98, "2030/12/17 14:59:59", 203, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 1, 24, 4, 204, 0.0, "2024/03/31 12:00:00", 129801, 10142, "マコト", 3.0, "2030/12/17 14:59:59", 204, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 2, 24, 1, 205, 0.0, "2024/03/31 12:00:00", 129801, 10142, "マコト", 10.3, "2030/12/17 14:59:59", 205, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 1, 24, 6, 206, 0.0, "2024/03/31 12:00:00", 129801, 10142, "マコト", 2.93, "2030/12/17 14:59:59", 206, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 1, 24, 3, 207, 0.0, "2024/03/31 12:00:00", 129801, 10142, "マコト", 9.3, "2030/12/17 14:59:59", 207, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 1, 24, 4, 208, 0.0, "2024/03/31 12:00:00", 129801, 10142, "マコト", 6.9, "2030/12/17 14:59:59", 208, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 2, 24, 2, 209, 0.0, "2024/03/31 12:00:00", 129801, 10142, "マコト", 0.0, "2030/12/17 14:59:59", 209, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 2, 24, 1, 210, 0.0, "2024/03/31 12:00:00", 129801, 10142, "マコト", 5.95, "2030/12/17 14:59:59", 210, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 1, 24, 2, 211, 0.0, "2024/03/31 12:00:00", 129801, 10142, "マコト", 6.73, "2030/12/17 14:59:59", 211, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 2, 24, 2, 212, 0.0, "2024/03/31 12:00:00", 129801, 10142, "マコト", 0.0, "2030/12/17 14:59:59", 212, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 2, 24, 1, 213, 0.0, "2024/03/31 12:00:00", 129801, 10142, "マコト", 6.65, "2030/12/17 14:59:59", 213, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 1, 24, 6, 214, 0.0, "2024/03/31 12:00:00", 129801, 10142, "マコト", 3.05, "2030/12/17 14:59:59", 214, 0.0);
