INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", "2024/03/31 12:00:00", 100, 129901, 10.85, 0.0, 0.0, "エリコ", 10142, 1, 100, "2030/12/17 14:59:59", 24, 7);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", "2024/03/31 12:00:00", 101, 129901, 11.15, 0.0, 0.0, "エリコ", 10142, 1, 101, "2030/12/17 14:59:59", 24, 2);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", "2024/03/31 12:00:00", 102, 129901, 6.4, 0.0, 0.0, "エリコ", 10142, 7, 102, "2030/12/17 14:59:59", 24, 5);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", "2024/03/31 12:00:00", 103, 129901, 0.0, 0.0, 0.0, "エリコ", 10142, 3, 103, "2030/12/17 14:59:59", 24, 3);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", "2024/03/31 12:00:00", 104, 129901, 2.43, 0.0, 0.0, "エリコ", 10142, 2, 104, "2030/12/17 14:59:59", 24, 8);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", "2024/03/31 12:00:00", 105, 129901, 9.08, 0.0, 0.0, "エリコ", 10142, 4, 105, "2030/12/17 14:59:59", 24, 3);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", "2024/03/31 12:00:00", 106, 129901, 0.0, 0.0, 0.0, "エリコ", 10142, 6, 106, "2030/12/17 14:59:59", 24, 6);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", "2024/03/31 12:00:00", 107, 129901, 10.6, 0.0, 0.0, "エリコ", 10142, 3, 107, "2030/12/17 14:59:59", 24, 8);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", "2024/03/31 12:00:00", 108, 129901, 7.54, 0.0, 0.0, "エリコ", 10142, 6, 108, "2030/12/17 14:59:59", 24, 4);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", "2024/03/31 12:00:00", 109, 129901, 11.5, 0.0, 0.0, "エリコ", 10142, 2, 109, "2030/12/17 14:59:59", 24, 7);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", "2024/03/31 12:00:00", 110, 129901, 8.83, 0.0, 0.0, "エリコ", 10142, 2, 110, "2030/12/17 14:59:59", 24, 1);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", "2024/03/31 12:00:00", 111, 129901, 6.68, 0.0, 0.0, "エリコ", 10142, 1, 111, "2030/12/17 14:59:59", 24, 2);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", "2024/03/31 12:00:00", 112, 129901, 8.8, 0.0, 0.0, "エリコ", 10142, 6, 112, "2030/12/17 14:59:59", 24, 2);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", "2024/03/31 12:00:00", 113, 129901, 8.9, 0.0, 0.0, "エリコ", 10142, 2, 113, "2030/12/17 14:59:59", 24, 1);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", "2024/03/31 12:00:00", 114, 129901, 3.7, 0.0, 0.0, "エリコ", 10142, 2, 114, "2030/12/17 14:59:59", 24, 5);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", "2024/03/31 12:00:00", 200, 129801, 7.6, 0.0, 0.0, "マコト", 10142, 1, 200, "2030/12/17 14:59:59", 24, 2);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", "2024/03/31 12:00:00", 201, 129801, 2.25, 0.0, 0.0, "マコト", 10142, 2, 201, "2030/12/17 14:59:59", 24, 1);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", "2024/03/31 12:00:00", 202, 129801, 3.28, 0.0, 0.0, "マコト", 10142, 2, 202, "2030/12/17 14:59:59", 24, 1);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", "2024/03/31 12:00:00", 203, 129801, 2.98, 0.0, 0.0, "マコト", 10142, 4, 203, "2030/12/17 14:59:59", 24, 1);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", "2024/03/31 12:00:00", 204, 129801, 3.0, 0.0, 0.0, "マコト", 10142, 4, 204, "2030/12/17 14:59:59", 24, 1);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", "2024/03/31 12:00:00", 205, 129801, 10.3, 0.0, 0.0, "マコト", 10142, 1, 205, "2030/12/17 14:59:59", 24, 2);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", "2024/03/31 12:00:00", 206, 129801, 2.93, 0.0, 0.0, "マコト", 10142, 6, 206, "2030/12/17 14:59:59", 24, 1);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", "2024/03/31 12:00:00", 207, 129801, 9.3, 0.0, 0.0, "マコト", 10142, 3, 207, "2030/12/17 14:59:59", 24, 1);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", "2024/03/31 12:00:00", 208, 129801, 6.9, 0.0, 0.0, "マコト", 10142, 4, 208, "2030/12/17 14:59:59", 24, 1);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", "2024/03/31 12:00:00", 209, 129801, 0.0, 0.0, 0.0, "マコト", 10142, 2, 209, "2030/12/17 14:59:59", 24, 2);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", "2024/03/31 12:00:00", 210, 129801, 5.95, 0.0, 0.0, "マコト", 10142, 1, 210, "2030/12/17 14:59:59", 24, 2);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", "2024/03/31 12:00:00", 211, 129801, 6.73, 0.0, 0.0, "マコト", 10142, 2, 211, "2030/12/17 14:59:59", 24, 1);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", "2024/03/31 12:00:00", 212, 129801, 0.0, 0.0, 0.0, "マコト", 10142, 2, 212, "2030/12/17 14:59:59", 24, 2);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", "2024/03/31 12:00:00", 213, 129801, 6.65, 0.0, 0.0, "マコト", 10142, 1, 213, "2030/12/17 14:59:59", 24, 2);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES ("あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", "2024/03/31 12:00:00", 214, 129801, 3.05, 0.0, 0.0, "マコト", 10142, 6, 214, "2030/12/17 14:59:59", 24, 1);
