INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 100, "エリコ", "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 100, 7, 24, "2030/12/17 14:59:59", 1, 10.85, 129901, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 101, "エリコ", "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 101, 2, 24, "2030/12/17 14:59:59", 1, 11.15, 129901, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 102, "エリコ", "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 102, 5, 24, "2030/12/17 14:59:59", 7, 6.4, 129901, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 103, "エリコ", "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 103, 3, 24, "2030/12/17 14:59:59", 3, 0.0, 129901, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 104, "エリコ", "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 104, 8, 24, "2030/12/17 14:59:59", 2, 2.43, 129901, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 105, "エリコ", "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 105, 3, 24, "2030/12/17 14:59:59", 4, 9.08, 129901, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 106, "エリコ", "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 106, 6, 24, "2030/12/17 14:59:59", 6, 0.0, 129901, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 107, "エリコ", "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 107, 8, 24, "2030/12/17 14:59:59", 3, 10.6, 129901, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 108, "エリコ", "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 108, 4, 24, "2030/12/17 14:59:59", 6, 7.54, 129901, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 109, "エリコ", "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 109, 7, 24, "2030/12/17 14:59:59", 2, 11.5, 129901, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 110, "エリコ", "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 110, 1, 24, "2030/12/17 14:59:59", 2, 8.83, 129901, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 111, "エリコ", "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 111, 2, 24, "2030/12/17 14:59:59", 1, 6.68, 129901, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 112, "エリコ", "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 112, 2, 24, "2030/12/17 14:59:59", 6, 8.8, 129901, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 113, "エリコ", "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 113, 1, 24, "2030/12/17 14:59:59", 2, 8.9, 129901, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 114, "エリコ", "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 114, 5, 24, "2030/12/17 14:59:59", 2, 3.7, 129901, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 200, "マコト", "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 200, 2, 24, "2030/12/17 14:59:59", 1, 7.6, 129801, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 201, "マコト", "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 201, 1, 24, "2030/12/17 14:59:59", 2, 2.25, 129801, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 202, "マコト", "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 202, 1, 24, "2030/12/17 14:59:59", 2, 3.28, 129801, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 203, "マコト", "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 203, 1, 24, "2030/12/17 14:59:59", 4, 2.98, 129801, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 204, "マコト", "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 204, 1, 24, "2030/12/17 14:59:59", 4, 3.0, 129801, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 205, "マコト", "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 205, 2, 24, "2030/12/17 14:59:59", 1, 10.3, 129801, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 206, "マコト", "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 206, 1, 24, "2030/12/17 14:59:59", 6, 2.93, 129801, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 207, "マコト", "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 207, 1, 24, "2030/12/17 14:59:59", 3, 9.3, 129801, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 208, "マコト", "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 208, 1, 24, "2030/12/17 14:59:59", 4, 6.9, 129801, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 209, "マコト", "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 209, 2, 24, "2030/12/17 14:59:59", 2, 0.0, 129801, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 210, "マコト", "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 210, 2, 24, "2030/12/17 14:59:59", 1, 5.95, 129801, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 211, "マコト", "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 211, 1, 24, "2030/12/17 14:59:59", 2, 6.73, 129801, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 212, "マコト", "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 212, 2, 24, "2030/12/17 14:59:59", 2, 0.0, 129801, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 213, "マコト", "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 213, 2, 24, "2030/12/17 14:59:59", 1, 6.65, 129801, 0.0);
INSERT INTO `v1_4ba67ab12aac233dbdff5d38f05c9d55cd68266956d157bd36a22355c97e45a5` VALUES (10142, "2024/03/31 12:00:00", 0.0, 214, "マコト", "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 214, 1, 24, "2030/12/17 14:59:59", 6, 3.05, 129801, 0.0);
