INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (100, 10142, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 100, 24, 10.85, "2030/12/17 14:59:59", 0.0, 7, 1, 0.0, "エリコ", "2024/03/31 12:00:00", 129901);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (101, 10142, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 101, 24, 11.15, "2030/12/17 14:59:59", 0.0, 2, 1, 0.0, "エリコ", "2024/03/31 12:00:00", 129901);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (102, 10142, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 102, 24, 6.4, "2030/12/17 14:59:59", 0.0, 5, 7, 0.0, "エリコ", "2024/03/31 12:00:00", 129901);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (103, 10142, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 103, 24, 0.0, "2030/12/17 14:59:59", 0.0, 3, 3, 0.0, "エリコ", "2024/03/31 12:00:00", 129901);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (104, 10142, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 104, 24, 2.43, "2030/12/17 14:59:59", 0.0, 8, 2, 0.0, "エリコ", "2024/03/31 12:00:00", 129901);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (105, 10142, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 105, 24, 9.08, "2030/12/17 14:59:59", 0.0, 3, 4, 0.0, "エリコ", "2024/03/31 12:00:00", 129901);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (106, 10142, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 106, 24, 0.0, "2030/12/17 14:59:59", 0.0, 6, 6, 0.0, "エリコ", "2024/03/31 12:00:00", 129901);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (107, 10142, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 107, 24, 10.6, "2030/12/17 14:59:59", 0.0, 8, 3, 0.0, "エリコ", "2024/03/31 12:00:00", 129901);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (108, 10142, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 108, 24, 7.54, "2030/12/17 14:59:59", 0.0, 4, 6, 0.0, "エリコ", "2024/03/31 12:00:00", 129901);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (109, 10142, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 109, 24, 11.5, "2030/12/17 14:59:59", 0.0, 7, 2, 0.0, "エリコ", "2024/03/31 12:00:00", 129901);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (110, 10142, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 110, 24, 8.83, "2030/12/17 14:59:59", 0.0, 1, 2, 0.0, "エリコ", "2024/03/31 12:00:00", 129901);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (111, 10142, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 111, 24, 6.68, "2030/12/17 14:59:59", 0.0, 2, 1, 0.0, "エリコ", "2024/03/31 12:00:00", 129901);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (112, 10142, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 112, 24, 8.8, "2030/12/17 14:59:59", 0.0, 2, 6, 0.0, "エリコ", "2024/03/31 12:00:00", 129901);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (113, 10142, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 113, 24, 8.9, "2030/12/17 14:59:59", 0.0, 1, 2, 0.0, "エリコ", "2024/03/31 12:00:00", 129901);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (114, 10142, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 114, 24, 3.7, "2030/12/17 14:59:59", 0.0, 5, 2, 0.0, "エリコ", "2024/03/31 12:00:00", 129901);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (200, 10142, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 200, 24, 7.6, "2030/12/17 14:59:59", 0.0, 2, 1, 0.0, "マコト", "2024/03/31 12:00:00", 129801);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (201, 10142, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 201, 24, 2.25, "2030/12/17 14:59:59", 0.0, 1, 2, 0.0, "マコト", "2024/03/31 12:00:00", 129801);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (202, 10142, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 202, 24, 3.28, "2030/12/17 14:59:59", 0.0, 1, 2, 0.0, "マコト", "2024/03/31 12:00:00", 129801);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (203, 10142, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 203, 24, 2.98, "2030/12/17 14:59:59", 0.0, 1, 4, 0.0, "マコト", "2024/03/31 12:00:00", 129801);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (204, 10142, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 204, 24, 3.0, "2030/12/17 14:59:59", 0.0, 1, 4, 0.0, "マコト", "2024/03/31 12:00:00", 129801);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (205, 10142, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 205, 24, 10.3, "2030/12/17 14:59:59", 0.0, 2, 1, 0.0, "マコト", "2024/03/31 12:00:00", 129801);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (206, 10142, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 206, 24, 2.93, "2030/12/17 14:59:59", 0.0, 1, 6, 0.0, "マコト", "2024/03/31 12:00:00", 129801);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (207, 10142, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 207, 24, 9.3, "2030/12/17 14:59:59", 0.0, 1, 3, 0.0, "マコト", "2024/03/31 12:00:00", 129801);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (208, 10142, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 208, 24, 6.9, "2030/12/17 14:59:59", 0.0, 1, 4, 0.0, "マコト", "2024/03/31 12:00:00", 129801);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (209, 10142, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 209, 24, 0.0, "2030/12/17 14:59:59", 0.0, 2, 2, 0.0, "マコト", "2024/03/31 12:00:00", 129801);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (210, 10142, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 210, 24, 5.95, "2030/12/17 14:59:59", 0.0, 2, 1, 0.0, "マコト", "2024/03/31 12:00:00", 129801);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (211, 10142, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 211, 24, 6.73, "2030/12/17 14:59:59", 0.0, 1, 2, 0.0, "マコト", "2024/03/31 12:00:00", 129801);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (212, 10142, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 212, 24, 0.0, "2030/12/17 14:59:59", 0.0, 2, 2, 0.0, "マコト", "2024/03/31 12:00:00", 129801);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (213, 10142, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 213, 24, 6.65, "2030/12/17 14:59:59", 0.0, 2, 1, 0.0, "マコト", "2024/03/31 12:00:00", 129801);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (214, 10142, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 214, 24, 3.05, "2030/12/17 14:59:59", 0.0, 1, 6, 0.0, "マコト", "2024/03/31 12:00:00", 129801);
