INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (1, 10.85, 0.0, "2024/03/31 12:00:00", 100, 100, "エリコ", 7, 24, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 129901, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (1, 11.15, 0.0, "2024/03/31 12:00:00", 101, 101, "エリコ", 2, 24, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 129901, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (7, 6.4, 0.0, "2024/03/31 12:00:00", 102, 102, "エリコ", 5, 24, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 129901, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (3, 0.0, 0.0, "2024/03/31 12:00:00", 103, 103, "エリコ", 3, 24, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 129901, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (2, 2.43, 0.0, "2024/03/31 12:00:00", 104, 104, "エリコ", 8, 24, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 129901, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (4, 9.08, 0.0, "2024/03/31 12:00:00", 105, 105, "エリコ", 3, 24, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 129901, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (6, 0.0, 0.0, "2024/03/31 12:00:00", 106, 106, "エリコ", 6, 24, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 129901, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (3, 10.6, 0.0, "2024/03/31 12:00:00", 107, 107, "エリコ", 8, 24, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 129901, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (6, 7.54, 0.0, "2024/03/31 12:00:00", 108, 108, "エリコ", 4, 24, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 129901, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (2, 11.5, 0.0, "2024/03/31 12:00:00", 109, 109, "エリコ", 7, 24, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 129901, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (2, 8.83, 0.0, "2024/03/31 12:00:00", 110, 110, "エリコ", 1, 24, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 129901, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (1, 6.68, 0.0, "2024/03/31 12:00:00", 111, 111, "エリコ", 2, 24, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 129901, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (6, 8.8, 0.0, "2024/03/31 12:00:00", 112, 112, "エリコ", 2, 24, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 129901, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (2, 8.9, 0.0, "2024/03/31 12:00:00", 113, 113, "エリコ", 1, 24, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 129901, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (2, 3.7, 0.0, "2024/03/31 12:00:00", 114, 114, "エリコ", 5, 24, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 129901, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (1, 7.6, 0.0, "2024/03/31 12:00:00", 200, 200, "マコト", 2, 24, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 129801, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (2, 2.25, 0.0, "2024/03/31 12:00:00", 201, 201, "マコト", 1, 24, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 129801, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (2, 3.28, 0.0, "2024/03/31 12:00:00", 202, 202, "マコト", 1, 24, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 129801, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (4, 2.98, 0.0, "2024/03/31 12:00:00", 203, 203, "マコト", 1, 24, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 129801, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (4, 3.0, 0.0, "2024/03/31 12:00:00", 204, 204, "マコト", 1, 24, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 129801, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (1, 10.3, 0.0, "2024/03/31 12:00:00", 205, 205, "マコト", 2, 24, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 129801, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (6, 2.93, 0.0, "2024/03/31 12:00:00", 206, 206, "マコト", 1, 24, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 129801, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (3, 9.3, 0.0, "2024/03/31 12:00:00", 207, 207, "マコト", 1, 24, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 129801, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (4, 6.9, 0.0, "2024/03/31 12:00:00", 208, 208, "マコト", 1, 24, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 129801, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (2, 0.0, 0.0, "2024/03/31 12:00:00", 209, 209, "マコト", 2, 24, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 129801, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (1, 5.95, 0.0, "2024/03/31 12:00:00", 210, 210, "マコト", 2, 24, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 129801, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (2, 6.73, 0.0, "2024/03/31 12:00:00", 211, 211, "マコト", 1, 24, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 129801, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (2, 0.0, 0.0, "2024/03/31 12:00:00", 212, 212, "マコト", 2, 24, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 129801, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (1, 6.65, 0.0, "2024/03/31 12:00:00", 213, 213, "マコト", 2, 24, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 129801, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (6, 3.05, 0.0, "2024/03/31 12:00:00", 214, 214, "マコト", 1, 24, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 129801, "2030/12/17 14:59:59", 0.0, 10142);
