INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (100, 1, 24, 10142, 7, 0.0, "2024/03/31 12:00:00", 129901, 10.85, 100, 0.0, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", "2030/12/17 14:59:59", "エリコ");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (101, 1, 24, 10142, 2, 0.0, "2024/03/31 12:00:00", 129901, 11.15, 101, 0.0, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", "2030/12/17 14:59:59", "エリコ");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (102, 7, 24, 10142, 5, 0.0, "2024/03/31 12:00:00", 129901, 6.4, 102, 0.0, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", "2030/12/17 14:59:59", "エリコ");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (103, 3, 24, 10142, 3, 0.0, "2024/03/31 12:00:00", 129901, 0.0, 103, 0.0, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", "2030/12/17 14:59:59", "エリコ");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (104, 2, 24, 10142, 8, 0.0, "2024/03/31 12:00:00", 129901, 2.43, 104, 0.0, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", "2030/12/17 14:59:59", "エリコ");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (105, 4, 24, 10142, 3, 0.0, "2024/03/31 12:00:00", 129901, 9.08, 105, 0.0, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", "2030/12/17 14:59:59", "エリコ");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (106, 6, 24, 10142, 6, 0.0, "2024/03/31 12:00:00", 129901, 0.0, 106, 0.0, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", "2030/12/17 14:59:59", "エリコ");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (107, 3, 24, 10142, 8, 0.0, "2024/03/31 12:00:00", 129901, 10.6, 107, 0.0, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", "2030/12/17 14:59:59", "エリコ");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (108, 6, 24, 10142, 4, 0.0, "2024/03/31 12:00:00", 129901, 7.54, 108, 0.0, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", "2030/12/17 14:59:59", "エリコ");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (109, 2, 24, 10142, 7, 0.0, "2024/03/31 12:00:00", 129901, 11.5, 109, 0.0, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", "2030/12/17 14:59:59", "エリコ");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (110, 2, 24, 10142, 1, 0.0, "2024/03/31 12:00:00", 129901, 8.83, 110, 0.0, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", "2030/12/17 14:59:59", "エリコ");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (111, 1, 24, 10142, 2, 0.0, "2024/03/31 12:00:00", 129901, 6.68, 111, 0.0, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", "2030/12/17 14:59:59", "エリコ");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (112, 6, 24, 10142, 2, 0.0, "2024/03/31 12:00:00", 129901, 8.8, 112, 0.0, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", "2030/12/17 14:59:59", "エリコ");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (113, 2, 24, 10142, 1, 0.0, "2024/03/31 12:00:00", 129901, 8.9, 113, 0.0, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", "2030/12/17 14:59:59", "エリコ");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (114, 2, 24, 10142, 5, 0.0, "2024/03/31 12:00:00", 129901, 3.7, 114, 0.0, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", "2030/12/17 14:59:59", "エリコ");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (200, 1, 24, 10142, 2, 0.0, "2024/03/31 12:00:00", 129801, 7.6, 200, 0.0, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", "2030/12/17 14:59:59", "マコト");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (201, 2, 24, 10142, 1, 0.0, "2024/03/31 12:00:00", 129801, 2.25, 201, 0.0, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", "2030/12/17 14:59:59", "マコト");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (202, 2, 24, 10142, 1, 0.0, "2024/03/31 12:00:00", 129801, 3.28, 202, 0.0, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", "2030/12/17 14:59:59", "マコト");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (203, 4, 24, 10142, 1, 0.0, "2024/03/31 12:00:00", 129801, 2.98, 203, 0.0, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", "2030/12/17 14:59:59", "マコト");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (204, 4, 24, 10142, 1, 0.0, "2024/03/31 12:00:00", 129801, 3.0, 204, 0.0, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", "2030/12/17 14:59:59", "マコト");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (205, 1, 24, 10142, 2, 0.0, "2024/03/31 12:00:00", 129801, 10.3, 205, 0.0, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", "2030/12/17 14:59:59", "マコト");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (206, 6, 24, 10142, 1, 0.0, "2024/03/31 12:00:00", 129801, 2.93, 206, 0.0, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", "2030/12/17 14:59:59", "マコト");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (207, 3, 24, 10142, 1, 0.0, "2024/03/31 12:00:00", 129801, 9.3, 207, 0.0, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", "2030/12/17 14:59:59", "マコト");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (208, 4, 24, 10142, 1, 0.0, "2024/03/31 12:00:00", 129801, 6.9, 208, 0.0, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", "2030/12/17 14:59:59", "マコト");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (209, 2, 24, 10142, 2, 0.0, "2024/03/31 12:00:00", 129801, 0.0, 209, 0.0, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", "2030/12/17 14:59:59", "マコト");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (210, 1, 24, 10142, 2, 0.0, "2024/03/31 12:00:00", 129801, 5.95, 210, 0.0, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", "2030/12/17 14:59:59", "マコト");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (211, 2, 24, 10142, 1, 0.0, "2024/03/31 12:00:00", 129801, 6.73, 211, 0.0, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", "2030/12/17 14:59:59", "マコト");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (212, 2, 24, 10142, 2, 0.0, "2024/03/31 12:00:00", 129801, 0.0, 212, 0.0, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", "2030/12/17 14:59:59", "マコト");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (213, 1, 24, 10142, 2, 0.0, "2024/03/31 12:00:00", 129801, 6.65, 213, 0.0, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", "2030/12/17 14:59:59", "マコト");
INSERT INTO `v1_4d3d5110821cf0084a6055880a9bae65f7f28d4654998859095e6299f4d0ba8a` VALUES (214, 6, 24, 10142, 1, 0.0, "2024/03/31 12:00:00", 129801, 3.05, 214, 0.0, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", "2030/12/17 14:59:59", "マコト");
