INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 1, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 190804, "", 60000, 1, "カリン", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 1, 0, "", 60000, 2, "", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, "", 60000, 2, "", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (2, 0, 0, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 1, 0, "", 60000, 2, "", 4);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 2, 0, "", 60000, 2, "", 5);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 2, 0, "", 60000, 2, "", 6);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (2, 0, 0, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2, 0, "", 60000, 2, "", 7);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 3, 0, "", 60000, 2, "", 8);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 3, 0, "", 60000, 2, "", 9);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 3, 0, "", 60000, 2, "", 10);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 3, 0, "", 60000, 2, "", 11);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (2, 0, 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 3, 0, "", 60000, 2, "", 12);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 4, 0, "", 60000, 2, "", 13);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 4, 0, "", 60000, 2, "", 14);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 4, 0, "", 60000, 2, "", 15);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 4, 0, "", 60000, 2, "", 16);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (2, 0, 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 4, 0, "", 60000, 2, "", 17);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 0, 2, 0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 190804, "", 60000, 3, "カリン", 18);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 3, 0, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 190804, "", 60000, 3, "カリン", 19);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 1, 0, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 0, 101201, "", 80001, 0, "ハツネ", 20);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 2, 0, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 0, 101201, "", 80001, 0, "ハツネ", 21);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 1, 0, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 0, 103801, "", 80001, 1, "シオリ", 22);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 2, 0, "来年も、\n一緒にいられたら…\nうれしいな", 0, 103801, "", 80001, 1, "シオリ", 23);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 1, 0, "あなたと一緒にいるようになって、\nもう1年ですか…", 0, 103601, "", 80001, 0, "キョウカ", 24);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 2, 0, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 0, 103601, "", 80001, 0, "キョウカ", 25);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 1, 0, "おにいちゃんとあってから、\n1年たったよ～！", 0, 102001, "", 80001, 1, "ミミ", 26);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 2, 0, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 0, 102001, "", 80001, 1, "ミミ", 27);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 1, 0, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 0, 100401, "", 80001, 2, "ミソギ", 28);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 2, 0, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 0, 100401, "", 80001, 2, "ミソギ", 29);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 1, 0, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 0, 101001, "", 80001, 0, "マホ", 30);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 2, 0, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 0, 101001, "", 80001, 0, "マホ", 31);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 1, 0, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 0, 104401, "", 80001, 1, "イリヤ", 32);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 2, 0, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 0, 104401, "", 80001, 1, "イリヤ", 33);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 1, 0, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 0, 101701, "", 80001, 2, "カオリ", 34);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 2, 0, "これからも、どうぞよろしく\nお願いするさ～！", 0, 101701, "", 80001, 2, "カオリ", 35);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 1, 0, "あんたと出会ってからもうそんなに\nたつのね～", 0, 102801, "", 80001, 0, "サレン", 36);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 2, 0, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 0, 102801, "", 80001, 0, "サレン", 37);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 1, 0, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 0, 102501, "", 80001, 1, "スズメ", 38);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 2, 0, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 0, 102501, "", 80001, 1, "スズメ", 39);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 1, 0, "振り返れば、今までいろんなことが\nあったにゃあ…", 0, 104601, "", 80001, 0, "タマキ", 40);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 2, 0, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 0, 104601, "", 80001, 0, "タマキ", 41);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 1, 0, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 0, 104801, "", 80001, 1, "ミフユ", 42);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 2, 0, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 0, 104801, "", 80001, 1, "ミフユ", 43);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 1, 0, "あなたさまと出会ってもう1年…", 0, 103201, "", 80001, 0, "アキノ", 44);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 2, 0, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 0, 103201, "", 80001, 0, "アキノ", 45);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 1, 0, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 0, 103401, "", 80001, 1, "ユカリ", 46);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 2, 0, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 0, 103401, "", 80001, 1, "ユカリ", 47);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 1, 0, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 0, 107101, "", 80001, 0, "クリスティーナ", 48);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 2, 0, "ともかくこれからも\n愉しませてくれよ☆", 0, 107101, "", 80001, 0, "クリスティーナ", 49);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 1, 0, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 0, 104701, "", 80001, 1, "ジュン", 50);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 2, 0, "見えないから分からないって？\nそれもそうだな…", 0, 104701, "", 80001, 1, "ジュン", 51);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 1, 0, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 0, 103701, "", 80001, 0, "トモ", 52);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 2, 0, "これからもよろしくね！", 0, 103701, "", 80001, 0, "トモ", 53);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 1, 0, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 0, 100501, "", 80001, 1, "マツリ", 54);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 2, 0, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 0, 100501, "", 80001, 1, "マツリ", 55);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 1, 0, "オマエと会って1年なの！\n全然成仏もできなかったし、", 0, 100701, "", 80001, 0, "ミヤコ", 56);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 2, 0, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 0, 100701, "", 80001, 0, "ミヤコ", 57);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 1, 0, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 0, 103101, "", 80001, 1, "シノブ", 58);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 2, 0, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 0, 103101, "", 80001, 1, "シノブ", 59);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 1, 0, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 0, 100901, "", 80001, 0, "アンナ", 60);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 2, 0, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 0, 100901, "", 80001, 0, "アンナ", 61);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 1, 0, "君と出会ってもう1年だって！\n早いねー！", 0, 101301, "", 80001, 1, "ナナカ", 62);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 2, 0, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 0, 101301, "", 80001, 1, "ナナカ", 63);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 1, 0, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 0, 105101, "", 80001, 0, "ミツキ", 64);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 2, 0, "これからも、いろんなところを\n調べさせてね？", 0, 105101, "", 80001, 0, "ミツキ", 65);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 1, 0, "お前さんと出会ってもう1年か\n助太刀するつもりが", 0, 105601, "", 80001, 1, "ルカ", 66);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 2, 0, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 0, 105601, "", 80001, 1, "ルカ", 67);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 1, 0, "早いものですわね、あなた様と\nお会いして、もう1年…", 0, 102701, "", 80001, 2, "エリコ", 68);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 2, 0, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 0, 102701, "", 80001, 2, "エリコ", 69);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 1, 0, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 0, 102301, "", 80001, 0, "アヤネ", 70);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 2, 0, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 0, 102301, "", 80001, 0, "アヤネ", 71);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 1, 0, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 0, 104201, "", 80001, 1, "チカ", 72);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 2, 0, "私のことを見守ってください\nできることなら、ずっと…", 0, 104201, "", 80001, 1, "チカ", 73);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 1, 0, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 0, 102101, "", 80001, 2, "クルミ", 74);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 2, 0, "で、でもこれからもずっと…\n一緒にいたいなぁ…", 0, 102101, "", 80001, 2, "クルミ", 75);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 1, 0, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 0, 104901, "", 80001, 0, "シズル", 76);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 2, 0, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 0, 104901, "", 80001, 0, "シズル", 77);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 1, 0, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 0, 101101, "", 80001, 1, "リノ", 78);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 2, 0, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 0, 101101, "", 80001, 1, "リノ", 79);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 1, 0, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 0, 100201, "", 80001, 0, "ユイ", 80);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 2, 0, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 0, 100201, "", 80001, 0, "ユイ", 81);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 1, 0, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 0, 100101, "", 80001, 1, "ヒヨリ", 82);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 2, 0, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 0, 100101, "", 80001, 1, "ヒヨリ", 83);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 1, 0, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 0, 100301, "", 80001, 2, "レイ", 84);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 2, 0, "キミに背中を預けられる日が、\n早くきますように", 0, 100301, "", 80001, 2, "レイ", 85);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 1, 0, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 0, 105801, "", 80001, 0, "ペコリーヌ", 86);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 2, 0, "これからもずっと、覚えていて\nくださいね！", 0, 105801, "", 80001, 0, "ペコリーヌ", 87);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 1, 0, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 0, 105901, "", 80001, 1, "コッコロ", 88);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 2, 0, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 0, 105901, "", 80001, 1, "コッコロ", 89);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 1, 0, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 0, 106001, "", 80001, 2, "キャル", 90);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 2, 0, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 0, 106001, "", 80001, 2, "キャル", 91);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 1, 0, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 0, 112101, "", 60001, 1, "スズメ", 92);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 1, 0, "", 60001, 2, "", 93);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, "", 60001, 2, "", 94);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 1, 0, "", 60001, 2, "", 95);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 1, 0, "", 60001, 2, "", 96);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (2, 0, 0, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 1, 0, "", 60001, 2, "", 97);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 2, 0, "", 60001, 2, "", 98);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 2, 0, "", 60001, 2, "", 99);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 2, 0, "", 60001, 2, "", 100);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 2, 0, "", 60001, 2, "", 101);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (2, 0, 0, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2, 0, "", 60001, 2, "", 102);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 3, 0, "", 60001, 2, "", 103);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 3, 0, "", 60001, 2, "", 104);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 3, 0, "", 60001, 2, "", 105);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 3, 0, "", 60001, 2, "", 106);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 3, 0, "", 60001, 2, "", 107);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 3, 0, "", 60001, 2, "", 108);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (2, 0, 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 3, 0, "", 60001, 2, "", 109);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 4, 0, "", 60001, 2, "", 110);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 4, 0, "", 60001, 2, "", 111);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 4, 0, "", 60001, 2, "", 112);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 4, 0, "", 60001, 2, "", 113);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 4, 0, "", 60001, 2, "", 114);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 4, 0, "", 60001, 2, "", 115);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (2, 0, 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 4, 0, "", 60001, 2, "", 116);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 0, 2, 0, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 0, 112101, "", 60001, 3, "スズメ", 117);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 3, 0, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 0, 112101, "", 60001, 3, "スズメ", 118);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 1, 0, "ヒューヒュー♪\nドンドンパフパフー♪", 0, 105801, "", 80003, 0, "ペコリーヌ", 119);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 1, 0, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 0, 105901, "", 80003, 1, "コッコロ", 120);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 2, 0, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 0, 105901, "", 80003, 1, "コッコロ", 121);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 1, 0, "え？　ちょっと\nいきなりなにっ？", 0, 106001, "", 80003, 2, "キャル", 122);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 2, 0, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 0, 106001, "", 80003, 2, "キャル", 123);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 2, 0, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 0, 105801, "", 80003, 0, "ペコリーヌ", 124);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 3, 0, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 0, 105801, "", 80003, 0, "ペコリーヌ", 125);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 3, 0, "ですからその日に向けて\n今から気分を盛り上げているのです", 0, 105901, "", 80003, 1, "コッコロ", 126);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 3, 0, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 0, 106001, "", 80003, 2, "キャル", 127);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 4, 0, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 0, 105901, "", 80003, 1, "コッコロ", 128);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 4, 0, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 0, 105801, "", 80003, 0, "ペコリーヌ", 129);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 4, 0, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 0, 106001, "", 80003, 2, "キャル", 130);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 5, 0, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 0, 105901, "", 80003, 1, "コッコロ", 131);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 5, 0, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 0, 106001, "", 80003, 0, "キャル", 132);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 1, 0, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 0, 190801, "", 80003, 1, "カリン", 133);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 6, 0, "げっ…カリンじゃない\nなんでもないわよ！", 0, 106001, "", 80003, 0, "キャル", 134);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 2, 0, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 0, 190801, "", 80003, 1, "カリン", 135);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 7, 0, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 0, 106001, "", 80003, 0, "キャル", 136);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 3, 0, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 0, 190801, "", 80003, 1, "カリン", 137);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 8, 0, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 0, 106001, "", 80003, 0, "キャル", 138);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 9, 0, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 0, 106001, "", 80003, 0, "キャル", 139);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 4, 0, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 0, 190801, "", 80003, 1, "カリン", 140);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 10, 0, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 0, 106001, "", 80003, 0, "キャル", 141);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 5, 0, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 0, 190801, "", 80003, 1, "カリン", 142);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 11, 0, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 0, 106001, "", 80003, 0, "キャル", 143);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 6, 0, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 0, 105901, "", 80003, 0, "コッコロ", 144);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 5, 0, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 0, 105801, "", 80003, 1, "ペコリーヌ", 145);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 7, 0, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 0, 105901, "", 80003, 0, "コッコロ", 146);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 8, 0, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 0, 105901, "", 80003, 0, "コッコロ", 147);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 6, 0, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 0, 105801, "", 80003, 1, "ペコリーヌ", 148);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 7, 0, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 0, 105801, "", 80003, 1, "ペコリーヌ", 149);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 9, 0, "あとは、牧場に素敵な温泉が\n出来たとか…", 0, 105901, "", 80003, 0, "コッコロ", 150);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 8, 0, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 0, 105801, "", 80003, 1, "ペコリーヌ", 151);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 10, 0, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 0, 105901, "", 80003, 0, "コッコロ", 152);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 9, 0, "えへへ、ありがとうございます\nコッコロちゃん！", 0, 105801, "", 80003, 1, "ペコリーヌ", 153);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 11, 0, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 0, 105901, "", 80003, 0, "コッコロ", 154);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 10, 0, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 0, 105801, "", 80003, 1, "ペコリーヌ", 155);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 11, 0, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 0, 105801, "", 80003, 0, "ペコリーヌ", 156);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 12, 0, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 0, 106001, "", 80003, 1, "キャル", 157);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 12, 0, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 0, 105801, "", 80003, 0, "ペコリーヌ", 158);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 13, 0, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 0, 106001, "", 80003, 1, "キャル", 159);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 13, 0, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 0, 105801, "", 80003, 0, "ペコリーヌ", 160);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 14, 0, "…積極的？\nなんの話よ？", 0, 106001, "", 80003, 1, "キャル", 161);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 14, 0, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 0, 105801, "", 80003, 0, "ペコリーヌ", 162);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 15, 0, "ムーサの町で\nきゃるきゃる～ん☆　って！", 0, 105801, "", 80003, 0, "ペコリーヌ", 163);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 15, 0, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 0, 106001, "", 80003, 1, "キャル", 164);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 16, 0, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 0, 105801, "", 80003, 0, "ペコリーヌ", 165);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 16, 0, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 0, 106001, "", 80003, 1, "キャル", 166);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 17, 0, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 0, 106001, "", 80003, 1, "キャル", 167);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 18, 0, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 0, 106001, "", 80003, 0, "キャル", 168);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 12, 0, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 0, 105901, "", 80003, 1, "コッコロ", 169);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 19, 0, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 0, 106001, "", 80003, 0, "キャル", 170);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 13, 0, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 0, 105901, "", 80003, 1, "コッコロ", 171);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 20, 0, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 0, 106001, "", 80003, 0, "キャル", 172);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 14, 0, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 0, 105901, "", 80003, 1, "コッコロ", 173);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 21, 0, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 0, 106001, "", 80003, 0, "キャル", 174);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 15, 0, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 0, 105901, "", 80003, 1, "コッコロ", 175);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 22, 0, "かくし芸？", 0, 106001, "", 80003, 0, "キャル", 176);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 16, 0, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 0, 105901, "", 80003, 1, "コッコロ", 177);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 23, 0, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 0, 106001, "", 80003, 0, "キャル", 178);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 17, 0, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 0, 105801, "", 80003, 0, "ペコリーヌ", 179);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 24, 0, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 0, 106001, "", 80003, 2, "キャル", 180);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 17, 0, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 0, 105901, "", 80003, 1, "コッコロ", 181);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 18, 0, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 0, 105801, "", 80003, 0, "ペコリーヌ", 182);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 25, 0, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 0, 106001, "", 80003, 2, "キャル", 183);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 18, 0, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 0, 105901, "", 80003, 1, "コッコロ", 184);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 26, 0, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 0, 106001, "", 80003, 2, "キャル", 185);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 27, 0, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 0, 106001, "", 80003, 2, "キャル", 186);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 28, 0, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 0, 106001, "", 80003, 2, "キャル", 187);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 19, 0, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 0, 105801, "", 80003, 0, "ペコリーヌ", 188);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 19, 0, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 0, 105901, "", 80003, 1, "コッコロ", 189);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 29, 0, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 0, 106001, "", 80003, 2, "キャル", 190);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 30, 0, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 0, 106001, "", 80003, 0, "キャル", 191);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 31, 0, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 0, 106001, "", 80003, 0, "キャル", 192);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 32, 0, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 0, 106001, "", 80003, 0, "キャル", 193);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 33, 0, "あー\nあー\n…声、これで大丈夫かしら？", 0, 106001, "", 80003, 0, "キャル", 194);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 34, 0, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 0, 106001, "", 80003, 0, "キャル", 195);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 35, 0, "オイッスー☆", 0, 106001, "", 80003, 0, "キャル", 196);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 36, 0, "……\nいや、ないわ\nいつもどおりが一番ね、うん", 0, 106001, "", 80003, 0, "キャル", 197);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 37, 0, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 0, 106001, "", 80003, 0, "キャル", 198);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 38, 0, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 0, 106001, "", 80003, 0, "キャル", 199);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 20, 0, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 0, 105901, "", 80003, 0, "コッコロ", 200);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 21, 0, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 0, 105901, "", 80003, 0, "コッコロ", 201);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 22, 0, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 0, 105901, "", 80003, 0, "コッコロ", 202);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 23, 0, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 0, 105901, "", 80003, 0, "コッコロ", 203);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 24, 0, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 0, 105901, "", 80003, 0, "コッコロ", 204);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 25, 0, "オイッスー☆", 0, 105901, "", 80003, 0, "コッコロ", 205);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 26, 0, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 0, 105901, "", 80003, 0, "コッコロ", 206);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 27, 0, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 0, 105901, "", 80003, 0, "コッコロ", 207);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 28, 0, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 0, 105901, "", 80003, 0, "コッコロ", 208);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 20, 0, "オイッスー☆", 0, 105801, "", 80003, 0, "ペコリーヌ", 209);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 21, 0, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 0, 105801, "", 80003, 0, "ペコリーヌ", 210);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 22, 0, "あと６日で2.5周年ですか…", 0, 105801, "", 80003, 0, "ペコリーヌ", 211);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 23, 0, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 0, 105801, "", 80003, 0, "ペコリーヌ", 212);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 24, 0, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 0, 105801, "", 80003, 0, "ペコリーヌ", 213);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 25, 0, "オイッスー☆", 0, 105801, "", 80003, 0, "ペコリーヌ", 214);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 26, 0, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 0, 105801, "", 80003, 0, "ペコリーヌ", 215);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 27, 0, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 0, 105801, "", 80003, 0, "ペコリーヌ", 216);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 28, 0, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 0, 105801, "", 80003, 0, "ペコリーヌ", 217);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 29, 0, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 0, 105801, "", 80003, 0, "ペコリーヌ", 218);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 30, 0, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 0, 105801, "", 80003, 0, "ペコリーヌ", 219);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 31, 0, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 0, 105801, "", 80003, 0, "ペコリーヌ", 220);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 29, 0, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 0, 105901, "", 80003, 1, "コッコロ", 221);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 32, 0, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 0, 105801, "", 80003, 0, "ペコリーヌ", 222);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 39, 0, "…………", 0, 106001, "", 80003, 2, "キャル", 223);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 30, 0, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 0, 105901, "", 80003, 1, "コッコロ", 224);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 40, 0, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 0, 106001, "", 80003, 2, "キャル", 225);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 33, 0, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 0, 105801, "", 80003, 0, "ペコリーヌ", 226);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 41, 0, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 0, 106001, "", 80003, 2, "キャル", 227);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 34, 0, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 0, 105801, "", 80003, 0, "ペコリーヌ", 228);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 35, 0, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 0, 105801, "", 80003, 0, "ペコリーヌ", 229);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 42, 0, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 0, 106001, "", 80003, 2, "キャル", 230);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 31, 0, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 0, 105901, "", 80003, 1, "コッコロ", 231);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 43, 0, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 0, 106001, "", 80003, 2, "キャル", 232);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 36, 0, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 0, 105801, "", 80003, 0, "ペコリーヌ", 233);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 6, 0, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 0, 190801, "", 80003, 0, "カリン", 234);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 7, 0, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 0, 190801, "", 80003, 0, "カリン", 235);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 8, 0, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 0, 190801, "", 80003, 0, "カリン", 236);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 9, 0, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 0, 190801, "", 80003, 0, "カリン", 237);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 10, 0, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 0, 190801, "", 80003, 0, "カリン", 238);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 11, 0, "次にコッコロさんですが\nこちらは発声練習をしています", 0, 190801, "", 80003, 0, "カリン", 239);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 12, 0, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 0, 190801, "", 80003, 0, "カリン", 240);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 13, 0, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 0, 190801, "", 80003, 0, "カリン", 241);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 14, 0, "あれ？\nもしかして緊張で気を失って…？", 0, 190801, "", 80003, 0, "カリン", 242);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 15, 0, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 0, 190801, "", 80003, 0, "カリン", 243);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 16, 0, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 0, 190801, "", 80003, 0, "カリン", 244);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 17, 0, "それでは\nまた明日お会いしましょう♪", 0, 190801, "", 80003, 0, "カリン", 245);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 44, 0, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 0, 106001, "", 80003, 0, "キャル", 246);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 37, 0, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 0, 105801, "", 80003, 2, "ペコリーヌ", 247);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 32, 0, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 0, 105901, "", 80003, 1, "コッコロ", 248);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 45, 0, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 0, 106001, "", 80003, 0, "キャル", 249);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 38, 0, "ひどいですよぉキャルちゃ～ん！", 0, 105801, "", 80003, 2, "ペコリーヌ", 250);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 33, 0, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 0, 105901, "", 80003, 1, "コッコロ", 251);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 39, 0, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 0, 105801, "", 80003, 2, "ペコリーヌ", 252);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 46, 0, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 0, 106001, "", 80003, 0, "キャル", 253);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 34, 0, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 0, 105901, "", 80003, 1, "コッコロ", 254);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 40, 0, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 0, 105801, "", 80003, 2, "ペコリーヌ", 255);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 41, 0, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 0, 105801, "", 80003, 0, "ペコリーヌ", 256);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 47, 0, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 0, 106001, "", 80003, 2, "キャル", 257);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 42, 0, "忘れてきちゃいました☆", 0, 105801, "", 80003, 0, "ペコリーヌ", 258);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 48, 0, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 0, 106001, "", 80003, 2, "キャル", 259);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 35, 0, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 0, 105901, "", 80003, 1, "コッコロ", 260);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 49, 0, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 0, 106001, "", 80003, 2, "キャル", 261);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 43, 0, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 0, 105801, "", 80003, 0, "ペコリーヌ", 262);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 50, 0, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 0, 106001, "", 80003, 2, "キャル", 263);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 36, 0, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 0, 105901, "", 80003, 1, "コッコロ", 264);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 51, 0, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 0, 106001, "", 80003, 2, "キャル", 265);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 37, 0, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 0, 105901, "", 80003, 1, "コッコロ", 266);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 52, 0, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 0, 106001, "", 80003, 2, "キャル", 267);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 44, 0, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 0, 105801, "", 80003, 0, "ペコリーヌ", 268);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 38, 0, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 0, 105901, "", 80003, 1, "コッコロ", 269);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 45, 0, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 0, 105801, "", 80003, 0, "ペコリーヌ", 270);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 53, 0, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 0, 106001, "", 80003, 2, "キャル", 271);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 46, 0, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 0, 105801, "", 80003, 0, "ペコリーヌ", 272);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 18, 0, "こんにちは\n【ギルド管理協会】のカリンです", 0, 190801, "", 80003, 0, "カリン", 273);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 19, 0, "昨日の生放送は\nいかがだったでしょうか？", 0, 190801, "", 80003, 0, "カリン", 274);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 20, 0, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 0, 190801, "", 80003, 0, "カリン", 275);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 21, 0, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 0, 190801, "", 80003, 0, "カリン", 276);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 22, 0, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 0, 190801, "", 80003, 0, "カリン", 277);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 23, 0, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 0, 190801, "", 80003, 0, "カリン", 278);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 24, 0, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 0, 190801, "", 80003, 0, "カリン", 279);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 25, 0, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 0, 190801, "", 80003, 0, "カリン", 280);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 26, 0, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 0, 190801, "", 80003, 0, "カリン", 281);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 27, 0, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 0, 190801, "", 80003, 0, "カリン", 282);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 47, 0, "準備はいいですかっ？\n…いっきますよー！", 0, 105801, "", 80003, 0, "ペコリーヌ", 283);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 54, 0, "祝！", 0, 106001, "", 80003, 2, "キャル", 284);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 39, 0, "2.5周年！", 0, 105901, "", 80003, 1, "コッコロ", 285);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 48, 0, "おめでとうございま～す☆", 0, 105801, "", 80003, 0, "ペコリーヌ", 286);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 55, 0, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 0, 106001, "", 80003, 2, "キャル", 287);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 40, 0, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 0, 105901, "", 80003, 1, "コッコロ", 288);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 49, 0, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 0, 105801, "", 80003, 0, "ペコリーヌ", 289);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 56, 0, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 0, 106001, "", 80003, 2, "キャル", 290);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 41, 0, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 0, 105901, "", 80003, 1, "コッコロ", 291);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 57, 0, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 0, 106001, "", 80003, 2, "キャル", 292);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 50, 0, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 0, 105801, "", 80003, 0, "ペコリーヌ", 293);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 58, 0, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 0, 106001, "", 80003, 2, "キャル", 294);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 51, 0, "記念日だから\n平気平気♪\nやばいですね☆", 0, 105801, "", 80003, 0, "ペコリーヌ", 295);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 59, 0, "意味分かんないわよ！", 0, 106001, "", 80003, 2, "キャル", 296);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 42, 0, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 0, 105901, "", 80003, 1, "コッコロ", 297);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 43, 0, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 0, 105901, "", 80003, 1, "コッコロ", 298);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 52, 0, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 0, 105801, "", 80003, 0, "ペコリーヌ", 299);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 60, 0, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 0, 106001, "", 80003, 2, "キャル", 300);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 44, 0, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 0, 105901, "", 80003, 1, "コッコロ", 301);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 53, 0, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 0, 105801, "", 80003, 0, "ペコリーヌ", 302);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 54, 1, "2.5周年\nありがとうございます！", 0, 105801, "105901,45,106001,61", 80003, 0, "ペコリーヌ・コッコロ・キャル", 303);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 1, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 190804, "", 60002, 1, "カリン", 304);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 1, 0, "", 60002, 2, "", 305);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, "", 60002, 2, "", 306);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 1, 0, "", 60002, 2, "", 307);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 1, 0, "", 60002, 2, "", 308);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (2, 0, 0, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 1, 0, "", 60002, 2, "", 309);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 2, 0, "", 60002, 2, "", 310);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 2, 0, "", 60002, 2, "", 311);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 2, 0, "", 60002, 2, "", 312);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 2, 0, "", 60002, 2, "", 313);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (2, 0, 0, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 2, 0, "", 60002, 2, "", 314);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 3, 0, "", 60002, 2, "", 315);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 3, 0, "", 60002, 2, "", 316);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 3, 0, "", 60002, 2, "", 317);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 3, 0, "", 60002, 2, "", 318);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 3, 0, "", 60002, 2, "", 319);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 3, 0, "", 60002, 2, "", 320);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (2, 0, 0, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 3, 0, "", 60002, 2, "", 321);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 4, 0, "", 60002, 2, "", 322);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 4, 0, "", 60002, 2, "", 323);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 4, 0, "", 60002, 2, "", 324);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 4, 0, "", 60002, 2, "", 325);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 4, 0, "", 60002, 2, "", 326);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 4, 0, "", 60002, 2, "", 327);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (2, 0, 0, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 4, 0, "", 60002, 2, "", 328);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 0, 2, 0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 190804, "", 60002, 3, "カリン", 329);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 3, 0, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 190804, "", 60002, 3, "カリン", 330);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 1, 0, "ささ、シェフィさまはこちらへ…", 0, 105901, "", 80005, 4, "コッコロ", 331);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 1, 0, "コッコロさん、どこへ行くの…？\n…え？", 0, 106401, "", 80005, 0, "シェフィ", 332);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 1, 0, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 0, 105801, "", 80005, 3, "ペコリーヌ", 333);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 2, 0, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 0, 105901, "", 80005, 4, "コッコロ", 334);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 2, 0, "ええと…\n二人とも何をしているの？", 0, 106401, "", 80005, 0, "シェフィ", 335);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 1, 0, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 0, 106001, "", 80005, 1, "キャル", 336);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 3, 0, "あの日…？", 0, 106401, "", 80005, 0, "シェフィ", 337);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 2, 0, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 0, 105801, "", 80005, 3, "ペコリーヌ", 338);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 3, 0, "『プリンセスコネクト！\nRe:Dive』3.5周年！", 0, 105801, "", 80005, 3, "ペコリーヌ", 339);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 1, 0, "やばいですね☆", 0, 190801, "", 80005, 2, "カリン", 340);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 2, 0, "って、なにしれっと\n参加してんのよカリン！", 0, 106001, "", 80005, 1, "キャル", 341);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 2, 0, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 0, 190801, "", 80005, 2, "カリン", 342);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 3, 0, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 0, 190801, "", 80005, 2, "カリン", 343);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 4, 0, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 0, 105801, "", 80005, 3, "ペコリーヌ", 344);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 1, 4, 0, "えっ！？　ええとええと…！\nあ、あと14日…！", 0, 106401, "", 80005, 0, "シェフィ", 345);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 5, 0, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 0, 105801, "", 80005, 3, "ペコリーヌ", 346);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 3, 0, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 0, 105901, "", 80005, 1, "コッコロ", 347);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 4, 0, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 0, 105901, "", 80005, 1, "コッコロ", 348);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 6, 0, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 0, 105801, "", 80005, 3, "ペコリーヌ", 349);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 5, 0, "さすがでございます\nペコリーヌさま", 0, 105901, "", 80005, 1, "コッコロ", 350);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 3, 0, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 0, 106001, "", 80005, 0, "キャル", 351);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 5, 0, "しゃなり、しゃなり…\nふふ、案外できるものね", 0, 106401, "", 80005, 2, "シェフィ", 352);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 7, 0, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 0, 105801, "", 80005, 3, "ペコリーヌ", 353);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 6, 0, "さ、次はキャルさまの\n番でございますよ", 0, 105901, "", 80005, 1, "コッコロ", 354);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 4, 0, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 0, 106001, "", 80005, 0, "キャル", 355);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 6, 0, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 0, 106401, "", 80005, 2, "シェフィ", 356);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 5, 0, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 0, 106001, "", 80005, 0, "キャル", 357);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 6, 0, "いたたた…もぉお～！\nなんであたしがこんな目に～！", 0, 106001, "", 80005, 0, "キャル", 358);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 7, 0, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 0, 105901, "", 80005, 1, "コッコロ", 359);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 2, 7, 0, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 0, 106001, "", 80005, 0, "キャル", 360);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 8, 0, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 0, 105901, "", 80005, 1, "コッコロ", 361);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 8, 0, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 0, 105801, "", 80005, 3, "ペコリーヌ", 362);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 9, 0, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 0, 105901, "", 80005, 1, "コッコロ", 363);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 7, 0, "聖学祭…って、なにかしら？", 0, 106401, "", 80005, 2, "シェフィ", 364);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 10, 0, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 0, 105901, "", 80005, 1, "コッコロ", 365);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 8, 0, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 0, 106001, "", 80005, 0, "キャル", 366);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 9, 0, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 0, 106001, "", 80005, 0, "キャル", 367);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 9, 0, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 0, 105801, "", 80005, 3, "ペコリーヌ", 368);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 10, 0, "いや、そのまんますぎでしょ…", 0, 106001, "", 80005, 0, "キャル", 369);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 8, 0, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 0, 106401, "", 80005, 2, "シェフィ", 370);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 10, 0, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 0, 105801, "", 80005, 3, "ペコリーヌ", 371);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 3, 9, 0, "えっ？　ええ…\nぎゅーっ…？", 0, 106401, "", 80005, 2, "シェフィ", 372);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 11, 0, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 0, 106001, "", 80005, 0, "キャル", 373);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 10, 0, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 0, 106401, "", 80005, 2, "シェフィ", 374);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 12, 0, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 0, 106001, "", 80005, 0, "キャル", 375);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 11, 0, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 0, 105901, "", 80005, 1, "コッコロ", 376);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 13, 0, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 0, 106001, "", 80005, 0, "キャル", 377);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 11, 0, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 0, 105801, "", 80005, 3, "ペコリーヌ", 378);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 14, 0, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 0, 106001, "", 80005, 0, "キャル", 379);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 15, 0, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 0, 106001, "", 80005, 0, "キャル", 380);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 11, 0, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 0, 106401, "", 80005, 2, "シェフィ", 381);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 12, 0, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 0, 105801, "", 80005, 3, "ペコリーヌ", 382);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 12, 0, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 0, 105901, "", 80005, 1, "コッコロ", 383);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 16, 0, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 0, 106001, "", 80005, 0, "キャル", 384);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 4, 12, 0, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 0, 106401, "", 80005, 2, "シェフィ", 385);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 13, 0, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 0, 105801, "", 80005, 3, "ペコリーヌ", 386);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 14, 0, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 0, 105801, "", 80005, 3, "ペコリーヌ", 387);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 15, 0, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 0, 105801, "", 80005, 3, "ペコリーヌ", 388);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 17, 0, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 0, 106001, "", 80005, 0, "キャル", 389);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 18, 0, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 0, 106001, "", 80005, 0, "キャル", 390);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 13, 0, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 0, 106401, "", 80005, 2, "シェフィ", 391);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 13, 0, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 0, 105901, "", 80005, 1, "コッコロ", 392);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 16, 0, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 0, 105801, "", 80005, 3, "ペコリーヌ", 393);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 19, 0, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 0, 106001, "", 80005, 0, "キャル", 394);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 17, 0, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 0, 105801, "", 80005, 3, "ペコリーヌ", 395);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 18, 0, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 0, 105801, "", 80005, 3, "ペコリーヌ", 396);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 5, 19, 0, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 0, 105801, "", 80005, 3, "ペコリーヌ", 397);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 4, 0, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 0, 190801, "", 80005, 2, "カリン", 398);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 5, 0, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 0, 190801, "", 80005, 2, "カリン", 399);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 20, 0, "…えっ！？", 0, 106001, "", 80005, 1, "キャル", 400);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 20, 0, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 0, 105801, "", 80005, 3, "ペコリーヌ", 401);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 14, 0, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 0, 106401, "", 80005, 0, "シェフィ", 402);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 14, 0, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 0, 105901, "", 80005, 4, "コッコロ", 403);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 15, 0, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 0, 106401, "", 80005, 0, "シェフィ", 404);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 21, 0, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 0, 106001, "", 80005, 1, "キャル", 405);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 21, 0, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 0, 105801, "", 80005, 3, "ペコリーヌ", 406);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 22, 0, "ぶっ殺すぞ！？", 0, 106001, "", 80005, 1, "キャル", 407);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 6, 6, 0, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 0, 190801, "", 80005, 2, "カリン", 408);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 15, 0, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 0, 105901, "", 80005, 1, "コッコロ", 409);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 23, 0, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 0, 106001, "", 80005, 0, "キャル", 410);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 16, 0, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 0, 106401, "", 80005, 2, "シェフィ", 411);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 22, 0, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 0, 105801, "", 80005, 3, "ペコリーヌ", 412);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 24, 0, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 0, 106001, "", 80005, 0, "キャル", 413);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 17, 0, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 0, 106401, "", 80005, 2, "シェフィ", 414);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 16, 0, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 0, 105901, "", 80005, 1, "コッコロ", 415);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 18, 0, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 0, 106401, "", 80005, 2, "シェフィ", 416);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 17, 0, "もちろんでございます\n仲良くお勉強いたしましょう", 0, 105901, "", 80005, 1, "コッコロ", 417);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 25, 0, "しれっと教育ママが\n生まれてる…？", 0, 106001, "", 80005, 0, "キャル", 418);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 23, 0, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 0, 105801, "", 80005, 3, "ペコリーヌ", 419);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 7, 26, 0, "え～～～～！？", 0, 106001, "", 80005, 0, "キャル", 420);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 27, 0, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 0, 106001, "", 80005, 0, "キャル", 421);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 19, 0, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 0, 106401, "", 80005, 2, "シェフィ", 422);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 18, 0, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 0, 105901, "", 80005, 1, "コッコロ", 423);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 24, 0, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 0, 105801, "", 80005, 3, "ペコリーヌ", 424);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 20, 0, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 0, 106401, "", 80005, 2, "シェフィ", 425);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 28, 0, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 0, 106001, "", 80005, 0, "キャル", 426);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 19, 0, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 0, 105901, "", 80005, 1, "コッコロ", 427);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 21, 0, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 0, 106401, "", 80005, 2, "シェフィ", 428);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 29, 0, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 0, 106001, "", 80005, 0, "キャル", 429);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 30, 0, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 0, 106001, "", 80005, 0, "キャル", 430);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 25, 0, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 0, 105801, "", 80005, 3, "ペコリーヌ", 431);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 8, 31, 0, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 0, 106001, "", 80005, 0, "キャル", 432);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 22, 0, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 0, 106401, "", 80005, 0, "シェフィ", 433);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 23, 0, "…えっ？\n生放送の意気込み！？\n突然なに！？", 0, 106401, "", 80005, 0, "シェフィ", 434);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 24, 0, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 0, 106401, "", 80005, 0, "シェフィ", 435);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 25, 0, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 0, 106401, "", 80005, 0, "シェフィ", 436);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 26, 0, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 0, 106401, "", 80005, 0, "シェフィ", 437);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 27, 0, "それから、それから…！\nぅ、ぅぅっ…！", 0, 106401, "", 80005, 0, "シェフィ", 438);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 28, 0, "―びえぇぇ～んっ！！！", 0, 106401, "", 80005, 0, "シェフィ", 439);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 20, 0, "どうされましたか、シェフィさま", 0, 105901, "", 80005, 1, "コッコロ", 440);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 29, 0, "ううっ…ひっく…\nママッ…！", 0, 106401, "", 80005, 0, "シェフィ", 441);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 21, 0, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 0, 105901, "", 80005, 1, "コッコロ", 442);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 22, 0, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 0, 105901, "", 80005, 1, "コッコロ", 443);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 9, 30, 0, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 0, 106401, "", 80005, 0, "シェフィ", 444);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 23, 0, "主さま\n3.5周年まであと５日\nでございます", 0, 105901, "", 80005, 0, "コッコロ", 445);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 24, 0, "ですがその前に\n生放送がありますね", 0, 105901, "", 80005, 0, "コッコロ", 446);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 25, 0, "…生放送に向けた\n意気込みでございますか？", 0, 105901, "", 80005, 0, "コッコロ", 447);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 26, 0, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 0, 105901, "", 80005, 0, "コッコロ", 448);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 27, 0, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 0, 105901, "", 80005, 0, "コッコロ", 449);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 28, 0, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 0, 105901, "", 80005, 0, "コッコロ", 450);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 29, 0, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 0, 105901, "", 80005, 0, "コッコロ", 451);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 30, 0, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 0, 105901, "", 80005, 0, "コッコロ", 452);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 10, 31, 0, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 0, 105901, "", 80005, 0, "コッコロ", 453);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 26, 0, "3.5周年まであと４日！\nやばいですね☆", 0, 105801, "", 80005, 0, "ペコリーヌ", 454);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 32, 0, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 0, 106001, "", 80005, 1, "キャル", 455);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 27, 0, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 0, 105801, "", 80005, 0, "ペコリーヌ", 456);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 28, 0, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 0, 105801, "", 80005, 0, "ペコリーヌ", 457);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 29, 0, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 0, 105801, "", 80005, 0, "ペコリーヌ", 458);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 33, 0, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 0, 106001, "", 80005, 1, "キャル", 459);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 30, 0, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 0, 105801, "", 80005, 0, "ペコリーヌ", 460);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 31, 0, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 0, 105801, "", 80005, 0, "ペコリーヌ", 461);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 32, 0, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 0, 105801, "", 80005, 0, "ペコリーヌ", 462);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 34, 0, "だからあたしはやるなんて一言も…", 0, 106001, "", 80005, 1, "キャル", 463);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 33, 0, "オイッスー☆", 0, 105801, "", 80005, 0, "ペコリーヌ", 464);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 11, 35, 0, "聞きなさいっての！！", 0, 106001, "", 80005, 1, "キャル", 465);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 31, 0, "3.5周年まであと３日…\nそして生放送は、明日…", 0, 106401, "", 80005, 2, "シェフィ", 466);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 32, 0, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 0, 106401, "", 80005, 2, "シェフィ", 467);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 33, 0, "おいっす！\n…こうじゃなかったわね\nええと確か…", 0, 106401, "", 80005, 2, "シェフィ", 468);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 34, 0, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 0, 106401, "", 80005, 2, "シェフィ", 469);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 35, 0, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 0, 106401, "", 80005, 2, "シェフィ", 470);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 34, 0, "やってますねシェフィちゃん！", 0, 105801, "", 80005, 3, "ペコリーヌ", 471);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 36, 0, "なによ、考えることは\nみんな同じってこと？", 0, 106001, "", 80005, 0, "キャル", 472);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 32, 0, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 0, 105901, "", 80005, 1, "コッコロ", 473);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 36, 0, "みんな…もちろんよ！", 0, 106401, "", 80005, 2, "シェフィ", 474);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 12, 35, 0, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 0, 105801, "", 80005, 3, "ペコリーヌ", 475);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 7, 0, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 0, 190801, "", 80005, 2, "カリン", 476);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 8, 0, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 0, 190801, "", 80005, 2, "カリン", 477);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 36, 0, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 0, 105801, "", 80005, 3, "ペコリーヌ", 478);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 9, 0, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 0, 190801, "", 80005, 2, "カリン", 479);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 33, 0, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 0, 105901, "", 80005, 4, "コッコロ", 480);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 10, 0, "さすが【美食殿】のママですね！\nそしてキャルさんは…", 0, 190801, "", 80005, 2, "カリン", 481);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 37, 0, "大丈夫…大丈夫…\nあたしは大丈夫…", 0, 106001, "", 80005, 1, "キャル", 482);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 11, 0, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 0, 190801, "", 80005, 2, "カリン", 483);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 37, 0, "あうー？", 0, 106401, "", 80005, 0, "シェフィ", 484);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 37, 1, "あ…", 0, 105801, "105901,34,106001,38,190801,12", 80005, 4, "みんな", 485);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 39, 0, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 0, 106001, "", 80005, 1, "キャル", 486);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 35, 0, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 0, 105901, "", 80005, 4, "コッコロ", 487);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 38, 0, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 0, 105801, "", 80005, 3, "ペコリーヌ", 488);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 13, 13, 0, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 0, 190801, "", 80005, 2, "カリン", 489);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 38, 0, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 0, 106401, "", 80005, 2, "シェフィ", 490);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 39, 0, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 0, 105801, "", 80005, 3, "ペコリーヌ", 491);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 40, 0, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 0, 106001, "", 80005, 0, "キャル", 492);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 36, 0, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 0, 105901, "", 80005, 1, "コッコロ", 493);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 39, 0, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 0, 106401, "", 80005, 2, "シェフィ", 494);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 40, 0, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 0, 105801, "", 80005, 3, "ペコリーヌ", 495);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 37, 0, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 0, 105901, "", 80005, 1, "コッコロ", 496);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 41, 0, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 0, 106001, "", 80005, 0, "キャル", 497);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 40, 0, "へ、へぇ…", 0, 106401, "", 80005, 2, "シェフィ", 498);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 41, 0, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 0, 105801, "", 80005, 3, "ペコリーヌ", 499);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 41, 0, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 0, 106401, "", 80005, 2, "シェフィ", 500);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 14, 42, 0, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 0, 106401, "", 80005, 2, "シェフィ", 501);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 42, 0, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 0, 105801, "", 80005, 3, "ペコリーヌ", 502);
