INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 43, 0, "おめでとうございます！", 0, 106401, "", 80005, 0, "シェフィ", 505);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 14, 0, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 0, 190801, "", 80005, 2, "カリン", 506);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 15, 0, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 0, 190801, "", 80005, 2, "カリン", 507);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 43, 0, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 0, 105801, "", 80005, 3, "ペコリーヌ", 508);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 44, 0, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 0, 105801, "", 80005, 3, "ペコリーヌ", 509);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 43, 0, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 0, 106001, "", 80005, 1, "キャル", 510);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 44, 0, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 0, 106001, "", 80005, 1, "キャル", 511);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 44, 0, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 0, 106401, "", 80005, 0, "シェフィ", 512);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 45, 0, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 0, 106401, "", 80005, 0, "シェフィ", 513);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 39, 0, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 0, 105901, "", 80005, 4, "コッコロ", 514);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 40, 0, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 0, 105901, "", 80005, 4, "コッコロ", 515);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 41, 0, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 0, 105901, "", 80005, 4, "コッコロ", 516);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 45, 0, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 0, 105801, "", 80005, 3, "ペコリーヌ", 517);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (0, 15, 46, 1, "これからも\nよろしくおねがいします！", 0, 105801, "105901,42,106001,45,106401,46,190801,16", 80005, 4, "みんな", 518);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 1, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 190804, "", 60003, 1, "カリン", 519);
