INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 0, 0, 0, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 1, 0, "", 60003, 2, "", 523);
