INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (1, 1, 0, "カリン", 60000, 1, "", 190804, 0, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (2, 0, 0, "", 60000, 1, "", 0, 0, 1, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (3, 0, 0, "", 60000, 1, "", 0, 0, 1, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (4, 0, 0, "", 60000, 2, "", 0, 0, 1, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (5, 0, 0, "", 60000, 1, "", 0, 0, 2, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (6, 0, 0, "", 60000, 1, "", 0, 0, 2, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (7, 0, 0, "", 60000, 2, "", 0, 0, 2, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (8, 0, 0, "", 60000, 1, "", 0, 0, 3, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (9, 0, 0, "", 60000, 1, "", 0, 0, 3, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (10, 0, 0, "", 60000, 1, "", 0, 0, 3, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (11, 0, 0, "", 60000, 1, "", 0, 0, 3, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (12, 0, 0, "", 60000, 2, "", 0, 0, 3, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (13, 0, 0, "", 60000, 1, "", 0, 0, 4, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (14, 0, 0, "", 60000, 1, "", 0, 0, 4, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (15, 0, 0, "", 60000, 1, "", 0, 0, 4, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (16, 0, 0, "", 60000, 1, "", 0, 0, 4, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (17, 0, 0, "", 60000, 2, "", 0, 0, 4, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (18, 2, 0, "カリン", 60000, 0, "", 190804, 0, 0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (19, 3, 7, "カリン", 60000, 0, "", 190804, 0, 0, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (20, 1, 1, "ハツネ", 80001, 0, "", 101201, 0, 0, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (21, 2, 1, "ハツネ", 80001, 0, "", 101201, 0, 0, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (22, 1, 1, "シオリ", 80001, 0, "", 103801, 0, 0, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (23, 2, 1, "シオリ", 80001, 0, "", 103801, 0, 0, "来年も、\n一緒にいられたら…\nうれしいな", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (24, 1, 2, "キョウカ", 80001, 0, "", 103601, 0, 0, "あなたと一緒にいるようになって、\nもう1年ですか…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (25, 2, 2, "キョウカ", 80001, 0, "", 103601, 0, 0, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (26, 1, 2, "ミミ", 80001, 0, "", 102001, 0, 0, "おにいちゃんとあってから、\n1年たったよ～！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (27, 2, 2, "ミミ", 80001, 0, "", 102001, 0, 0, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (28, 1, 2, "ミソギ", 80001, 0, "", 100401, 0, 0, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (29, 2, 2, "ミソギ", 80001, 0, "", 100401, 0, 0, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (30, 1, 3, "マホ", 80001, 0, "", 101001, 0, 0, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (31, 2, 3, "マホ", 80001, 0, "", 101001, 0, 0, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (32, 1, 3, "イリヤ", 80001, 0, "", 104401, 0, 0, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (33, 2, 3, "イリヤ", 80001, 0, "", 104401, 0, 0, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (34, 1, 3, "カオリ", 80001, 0, "", 101701, 0, 0, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (35, 2, 3, "カオリ", 80001, 0, "", 101701, 0, 0, "これからも、どうぞよろしく\nお願いするさ～！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (36, 1, 4, "サレン", 80001, 0, "", 102801, 0, 0, "あんたと出会ってからもうそんなに\nたつのね～", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (37, 2, 4, "サレン", 80001, 0, "", 102801, 0, 0, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (38, 1, 4, "スズメ", 80001, 0, "", 102501, 0, 0, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (39, 2, 4, "スズメ", 80001, 0, "", 102501, 0, 0, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (40, 1, 5, "タマキ", 80001, 0, "", 104601, 0, 0, "振り返れば、今までいろんなことが\nあったにゃあ…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (41, 2, 5, "タマキ", 80001, 0, "", 104601, 0, 0, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (42, 1, 5, "ミフユ", 80001, 0, "", 104801, 0, 0, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (43, 2, 5, "ミフユ", 80001, 0, "", 104801, 0, 0, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (44, 1, 6, "アキノ", 80001, 0, "", 103201, 0, 0, "あなたさまと出会ってもう1年…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (45, 2, 6, "アキノ", 80001, 0, "", 103201, 0, 0, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (46, 1, 6, "ユカリ", 80001, 0, "", 103401, 0, 0, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (47, 2, 6, "ユカリ", 80001, 0, "", 103401, 0, 0, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (48, 1, 7, "クリスティーナ", 80001, 0, "", 107101, 0, 0, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (49, 2, 7, "クリスティーナ", 80001, 0, "", 107101, 0, 0, "ともかくこれからも\n愉しませてくれよ☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (50, 1, 7, "ジュン", 80001, 0, "", 104701, 0, 0, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (51, 2, 7, "ジュン", 80001, 0, "", 104701, 0, 0, "見えないから分からないって？\nそれもそうだな…", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (52, 1, 8, "トモ", 80001, 0, "", 103701, 0, 0, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (53, 2, 8, "トモ", 80001, 0, "", 103701, 0, 0, "これからもよろしくね！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (54, 1, 8, "マツリ", 80001, 0, "", 100501, 0, 0, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (55, 2, 8, "マツリ", 80001, 0, "", 100501, 0, 0, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (56, 1, 9, "ミヤコ", 80001, 0, "", 100701, 0, 0, "オマエと会って1年なの！\n全然成仏もできなかったし、", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (57, 2, 9, "ミヤコ", 80001, 0, "", 100701, 0, 0, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (58, 1, 9, "シノブ", 80001, 0, "", 103101, 0, 0, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (59, 2, 9, "シノブ", 80001, 0, "", 103101, 0, 0, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (60, 1, 10, "アンナ", 80001, 0, "", 100901, 0, 0, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (61, 2, 10, "アンナ", 80001, 0, "", 100901, 0, 0, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (62, 1, 10, "ナナカ", 80001, 0, "", 101301, 0, 0, "君と出会ってもう1年だって！\n早いねー！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (63, 2, 10, "ナナカ", 80001, 0, "", 101301, 0, 0, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (64, 1, 11, "ミツキ", 80001, 0, "", 105101, 0, 0, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (65, 2, 11, "ミツキ", 80001, 0, "", 105101, 0, 0, "これからも、いろんなところを\n調べさせてね？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (66, 1, 11, "ルカ", 80001, 0, "", 105601, 0, 0, "お前さんと出会ってもう1年か\n助太刀するつもりが", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (67, 2, 11, "ルカ", 80001, 0, "", 105601, 0, 0, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (68, 1, 11, "エリコ", 80001, 0, "", 102701, 0, 0, "早いものですわね、あなた様と\nお会いして、もう1年…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (69, 2, 11, "エリコ", 80001, 0, "", 102701, 0, 0, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (70, 1, 12, "アヤネ", 80001, 0, "", 102301, 0, 0, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (71, 2, 12, "アヤネ", 80001, 0, "", 102301, 0, 0, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (72, 1, 12, "チカ", 80001, 0, "", 104201, 0, 0, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (73, 2, 12, "チカ", 80001, 0, "", 104201, 0, 0, "私のことを見守ってください\nできることなら、ずっと…", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (74, 1, 12, "クルミ", 80001, 0, "", 102101, 0, 0, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (75, 2, 12, "クルミ", 80001, 0, "", 102101, 0, 0, "で、でもこれからもずっと…\n一緒にいたいなぁ…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (76, 1, 13, "シズル", 80001, 0, "", 104901, 0, 0, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (77, 2, 13, "シズル", 80001, 0, "", 104901, 0, 0, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (78, 1, 13, "リノ", 80001, 0, "", 101101, 0, 0, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (79, 2, 13, "リノ", 80001, 0, "", 101101, 0, 0, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (80, 1, 14, "ユイ", 80001, 0, "", 100201, 0, 0, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (81, 2, 14, "ユイ", 80001, 0, "", 100201, 0, 0, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (82, 1, 14, "ヒヨリ", 80001, 0, "", 100101, 0, 0, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (83, 2, 14, "ヒヨリ", 80001, 0, "", 100101, 0, 0, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (84, 1, 14, "レイ", 80001, 0, "", 100301, 0, 0, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (85, 2, 14, "レイ", 80001, 0, "", 100301, 0, 0, "キミに背中を預けられる日が、\n早くきますように", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (86, 1, 15, "ペコリーヌ", 80001, 0, "", 105801, 0, 0, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (87, 2, 15, "ペコリーヌ", 80001, 0, "", 105801, 0, 0, "これからもずっと、覚えていて\nくださいね！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (88, 1, 15, "コッコロ", 80001, 0, "", 105901, 0, 0, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (89, 2, 15, "コッコロ", 80001, 0, "", 105901, 0, 0, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (90, 1, 15, "キャル", 80001, 0, "", 106001, 0, 0, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (91, 2, 15, "キャル", 80001, 0, "", 106001, 0, 0, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (92, 1, 0, "スズメ", 60001, 1, "", 112101, 0, 0, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (93, 0, 0, "", 60001, 1, "", 0, 0, 1, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (94, 0, 0, "", 60001, 1, "", 0, 0, 1, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (95, 0, 0, "", 60001, 1, "", 0, 0, 1, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (96, 0, 0, "", 60001, 1, "", 0, 0, 1, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (97, 0, 0, "", 60001, 2, "", 0, 0, 1, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (98, 0, 0, "", 60001, 1, "", 0, 0, 2, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (99, 0, 0, "", 60001, 1, "", 0, 0, 2, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (100, 0, 0, "", 60001, 1, "", 0, 0, 2, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (101, 0, 0, "", 60001, 1, "", 0, 0, 2, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (102, 0, 0, "", 60001, 2, "", 0, 0, 2, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (103, 0, 0, "", 60001, 1, "", 0, 0, 3, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (104, 0, 0, "", 60001, 1, "", 0, 0, 3, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (105, 0, 0, "", 60001, 1, "", 0, 0, 3, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (106, 0, 0, "", 60001, 1, "", 0, 0, 3, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (107, 0, 0, "", 60001, 1, "", 0, 0, 3, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (108, 0, 0, "", 60001, 1, "", 0, 0, 3, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (109, 0, 0, "", 60001, 2, "", 0, 0, 3, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (110, 0, 0, "", 60001, 1, "", 0, 0, 4, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (111, 0, 0, "", 60001, 1, "", 0, 0, 4, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (112, 0, 0, "", 60001, 1, "", 0, 0, 4, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (113, 0, 0, "", 60001, 1, "", 0, 0, 4, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (114, 0, 0, "", 60001, 1, "", 0, 0, 4, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (115, 0, 0, "", 60001, 1, "", 0, 0, 4, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (116, 0, 0, "", 60001, 2, "", 0, 0, 4, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (117, 2, 0, "スズメ", 60001, 0, "", 112101, 0, 0, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (118, 3, 7, "スズメ", 60001, 0, "", 112101, 0, 0, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (119, 1, 1, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "ヒューヒュー♪\nドンドンパフパフー♪", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (120, 1, 1, "コッコロ", 80003, 0, "", 105901, 0, 0, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (121, 2, 1, "コッコロ", 80003, 0, "", 105901, 0, 0, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (122, 1, 1, "キャル", 80003, 0, "", 106001, 0, 0, "え？　ちょっと\nいきなりなにっ？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (123, 2, 1, "キャル", 80003, 0, "", 106001, 0, 0, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (124, 2, 1, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (125, 3, 1, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (126, 3, 1, "コッコロ", 80003, 0, "", 105901, 0, 0, "ですからその日に向けて\n今から気分を盛り上げているのです", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (127, 3, 1, "キャル", 80003, 0, "", 106001, 0, 0, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (128, 4, 1, "コッコロ", 80003, 0, "", 105901, 0, 0, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (129, 4, 1, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (130, 4, 1, "キャル", 80003, 0, "", 106001, 0, 0, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (131, 5, 1, "コッコロ", 80003, 0, "", 105901, 0, 0, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (132, 5, 2, "キャル", 80003, 0, "", 106001, 0, 0, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (133, 1, 2, "カリン", 80003, 0, "", 190801, 0, 0, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (134, 6, 2, "キャル", 80003, 0, "", 106001, 0, 0, "げっ…カリンじゃない\nなんでもないわよ！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (135, 2, 2, "カリン", 80003, 0, "", 190801, 0, 0, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (136, 7, 2, "キャル", 80003, 0, "", 106001, 0, 0, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (137, 3, 2, "カリン", 80003, 0, "", 190801, 0, 0, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (138, 8, 2, "キャル", 80003, 0, "", 106001, 0, 0, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (139, 9, 2, "キャル", 80003, 0, "", 106001, 0, 0, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (140, 4, 2, "カリン", 80003, 0, "", 190801, 0, 0, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (141, 10, 2, "キャル", 80003, 0, "", 106001, 0, 0, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (142, 5, 2, "カリン", 80003, 0, "", 190801, 0, 0, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (143, 11, 2, "キャル", 80003, 0, "", 106001, 0, 0, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (144, 6, 3, "コッコロ", 80003, 0, "", 105901, 0, 0, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (145, 5, 3, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (146, 7, 3, "コッコロ", 80003, 0, "", 105901, 0, 0, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (147, 8, 3, "コッコロ", 80003, 0, "", 105901, 0, 0, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (148, 6, 3, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (149, 7, 3, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (150, 9, 3, "コッコロ", 80003, 0, "", 105901, 0, 0, "あとは、牧場に素敵な温泉が\n出来たとか…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (151, 8, 3, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (152, 10, 3, "コッコロ", 80003, 0, "", 105901, 0, 0, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (153, 9, 3, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "えへへ、ありがとうございます\nコッコロちゃん！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (154, 11, 3, "コッコロ", 80003, 0, "", 105901, 0, 0, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (155, 10, 3, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (156, 11, 4, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (157, 12, 4, "キャル", 80003, 0, "", 106001, 0, 0, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (158, 12, 4, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (159, 13, 4, "キャル", 80003, 0, "", 106001, 0, 0, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (160, 13, 4, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (161, 14, 4, "キャル", 80003, 0, "", 106001, 0, 0, "…積極的？\nなんの話よ？", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (162, 14, 4, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (163, 15, 4, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "ムーサの町で\nきゃるきゃる～ん☆　って！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (164, 15, 4, "キャル", 80003, 0, "", 106001, 0, 0, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (165, 16, 4, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (166, 16, 4, "キャル", 80003, 0, "", 106001, 0, 0, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (167, 17, 4, "キャル", 80003, 0, "", 106001, 0, 0, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (168, 18, 5, "キャル", 80003, 0, "", 106001, 0, 0, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (169, 12, 5, "コッコロ", 80003, 0, "", 105901, 0, 0, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (170, 19, 5, "キャル", 80003, 0, "", 106001, 0, 0, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (171, 13, 5, "コッコロ", 80003, 0, "", 105901, 0, 0, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (172, 20, 5, "キャル", 80003, 0, "", 106001, 0, 0, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (173, 14, 5, "コッコロ", 80003, 0, "", 105901, 0, 0, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (174, 21, 5, "キャル", 80003, 0, "", 106001, 0, 0, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (175, 15, 5, "コッコロ", 80003, 0, "", 105901, 0, 0, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (176, 22, 5, "キャル", 80003, 0, "", 106001, 0, 0, "かくし芸？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (177, 16, 5, "コッコロ", 80003, 0, "", 105901, 0, 0, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (178, 23, 5, "キャル", 80003, 0, "", 106001, 0, 0, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (179, 17, 6, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (180, 24, 6, "キャル", 80003, 0, "", 106001, 0, 0, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (181, 17, 6, "コッコロ", 80003, 0, "", 105901, 0, 0, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (182, 18, 6, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (183, 25, 6, "キャル", 80003, 0, "", 106001, 0, 0, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (184, 18, 6, "コッコロ", 80003, 0, "", 105901, 0, 0, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (185, 26, 6, "キャル", 80003, 0, "", 106001, 0, 0, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (186, 27, 6, "キャル", 80003, 0, "", 106001, 0, 0, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (187, 28, 6, "キャル", 80003, 0, "", 106001, 0, 0, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (188, 19, 6, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (189, 19, 6, "コッコロ", 80003, 0, "", 105901, 0, 0, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (190, 29, 6, "キャル", 80003, 0, "", 106001, 0, 0, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (191, 30, 7, "キャル", 80003, 0, "", 106001, 0, 0, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (192, 31, 7, "キャル", 80003, 0, "", 106001, 0, 0, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (193, 32, 7, "キャル", 80003, 0, "", 106001, 0, 0, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (194, 33, 7, "キャル", 80003, 0, "", 106001, 0, 0, "あー\nあー\n…声、これで大丈夫かしら？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (195, 34, 7, "キャル", 80003, 0, "", 106001, 0, 0, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (196, 35, 7, "キャル", 80003, 0, "", 106001, 0, 0, "オイッスー☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (197, 36, 7, "キャル", 80003, 0, "", 106001, 0, 0, "……\nいや、ないわ\nいつもどおりが一番ね、うん", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (198, 37, 7, "キャル", 80003, 0, "", 106001, 0, 0, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (199, 38, 7, "キャル", 80003, 0, "", 106001, 0, 0, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (200, 20, 8, "コッコロ", 80003, 0, "", 105901, 0, 0, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (201, 21, 8, "コッコロ", 80003, 0, "", 105901, 0, 0, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (202, 22, 8, "コッコロ", 80003, 0, "", 105901, 0, 0, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (203, 23, 8, "コッコロ", 80003, 0, "", 105901, 0, 0, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (204, 24, 8, "コッコロ", 80003, 0, "", 105901, 0, 0, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (205, 25, 8, "コッコロ", 80003, 0, "", 105901, 0, 0, "オイッスー☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (206, 26, 8, "コッコロ", 80003, 0, "", 105901, 0, 0, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (207, 27, 8, "コッコロ", 80003, 0, "", 105901, 0, 0, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (208, 28, 8, "コッコロ", 80003, 0, "", 105901, 0, 0, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (209, 20, 9, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "オイッスー☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (210, 21, 9, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (211, 22, 9, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "あと６日で2.5周年ですか…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (212, 23, 9, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (213, 24, 9, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (214, 25, 9, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "オイッスー☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (215, 26, 9, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (216, 27, 9, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (217, 28, 9, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (218, 29, 9, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (219, 30, 9, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (220, 31, 10, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (221, 29, 10, "コッコロ", 80003, 0, "", 105901, 0, 0, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (222, 32, 10, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (223, 39, 10, "キャル", 80003, 0, "", 106001, 0, 0, "…………", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (224, 30, 10, "コッコロ", 80003, 0, "", 105901, 0, 0, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (225, 40, 10, "キャル", 80003, 0, "", 106001, 0, 0, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (226, 33, 10, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (227, 41, 10, "キャル", 80003, 0, "", 106001, 0, 0, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (228, 34, 10, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (229, 35, 10, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (230, 42, 10, "キャル", 80003, 0, "", 106001, 0, 0, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (231, 31, 10, "コッコロ", 80003, 0, "", 105901, 0, 0, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (232, 43, 10, "キャル", 80003, 0, "", 106001, 0, 0, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (233, 36, 10, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (234, 6, 11, "カリン", 80003, 0, "", 190801, 0, 0, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (235, 7, 11, "カリン", 80003, 0, "", 190801, 0, 0, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (236, 8, 11, "カリン", 80003, 0, "", 190801, 0, 0, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (237, 9, 11, "カリン", 80003, 0, "", 190801, 0, 0, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (238, 10, 11, "カリン", 80003, 0, "", 190801, 0, 0, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (239, 11, 11, "カリン", 80003, 0, "", 190801, 0, 0, "次にコッコロさんですが\nこちらは発声練習をしています", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (240, 12, 11, "カリン", 80003, 0, "", 190801, 0, 0, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (241, 13, 11, "カリン", 80003, 0, "", 190801, 0, 0, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (242, 14, 11, "カリン", 80003, 0, "", 190801, 0, 0, "あれ？\nもしかして緊張で気を失って…？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (243, 15, 11, "カリン", 80003, 0, "", 190801, 0, 0, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (244, 16, 11, "カリン", 80003, 0, "", 190801, 0, 0, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (245, 17, 11, "カリン", 80003, 0, "", 190801, 0, 0, "それでは\nまた明日お会いしましょう♪", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (246, 44, 12, "キャル", 80003, 0, "", 106001, 0, 0, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (247, 37, 12, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (248, 32, 12, "コッコロ", 80003, 0, "", 105901, 0, 0, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (249, 45, 12, "キャル", 80003, 0, "", 106001, 0, 0, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (250, 38, 12, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "ひどいですよぉキャルちゃ～ん！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (251, 33, 12, "コッコロ", 80003, 0, "", 105901, 0, 0, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (252, 39, 12, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (253, 46, 12, "キャル", 80003, 0, "", 106001, 0, 0, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (254, 34, 12, "コッコロ", 80003, 0, "", 105901, 0, 0, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (255, 40, 12, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (256, 41, 13, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (257, 47, 13, "キャル", 80003, 0, "", 106001, 0, 0, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (258, 42, 13, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "忘れてきちゃいました☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (259, 48, 13, "キャル", 80003, 0, "", 106001, 0, 0, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (260, 35, 13, "コッコロ", 80003, 0, "", 105901, 0, 0, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (261, 49, 13, "キャル", 80003, 0, "", 106001, 0, 0, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (262, 43, 13, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (263, 50, 13, "キャル", 80003, 0, "", 106001, 0, 0, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (264, 36, 13, "コッコロ", 80003, 0, "", 105901, 0, 0, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (265, 51, 13, "キャル", 80003, 0, "", 106001, 0, 0, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (266, 37, 13, "コッコロ", 80003, 0, "", 105901, 0, 0, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (267, 52, 13, "キャル", 80003, 0, "", 106001, 0, 0, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (268, 44, 13, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (269, 38, 13, "コッコロ", 80003, 0, "", 105901, 0, 0, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (270, 45, 13, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (271, 53, 13, "キャル", 80003, 0, "", 106001, 0, 0, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (272, 46, 13, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (273, 18, 14, "カリン", 80003, 0, "", 190801, 0, 0, "こんにちは\n【ギルド管理協会】のカリンです", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (274, 19, 14, "カリン", 80003, 0, "", 190801, 0, 0, "昨日の生放送は\nいかがだったでしょうか？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (275, 20, 14, "カリン", 80003, 0, "", 190801, 0, 0, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (276, 21, 14, "カリン", 80003, 0, "", 190801, 0, 0, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (277, 22, 14, "カリン", 80003, 0, "", 190801, 0, 0, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (278, 23, 14, "カリン", 80003, 0, "", 190801, 0, 0, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (279, 24, 14, "カリン", 80003, 0, "", 190801, 0, 0, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (280, 25, 14, "カリン", 80003, 0, "", 190801, 0, 0, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (281, 26, 14, "カリン", 80003, 0, "", 190801, 0, 0, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (282, 27, 14, "カリン", 80003, 0, "", 190801, 0, 0, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (283, 47, 15, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "準備はいいですかっ？\n…いっきますよー！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (284, 54, 15, "キャル", 80003, 0, "", 106001, 0, 0, "祝！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (285, 39, 15, "コッコロ", 80003, 0, "", 105901, 0, 0, "2.5周年！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (286, 48, 15, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "おめでとうございま～す☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (287, 55, 15, "キャル", 80003, 0, "", 106001, 0, 0, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (288, 40, 15, "コッコロ", 80003, 0, "", 105901, 0, 0, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (289, 49, 15, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (290, 56, 15, "キャル", 80003, 0, "", 106001, 0, 0, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (291, 41, 15, "コッコロ", 80003, 0, "", 105901, 0, 0, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (292, 57, 15, "キャル", 80003, 0, "", 106001, 0, 0, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (293, 50, 15, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (294, 58, 15, "キャル", 80003, 0, "", 106001, 0, 0, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (295, 51, 15, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "記念日だから\n平気平気♪\nやばいですね☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (296, 59, 15, "キャル", 80003, 0, "", 106001, 0, 0, "意味分かんないわよ！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (297, 42, 15, "コッコロ", 80003, 0, "", 105901, 0, 0, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (298, 43, 15, "コッコロ", 80003, 0, "", 105901, 0, 0, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (299, 52, 15, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (300, 60, 15, "キャル", 80003, 0, "", 106001, 0, 0, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (301, 44, 15, "コッコロ", 80003, 0, "", 105901, 0, 0, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (302, 53, 15, "ペコリーヌ", 80003, 0, "", 105801, 0, 0, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (303, 54, 15, "ペコリーヌ・コッコロ・キャル", 80003, 0, "105901,45,106001,61", 105801, 1, 0, "2.5周年\nありがとうございます！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (304, 1, 0, "カリン", 60002, 1, "", 190804, 0, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (305, 0, 0, "", 60002, 1, "", 0, 0, 1, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (306, 0, 0, "", 60002, 1, "", 0, 0, 1, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (307, 0, 0, "", 60002, 1, "", 0, 0, 1, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (308, 0, 0, "", 60002, 1, "", 0, 0, 1, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (309, 0, 0, "", 60002, 2, "", 0, 0, 1, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (310, 0, 0, "", 60002, 1, "", 0, 0, 2, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (311, 0, 0, "", 60002, 1, "", 0, 0, 2, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (312, 0, 0, "", 60002, 1, "", 0, 0, 2, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (313, 0, 0, "", 60002, 1, "", 0, 0, 2, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (314, 0, 0, "", 60002, 2, "", 0, 0, 2, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (315, 0, 0, "", 60002, 1, "", 0, 0, 3, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (316, 0, 0, "", 60002, 1, "", 0, 0, 3, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (317, 0, 0, "", 60002, 1, "", 0, 0, 3, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (318, 0, 0, "", 60002, 1, "", 0, 0, 3, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (319, 0, 0, "", 60002, 1, "", 0, 0, 3, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (320, 0, 0, "", 60002, 1, "", 0, 0, 3, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (321, 0, 0, "", 60002, 2, "", 0, 0, 3, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (322, 0, 0, "", 60002, 1, "", 0, 0, 4, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (323, 0, 0, "", 60002, 1, "", 0, 0, 4, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (324, 0, 0, "", 60002, 1, "", 0, 0, 4, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (325, 0, 0, "", 60002, 1, "", 0, 0, 4, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (326, 0, 0, "", 60002, 1, "", 0, 0, 4, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (327, 0, 0, "", 60002, 1, "", 0, 0, 4, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (328, 0, 0, "", 60002, 2, "", 0, 0, 4, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (329, 2, 0, "カリン", 60002, 0, "", 190804, 0, 0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (330, 3, 7, "カリン", 60002, 0, "", 190804, 0, 0, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (331, 1, 1, "コッコロ", 80005, 0, "", 105901, 0, 0, "ささ、シェフィさまはこちらへ…", 4);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (332, 1, 1, "シェフィ", 80005, 0, "", 106401, 0, 0, "コッコロさん、どこへ行くの…？\n…え？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (333, 1, 1, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (334, 2, 1, "コッコロ", 80005, 0, "", 105901, 0, 0, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 4);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (335, 2, 1, "シェフィ", 80005, 0, "", 106401, 0, 0, "ええと…\n二人とも何をしているの？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (336, 1, 1, "キャル", 80005, 0, "", 106001, 0, 0, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (337, 3, 1, "シェフィ", 80005, 0, "", 106401, 0, 0, "あの日…？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (338, 2, 1, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (339, 3, 1, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "『プリンセスコネクト！\nRe:Dive』3.5周年！", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (340, 1, 1, "カリン", 80005, 0, "", 190801, 0, 0, "やばいですね☆", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (341, 2, 1, "キャル", 80005, 0, "", 106001, 0, 0, "って、なにしれっと\n参加してんのよカリン！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (342, 2, 1, "カリン", 80005, 0, "", 190801, 0, 0, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (343, 3, 1, "カリン", 80005, 0, "", 190801, 0, 0, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (344, 4, 1, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (345, 4, 1, "シェフィ", 80005, 0, "", 106401, 0, 0, "えっ！？　ええとええと…！\nあ、あと14日…！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (346, 5, 2, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (347, 3, 2, "コッコロ", 80005, 0, "", 105901, 0, 0, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (348, 4, 2, "コッコロ", 80005, 0, "", 105901, 0, 0, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (349, 6, 2, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (350, 5, 2, "コッコロ", 80005, 0, "", 105901, 0, 0, "さすがでございます\nペコリーヌさま", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (351, 3, 2, "キャル", 80005, 0, "", 106001, 0, 0, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (352, 5, 2, "シェフィ", 80005, 0, "", 106401, 0, 0, "しゃなり、しゃなり…\nふふ、案外できるものね", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (353, 7, 2, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (354, 6, 2, "コッコロ", 80005, 0, "", 105901, 0, 0, "さ、次はキャルさまの\n番でございますよ", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (355, 4, 2, "キャル", 80005, 0, "", 106001, 0, 0, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (356, 6, 2, "シェフィ", 80005, 0, "", 106401, 0, 0, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (357, 5, 2, "キャル", 80005, 0, "", 106001, 0, 0, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (358, 6, 2, "キャル", 80005, 0, "", 106001, 0, 0, "いたたた…もぉお～！\nなんであたしがこんな目に～！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (359, 7, 2, "コッコロ", 80005, 0, "", 105901, 0, 0, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (360, 7, 2, "キャル", 80005, 0, "", 106001, 0, 0, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (361, 8, 3, "コッコロ", 80005, 0, "", 105901, 0, 0, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (362, 8, 3, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (363, 9, 3, "コッコロ", 80005, 0, "", 105901, 0, 0, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (364, 7, 3, "シェフィ", 80005, 0, "", 106401, 0, 0, "聖学祭…って、なにかしら？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (365, 10, 3, "コッコロ", 80005, 0, "", 105901, 0, 0, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (366, 8, 3, "キャル", 80005, 0, "", 106001, 0, 0, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (367, 9, 3, "キャル", 80005, 0, "", 106001, 0, 0, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (368, 9, 3, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (369, 10, 3, "キャル", 80005, 0, "", 106001, 0, 0, "いや、そのまんますぎでしょ…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (370, 8, 3, "シェフィ", 80005, 0, "", 106401, 0, 0, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (371, 10, 3, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (372, 9, 3, "シェフィ", 80005, 0, "", 106401, 0, 0, "えっ？　ええ…\nぎゅーっ…？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (373, 11, 4, "キャル", 80005, 0, "", 106001, 0, 0, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (374, 10, 4, "シェフィ", 80005, 0, "", 106401, 0, 0, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (375, 12, 4, "キャル", 80005, 0, "", 106001, 0, 0, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (376, 11, 4, "コッコロ", 80005, 0, "", 105901, 0, 0, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (377, 13, 4, "キャル", 80005, 0, "", 106001, 0, 0, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (378, 11, 4, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (379, 14, 4, "キャル", 80005, 0, "", 106001, 0, 0, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (380, 15, 4, "キャル", 80005, 0, "", 106001, 0, 0, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (381, 11, 4, "シェフィ", 80005, 0, "", 106401, 0, 0, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (382, 12, 4, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (383, 12, 4, "コッコロ", 80005, 0, "", 105901, 0, 0, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (384, 16, 4, "キャル", 80005, 0, "", 106001, 0, 0, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (385, 12, 4, "シェフィ", 80005, 0, "", 106401, 0, 0, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (386, 13, 5, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (387, 14, 5, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (388, 15, 5, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (389, 17, 5, "キャル", 80005, 0, "", 106001, 0, 0, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (390, 18, 5, "キャル", 80005, 0, "", 106001, 0, 0, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (391, 13, 5, "シェフィ", 80005, 0, "", 106401, 0, 0, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (392, 13, 5, "コッコロ", 80005, 0, "", 105901, 0, 0, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (393, 16, 5, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (394, 19, 5, "キャル", 80005, 0, "", 106001, 0, 0, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (395, 17, 5, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (396, 18, 5, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (397, 19, 5, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (398, 4, 6, "カリン", 80005, 0, "", 190801, 0, 0, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (399, 5, 6, "カリン", 80005, 0, "", 190801, 0, 0, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (400, 20, 6, "キャル", 80005, 0, "", 106001, 0, 0, "…えっ！？", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (401, 20, 6, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (402, 14, 6, "シェフィ", 80005, 0, "", 106401, 0, 0, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (403, 14, 6, "コッコロ", 80005, 0, "", 105901, 0, 0, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 4);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (404, 15, 6, "シェフィ", 80005, 0, "", 106401, 0, 0, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (405, 21, 6, "キャル", 80005, 0, "", 106001, 0, 0, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (406, 21, 6, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (407, 22, 6, "キャル", 80005, 0, "", 106001, 0, 0, "ぶっ殺すぞ！？", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (408, 6, 6, "カリン", 80005, 0, "", 190801, 0, 0, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (409, 15, 7, "コッコロ", 80005, 0, "", 105901, 0, 0, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (410, 23, 7, "キャル", 80005, 0, "", 106001, 0, 0, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (411, 16, 7, "シェフィ", 80005, 0, "", 106401, 0, 0, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (412, 22, 7, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (413, 24, 7, "キャル", 80005, 0, "", 106001, 0, 0, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (414, 17, 7, "シェフィ", 80005, 0, "", 106401, 0, 0, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (415, 16, 7, "コッコロ", 80005, 0, "", 105901, 0, 0, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (416, 18, 7, "シェフィ", 80005, 0, "", 106401, 0, 0, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (417, 17, 7, "コッコロ", 80005, 0, "", 105901, 0, 0, "もちろんでございます\n仲良くお勉強いたしましょう", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (418, 25, 7, "キャル", 80005, 0, "", 106001, 0, 0, "しれっと教育ママが\n生まれてる…？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (419, 23, 7, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (420, 26, 7, "キャル", 80005, 0, "", 106001, 0, 0, "え～～～～！？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (421, 27, 8, "キャル", 80005, 0, "", 106001, 0, 0, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (422, 19, 8, "シェフィ", 80005, 0, "", 106401, 0, 0, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (423, 18, 8, "コッコロ", 80005, 0, "", 105901, 0, 0, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (424, 24, 8, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (425, 20, 8, "シェフィ", 80005, 0, "", 106401, 0, 0, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (426, 28, 8, "キャル", 80005, 0, "", 106001, 0, 0, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (427, 19, 8, "コッコロ", 80005, 0, "", 105901, 0, 0, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (428, 21, 8, "シェフィ", 80005, 0, "", 106401, 0, 0, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (429, 29, 8, "キャル", 80005, 0, "", 106001, 0, 0, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (430, 30, 8, "キャル", 80005, 0, "", 106001, 0, 0, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (431, 25, 8, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (432, 31, 8, "キャル", 80005, 0, "", 106001, 0, 0, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (433, 22, 9, "シェフィ", 80005, 0, "", 106401, 0, 0, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (434, 23, 9, "シェフィ", 80005, 0, "", 106401, 0, 0, "…えっ？\n生放送の意気込み！？\n突然なに！？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (435, 24, 9, "シェフィ", 80005, 0, "", 106401, 0, 0, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (436, 25, 9, "シェフィ", 80005, 0, "", 106401, 0, 0, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (437, 26, 9, "シェフィ", 80005, 0, "", 106401, 0, 0, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (438, 27, 9, "シェフィ", 80005, 0, "", 106401, 0, 0, "それから、それから…！\nぅ、ぅぅっ…！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (439, 28, 9, "シェフィ", 80005, 0, "", 106401, 0, 0, "―びえぇぇ～んっ！！！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (440, 20, 9, "コッコロ", 80005, 0, "", 105901, 0, 0, "どうされましたか、シェフィさま", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (441, 29, 9, "シェフィ", 80005, 0, "", 106401, 0, 0, "ううっ…ひっく…\nママッ…！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (442, 21, 9, "コッコロ", 80005, 0, "", 105901, 0, 0, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (443, 22, 9, "コッコロ", 80005, 0, "", 105901, 0, 0, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (444, 30, 9, "シェフィ", 80005, 0, "", 106401, 0, 0, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (445, 23, 10, "コッコロ", 80005, 0, "", 105901, 0, 0, "主さま\n3.5周年まであと５日\nでございます", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (446, 24, 10, "コッコロ", 80005, 0, "", 105901, 0, 0, "ですがその前に\n生放送がありますね", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (447, 25, 10, "コッコロ", 80005, 0, "", 105901, 0, 0, "…生放送に向けた\n意気込みでございますか？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (448, 26, 10, "コッコロ", 80005, 0, "", 105901, 0, 0, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (449, 27, 10, "コッコロ", 80005, 0, "", 105901, 0, 0, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (450, 28, 10, "コッコロ", 80005, 0, "", 105901, 0, 0, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (451, 29, 10, "コッコロ", 80005, 0, "", 105901, 0, 0, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (452, 30, 10, "コッコロ", 80005, 0, "", 105901, 0, 0, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (453, 31, 10, "コッコロ", 80005, 0, "", 105901, 0, 0, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (454, 26, 11, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "3.5周年まであと４日！\nやばいですね☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (455, 32, 11, "キャル", 80005, 0, "", 106001, 0, 0, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (456, 27, 11, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (457, 28, 11, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (458, 29, 11, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (459, 33, 11, "キャル", 80005, 0, "", 106001, 0, 0, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (460, 30, 11, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (461, 31, 11, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (462, 32, 11, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (463, 34, 11, "キャル", 80005, 0, "", 106001, 0, 0, "だからあたしはやるなんて一言も…", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (464, 33, 11, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "オイッスー☆", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (465, 35, 11, "キャル", 80005, 0, "", 106001, 0, 0, "聞きなさいっての！！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (466, 31, 12, "シェフィ", 80005, 0, "", 106401, 0, 0, "3.5周年まであと３日…\nそして生放送は、明日…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (467, 32, 12, "シェフィ", 80005, 0, "", 106401, 0, 0, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (468, 33, 12, "シェフィ", 80005, 0, "", 106401, 0, 0, "おいっす！\n…こうじゃなかったわね\nええと確か…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (469, 34, 12, "シェフィ", 80005, 0, "", 106401, 0, 0, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (470, 35, 12, "シェフィ", 80005, 0, "", 106401, 0, 0, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (471, 34, 12, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "やってますねシェフィちゃん！", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (472, 36, 12, "キャル", 80005, 0, "", 106001, 0, 0, "なによ、考えることは\nみんな同じってこと？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (473, 32, 12, "コッコロ", 80005, 0, "", 105901, 0, 0, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (474, 36, 12, "シェフィ", 80005, 0, "", 106401, 0, 0, "みんな…もちろんよ！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (475, 35, 12, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (476, 7, 13, "カリン", 80005, 0, "", 190801, 0, 0, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (477, 8, 13, "カリン", 80005, 0, "", 190801, 0, 0, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (478, 36, 13, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (479, 9, 13, "カリン", 80005, 0, "", 190801, 0, 0, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (480, 33, 13, "コッコロ", 80005, 0, "", 105901, 0, 0, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 4);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (481, 10, 13, "カリン", 80005, 0, "", 190801, 0, 0, "さすが【美食殿】のママですね！\nそしてキャルさんは…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (482, 37, 13, "キャル", 80005, 0, "", 106001, 0, 0, "大丈夫…大丈夫…\nあたしは大丈夫…", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (483, 11, 13, "カリン", 80005, 0, "", 190801, 0, 0, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (484, 37, 13, "シェフィ", 80005, 0, "", 106401, 0, 0, "あうー？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (485, 37, 13, "みんな", 80005, 0, "105901,34,106001,38,190801,12", 105801, 1, 0, "あ…", 4);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (486, 39, 13, "キャル", 80005, 0, "", 106001, 0, 0, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (487, 35, 13, "コッコロ", 80005, 0, "", 105901, 0, 0, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 4);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (488, 38, 13, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (489, 13, 13, "カリン", 80005, 0, "", 190801, 0, 0, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (490, 38, 14, "シェフィ", 80005, 0, "", 106401, 0, 0, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (491, 39, 14, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (492, 40, 14, "キャル", 80005, 0, "", 106001, 0, 0, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (493, 36, 14, "コッコロ", 80005, 0, "", 105901, 0, 0, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (494, 39, 14, "シェフィ", 80005, 0, "", 106401, 0, 0, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (495, 40, 14, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (496, 37, 14, "コッコロ", 80005, 0, "", 105901, 0, 0, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (497, 41, 14, "キャル", 80005, 0, "", 106001, 0, 0, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 0);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (498, 40, 14, "シェフィ", 80005, 0, "", 106401, 0, 0, "へ、へぇ…", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (499, 41, 14, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 3);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (500, 41, 14, "シェフィ", 80005, 0, "", 106401, 0, 0, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (501, 42, 14, "シェフィ", 80005, 0, "", 106401, 0, 0, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 2);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (502, 42, 15, "ペコリーヌ", 80005, 0, "", 105801, 0, 0, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 3);
