INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (510, 43, 15, "キャル", 80005, 0, "", 106001, 0, 0, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 1);
INSERT INTO `v1_4f5c76c90ca443238977d91e21d8e94d09a7af13f6d3ed3a77e7434e0a4e1e66` VALUES (511, 44, 15, "キャル", 80005, 0, "", 106001, 0, 0, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 1);
