INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 1, 190804, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 1, 1, 0, 1, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0, 2, 0, 0, 1, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 3, 0, 0, 1, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0, 4, 0, 0, 2, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0, 5, 0, 0, 1, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0, 6, 0, 0, 1, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0, 7, 0, 0, 2, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0, 8, 0, 0, 1, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0, 9, 0, 0, 1, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 10, 0, 0, 1, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 11, 0, 0, 1, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0, 12, 0, 0, 2, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0, 13, 0, 0, 1, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0, 14, 0, 0, 1, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0, 15, 0, 0, 1, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 16, 0, 0, 1, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 17, 0, 0, 2, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 3, 190804, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 18, 2, 0, 0, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 3, 190804, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 19, 3, 7, 0, 60000);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ハツネ", 0, "", 0, 101201, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 0, 20, 1, 1, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ハツネ", 0, "", 0, 101201, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 0, 21, 2, 1, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シオリ", 0, "", 1, 103801, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 0, 22, 1, 1, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シオリ", 0, "", 1, 103801, "来年も、\n一緒にいられたら…\nうれしいな", 0, 23, 2, 1, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キョウカ", 0, "", 0, 103601, "あなたと一緒にいるようになって、\nもう1年ですか…", 0, 24, 1, 2, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キョウカ", 0, "", 0, 103601, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 0, 25, 2, 2, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ミミ", 0, "", 1, 102001, "おにいちゃんとあってから、\n1年たったよ～！", 0, 26, 1, 2, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ミミ", 0, "", 1, 102001, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 0, 27, 2, 2, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ミソギ", 0, "", 2, 100401, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 0, 28, 1, 2, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ミソギ", 0, "", 2, 100401, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 0, 29, 2, 2, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("マホ", 0, "", 0, 101001, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 0, 30, 1, 3, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("マホ", 0, "", 0, 101001, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 0, 31, 2, 3, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("イリヤ", 0, "", 1, 104401, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 0, 32, 1, 3, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("イリヤ", 0, "", 1, 104401, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 0, 33, 2, 3, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カオリ", 0, "", 2, 101701, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 0, 34, 1, 3, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カオリ", 0, "", 2, 101701, "これからも、どうぞよろしく\nお願いするさ～！", 0, 35, 2, 3, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("サレン", 0, "", 0, 102801, "あんたと出会ってからもうそんなに\nたつのね～", 0, 36, 1, 4, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("サレン", 0, "", 0, 102801, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 0, 37, 2, 4, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("スズメ", 0, "", 1, 102501, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 0, 38, 1, 4, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("スズメ", 0, "", 1, 102501, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 0, 39, 2, 4, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("タマキ", 0, "", 0, 104601, "振り返れば、今までいろんなことが\nあったにゃあ…", 0, 40, 1, 5, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("タマキ", 0, "", 0, 104601, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 0, 41, 2, 5, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ミフユ", 0, "", 1, 104801, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 0, 42, 1, 5, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ミフユ", 0, "", 1, 104801, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 0, 43, 2, 5, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("アキノ", 0, "", 0, 103201, "あなたさまと出会ってもう1年…", 0, 44, 1, 6, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("アキノ", 0, "", 0, 103201, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 0, 45, 2, 6, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ユカリ", 0, "", 1, 103401, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 0, 46, 1, 6, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ユカリ", 0, "", 1, 103401, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 0, 47, 2, 6, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("クリスティーナ", 0, "", 0, 107101, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 0, 48, 1, 7, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("クリスティーナ", 0, "", 0, 107101, "ともかくこれからも\n愉しませてくれよ☆", 0, 49, 2, 7, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ジュン", 0, "", 1, 104701, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 0, 50, 1, 7, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ジュン", 0, "", 1, 104701, "見えないから分からないって？\nそれもそうだな…", 0, 51, 2, 7, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("トモ", 0, "", 0, 103701, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 0, 52, 1, 8, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("トモ", 0, "", 0, 103701, "これからもよろしくね！", 0, 53, 2, 8, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("マツリ", 0, "", 1, 100501, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 0, 54, 1, 8, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("マツリ", 0, "", 1, 100501, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 0, 55, 2, 8, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ミヤコ", 0, "", 0, 100701, "オマエと会って1年なの！\n全然成仏もできなかったし、", 0, 56, 1, 9, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ミヤコ", 0, "", 0, 100701, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 0, 57, 2, 9, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シノブ", 0, "", 1, 103101, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 0, 58, 1, 9, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シノブ", 0, "", 1, 103101, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 0, 59, 2, 9, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("アンナ", 0, "", 0, 100901, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 0, 60, 1, 10, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("アンナ", 0, "", 0, 100901, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 0, 61, 2, 10, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ナナカ", 0, "", 1, 101301, "君と出会ってもう1年だって！\n早いねー！", 0, 62, 1, 10, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ナナカ", 0, "", 1, 101301, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 0, 63, 2, 10, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ミツキ", 0, "", 0, 105101, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 0, 64, 1, 11, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ミツキ", 0, "", 0, 105101, "これからも、いろんなところを\n調べさせてね？", 0, 65, 2, 11, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ルカ", 0, "", 1, 105601, "お前さんと出会ってもう1年か\n助太刀するつもりが", 0, 66, 1, 11, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ルカ", 0, "", 1, 105601, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 0, 67, 2, 11, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("エリコ", 0, "", 2, 102701, "早いものですわね、あなた様と\nお会いして、もう1年…", 0, 68, 1, 11, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("エリコ", 0, "", 2, 102701, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 0, 69, 2, 11, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("アヤネ", 0, "", 0, 102301, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 0, 70, 1, 12, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("アヤネ", 0, "", 0, 102301, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 0, 71, 2, 12, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("チカ", 0, "", 1, 104201, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 0, 72, 1, 12, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("チカ", 0, "", 1, 104201, "私のことを見守ってください\nできることなら、ずっと…", 0, 73, 2, 12, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("クルミ", 0, "", 2, 102101, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 0, 74, 1, 12, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("クルミ", 0, "", 2, 102101, "で、でもこれからもずっと…\n一緒にいたいなぁ…", 0, 75, 2, 12, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シズル", 0, "", 0, 104901, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 0, 76, 1, 13, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シズル", 0, "", 0, 104901, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 0, 77, 2, 13, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("リノ", 0, "", 1, 101101, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 0, 78, 1, 13, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("リノ", 0, "", 1, 101101, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 0, 79, 2, 13, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ユイ", 0, "", 0, 100201, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 0, 80, 1, 14, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ユイ", 0, "", 0, 100201, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 0, 81, 2, 14, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ヒヨリ", 0, "", 1, 100101, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 0, 82, 1, 14, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ヒヨリ", 0, "", 1, 100101, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 0, 83, 2, 14, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("レイ", 0, "", 2, 100301, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 0, 84, 1, 14, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("レイ", 0, "", 2, 100301, "キミに背中を預けられる日が、\n早くきますように", 0, 85, 2, 14, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 0, 86, 1, 15, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "これからもずっと、覚えていて\nくださいね！", 0, 87, 2, 15, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 0, 88, 1, 15, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 0, 89, 2, 15, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 0, 90, 1, 15, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 0, 91, 2, 15, 0, 80001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("スズメ", 0, "", 1, 112101, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 0, 92, 1, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0, 93, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 94, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0, 95, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0, 96, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0, 97, 0, 0, 2, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0, 98, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0, 99, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0, 100, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0, 101, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0, 102, 0, 0, 2, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0, 103, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0, 104, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 105, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 106, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 0, 107, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 0, 108, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0, 109, 0, 0, 2, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0, 110, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0, 111, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0, 112, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 113, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0, 114, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 0, 115, 0, 0, 1, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 116, 0, 0, 2, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("スズメ", 0, "", 3, 112101, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 0, 117, 2, 0, 0, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("スズメ", 0, "", 3, 112101, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 0, 118, 3, 7, 0, 60001);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "ヒューヒュー♪\nドンドンパフパフー♪", 0, 119, 1, 1, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 0, 120, 1, 1, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 0, 121, 2, 1, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "え？　ちょっと\nいきなりなにっ？", 0, 122, 1, 1, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 0, 123, 2, 1, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 0, 124, 2, 1, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 0, 125, 3, 1, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "ですからその日に向けて\n今から気分を盛り上げているのです", 0, 126, 3, 1, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 0, 127, 3, 1, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 0, 128, 4, 1, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 0, 129, 4, 1, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 0, 130, 4, 1, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 0, 131, 5, 1, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 0, 132, 5, 2, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 1, 190801, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 0, 133, 1, 2, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "げっ…カリンじゃない\nなんでもないわよ！", 0, 134, 6, 2, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 1, 190801, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 0, 135, 2, 2, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 0, 136, 7, 2, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 1, 190801, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 0, 137, 3, 2, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 0, 138, 8, 2, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 0, 139, 9, 2, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 1, 190801, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 0, 140, 4, 2, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 0, 141, 10, 2, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 1, 190801, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 0, 142, 5, 2, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 0, 143, 11, 2, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 0, 144, 6, 3, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 1, 105801, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 0, 145, 5, 3, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 0, 146, 7, 3, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 0, 147, 8, 3, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 1, 105801, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 0, 148, 6, 3, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 1, 105801, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 0, 149, 7, 3, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "あとは、牧場に素敵な温泉が\n出来たとか…", 0, 150, 9, 3, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 1, 105801, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 0, 151, 8, 3, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 0, 152, 10, 3, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 1, 105801, "えへへ、ありがとうございます\nコッコロちゃん！", 0, 153, 9, 3, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 0, 154, 11, 3, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 1, 105801, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 0, 155, 10, 3, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 0, 156, 11, 4, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 0, 157, 12, 4, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 0, 158, 12, 4, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 0, 159, 13, 4, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 0, 160, 13, 4, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "…積極的？\nなんの話よ？", 0, 161, 14, 4, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 0, 162, 14, 4, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "ムーサの町で\nきゃるきゃる～ん☆　って！", 0, 163, 15, 4, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 0, 164, 15, 4, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 0, 165, 16, 4, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 0, 166, 16, 4, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 0, 167, 17, 4, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 0, 168, 18, 5, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 0, 169, 12, 5, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 0, 170, 19, 5, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 0, 171, 13, 5, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 0, 172, 20, 5, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 0, 173, 14, 5, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 0, 174, 21, 5, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 0, 175, 15, 5, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "かくし芸？", 0, 176, 22, 5, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 0, 177, 16, 5, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 0, 178, 23, 5, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 0, 179, 17, 6, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 0, 180, 24, 6, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 0, 181, 17, 6, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 0, 182, 18, 6, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 0, 183, 25, 6, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 0, 184, 18, 6, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 0, 185, 26, 6, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 0, 186, 27, 6, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 0, 187, 28, 6, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 0, 188, 19, 6, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 0, 189, 19, 6, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 0, 190, 29, 6, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 0, 191, 30, 7, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 0, 192, 31, 7, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 0, 193, 32, 7, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "あー\nあー\n…声、これで大丈夫かしら？", 0, 194, 33, 7, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 0, 195, 34, 7, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "オイッスー☆", 0, 196, 35, 7, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "……\nいや、ないわ\nいつもどおりが一番ね、うん", 0, 197, 36, 7, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 0, 198, 37, 7, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 0, 199, 38, 7, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 0, 200, 20, 8, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 0, 201, 21, 8, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 0, 202, 22, 8, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 0, 203, 23, 8, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 0, 204, 24, 8, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "オイッスー☆", 0, 205, 25, 8, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 0, 206, 26, 8, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 0, 207, 27, 8, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 0, 208, 28, 8, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "オイッスー☆", 0, 209, 20, 9, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 0, 210, 21, 9, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "あと６日で2.5周年ですか…", 0, 211, 22, 9, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 0, 212, 23, 9, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 0, 213, 24, 9, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "オイッスー☆", 0, 214, 25, 9, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 0, 215, 26, 9, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 0, 216, 27, 9, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 0, 217, 28, 9, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 0, 218, 29, 9, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 0, 219, 30, 9, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 0, 220, 31, 10, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 0, 221, 29, 10, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 0, 222, 32, 10, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "…………", 0, 223, 39, 10, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 0, 224, 30, 10, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 0, 225, 40, 10, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 0, 226, 33, 10, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 0, 227, 41, 10, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 0, 228, 34, 10, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 0, 229, 35, 10, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 0, 230, 42, 10, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 0, 231, 31, 10, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 0, 232, 43, 10, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 0, 233, 36, 10, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 0, 234, 6, 11, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 0, 235, 7, 11, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 0, 236, 8, 11, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 0, 237, 9, 11, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 0, 238, 10, 11, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "次にコッコロさんですが\nこちらは発声練習をしています", 0, 239, 11, 11, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 0, 240, 12, 11, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 0, 241, 13, 11, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "あれ？\nもしかして緊張で気を失って…？", 0, 242, 14, 11, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 0, 243, 15, 11, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 0, 244, 16, 11, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "それでは\nまた明日お会いしましょう♪", 0, 245, 17, 11, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 0, 246, 44, 12, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 2, 105801, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 0, 247, 37, 12, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 0, 248, 32, 12, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 0, 249, 45, 12, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 2, 105801, "ひどいですよぉキャルちゃ～ん！", 0, 250, 38, 12, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 0, 251, 33, 12, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 2, 105801, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 0, 252, 39, 12, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 0, 253, 46, 12, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 0, 254, 34, 12, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 2, 105801, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 0, 255, 40, 12, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 0, 256, 41, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 0, 257, 47, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "忘れてきちゃいました☆", 0, 258, 42, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 0, 259, 48, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 0, 260, 35, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 0, 261, 49, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 0, 262, 43, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 0, 263, 50, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 0, 264, 36, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 0, 265, 51, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 0, 266, 37, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 0, 267, 52, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 0, 268, 44, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 0, 269, 38, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 0, 270, 45, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 0, 271, 53, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 0, 272, 46, 13, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "こんにちは\n【ギルド管理協会】のカリンです", 0, 273, 18, 14, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "昨日の生放送は\nいかがだったでしょうか？", 0, 274, 19, 14, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 0, 275, 20, 14, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 0, 276, 21, 14, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 0, 277, 22, 14, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 0, 278, 23, 14, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 0, 279, 24, 14, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 0, 280, 25, 14, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 0, 281, 26, 14, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 0, 190801, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 0, 282, 27, 14, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "準備はいいですかっ？\n…いっきますよー！", 0, 283, 47, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "祝！", 0, 284, 54, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "2.5周年！", 0, 285, 39, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "おめでとうございま～す☆", 0, 286, 48, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 0, 287, 55, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 0, 288, 40, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 0, 289, 49, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 0, 290, 56, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 0, 291, 41, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 0, 292, 57, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 0, 293, 50, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 0, 294, 58, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "記念日だから\n平気平気♪\nやばいですね☆", 0, 295, 51, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "意味分かんないわよ！", 0, 296, 59, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 0, 297, 42, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 0, 298, 43, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 0, 299, 52, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 2, 106001, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 0, 300, 60, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 0, 301, 44, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 0, 302, 53, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ・コッコロ・キャル", 0, "105901,45,106001,61", 0, 105801, "2.5周年\nありがとうございます！", 1, 303, 54, 15, 0, 80003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 1, 190804, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 304, 1, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0, 305, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 306, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0, 307, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0, 308, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0, 309, 0, 0, 2, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0, 310, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0, 311, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0, 312, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0, 313, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0, 314, 0, 0, 2, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0, 315, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0, 316, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 317, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0, 318, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 0, 319, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 320, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 0, 321, 0, 0, 2, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 0, 322, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 0, 323, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 0, 324, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 325, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0, 326, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 0, 327, 0, 0, 1, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 328, 0, 0, 2, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 3, 190804, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 329, 2, 0, 0, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 3, 190804, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 330, 3, 7, 0, 60002);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 4, 105901, "ささ、シェフィさまはこちらへ…", 0, 331, 1, 1, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "コッコロさん、どこへ行くの…？\n…え？", 0, 332, 1, 1, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 0, 333, 1, 1, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 4, 105901, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 0, 334, 2, 1, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "ええと…\n二人とも何をしているの？", 0, 335, 2, 1, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 0, 336, 1, 1, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "あの日…？", 0, 337, 3, 1, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 0, 338, 2, 1, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "『プリンセスコネクト！\nRe:Dive』3.5周年！", 0, 339, 3, 1, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 2, 190801, "やばいですね☆", 0, 340, 1, 1, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "って、なにしれっと\n参加してんのよカリン！", 0, 341, 2, 1, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 2, 190801, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 0, 342, 2, 1, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 2, 190801, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 0, 343, 3, 1, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 0, 344, 4, 1, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "えっ！？　ええとええと…！\nあ、あと14日…！", 0, 345, 4, 1, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 0, 346, 5, 2, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 0, 347, 3, 2, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 0, 348, 4, 2, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 0, 349, 6, 2, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "さすがでございます\nペコリーヌさま", 0, 350, 5, 2, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 0, 351, 3, 2, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "しゃなり、しゃなり…\nふふ、案外できるものね", 0, 352, 5, 2, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 0, 353, 7, 2, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "さ、次はキャルさまの\n番でございますよ", 0, 354, 6, 2, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 0, 355, 4, 2, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 0, 356, 6, 2, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 0, 357, 5, 2, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "いたたた…もぉお～！\nなんであたしがこんな目に～！", 0, 358, 6, 2, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 0, 359, 7, 2, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 0, 360, 7, 2, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 0, 361, 8, 3, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 0, 362, 8, 3, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 0, 363, 9, 3, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "聖学祭…って、なにかしら？", 0, 364, 7, 3, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 0, 365, 10, 3, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 0, 366, 8, 3, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 0, 367, 9, 3, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 0, 368, 9, 3, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "いや、そのまんますぎでしょ…", 0, 369, 10, 3, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 0, 370, 8, 3, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 0, 371, 10, 3, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "えっ？　ええ…\nぎゅーっ…？", 0, 372, 9, 3, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 0, 373, 11, 4, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 0, 374, 10, 4, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 0, 375, 12, 4, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 0, 376, 11, 4, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 0, 377, 13, 4, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 0, 378, 11, 4, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 0, 379, 14, 4, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 0, 380, 15, 4, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 0, 381, 11, 4, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 0, 382, 12, 4, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 0, 383, 12, 4, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 0, 384, 16, 4, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 0, 385, 12, 4, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 0, 386, 13, 5, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 0, 387, 14, 5, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 0, 388, 15, 5, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 0, 389, 17, 5, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 0, 390, 18, 5, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 0, 391, 13, 5, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 0, 392, 13, 5, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 0, 393, 16, 5, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 0, 394, 19, 5, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 0, 395, 17, 5, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 0, 396, 18, 5, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 0, 397, 19, 5, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 2, 190801, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 0, 398, 4, 6, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 2, 190801, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 0, 399, 5, 6, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "…えっ！？", 0, 400, 20, 6, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 0, 401, 20, 6, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 0, 402, 14, 6, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 4, 105901, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 0, 403, 14, 6, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 0, 404, 15, 6, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 0, 405, 21, 6, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 0, 406, 21, 6, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "ぶっ殺すぞ！？", 0, 407, 22, 6, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 2, 190801, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 0, 408, 6, 6, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 0, 409, 15, 7, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 0, 410, 23, 7, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 0, 411, 16, 7, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 0, 412, 22, 7, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 0, 413, 24, 7, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 0, 414, 17, 7, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 0, 415, 16, 7, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 0, 416, 18, 7, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "もちろんでございます\n仲良くお勉強いたしましょう", 0, 417, 17, 7, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "しれっと教育ママが\n生まれてる…？", 0, 418, 25, 7, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 0, 419, 23, 7, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "え～～～～！？", 0, 420, 26, 7, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 0, 421, 27, 8, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 0, 422, 19, 8, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 0, 423, 18, 8, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 0, 424, 24, 8, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 0, 425, 20, 8, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 0, 426, 28, 8, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 0, 427, 19, 8, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 0, 428, 21, 8, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 0, 429, 29, 8, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 0, 430, 30, 8, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 0, 431, 25, 8, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 0, 432, 31, 8, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 0, 433, 22, 9, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "…えっ？\n生放送の意気込み！？\n突然なに！？", 0, 434, 23, 9, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 0, 435, 24, 9, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 0, 436, 25, 9, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 0, 437, 26, 9, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "それから、それから…！\nぅ、ぅぅっ…！", 0, 438, 27, 9, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "―びえぇぇ～んっ！！！", 0, 439, 28, 9, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "どうされましたか、シェフィさま", 0, 440, 20, 9, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "ううっ…ひっく…\nママッ…！", 0, 441, 29, 9, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 0, 442, 21, 9, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 0, 443, 22, 9, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 0, 444, 30, 9, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "主さま\n3.5周年まであと５日\nでございます", 0, 445, 23, 10, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "ですがその前に\n生放送がありますね", 0, 446, 24, 10, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "…生放送に向けた\n意気込みでございますか？", 0, 447, 25, 10, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 0, 448, 26, 10, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 0, 449, 27, 10, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 0, 450, 28, 10, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 0, 451, 29, 10, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 0, 452, 30, 10, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 0, 105901, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 0, 453, 31, 10, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "3.5周年まであと４日！\nやばいですね☆", 0, 454, 26, 11, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 0, 455, 32, 11, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 0, 456, 27, 11, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 0, 457, 28, 11, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 0, 458, 29, 11, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 0, 459, 33, 11, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 0, 460, 30, 11, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 0, 461, 31, 11, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 0, 462, 32, 11, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "だからあたしはやるなんて一言も…", 0, 463, 34, 11, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 0, 105801, "オイッスー☆", 0, 464, 33, 11, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "聞きなさいっての！！", 0, 465, 35, 11, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "3.5周年まであと３日…\nそして生放送は、明日…", 0, 466, 31, 12, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 0, 467, 32, 12, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "おいっす！\n…こうじゃなかったわね\nええと確か…", 0, 468, 33, 12, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 0, 469, 34, 12, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 0, 470, 35, 12, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "やってますねシェフィちゃん！", 0, 471, 34, 12, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "なによ、考えることは\nみんな同じってこと？", 0, 472, 36, 12, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 0, 473, 32, 12, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "みんな…もちろんよ！", 0, 474, 36, 12, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 0, 475, 35, 12, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 2, 190801, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 0, 476, 7, 13, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 2, 190801, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 0, 477, 8, 13, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 0, 478, 36, 13, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 2, 190801, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 0, 479, 9, 13, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 4, 105901, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 0, 480, 33, 13, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 2, 190801, "さすが【美食殿】のママですね！\nそしてキャルさんは…", 0, 481, 10, 13, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "大丈夫…大丈夫…\nあたしは大丈夫…", 0, 482, 37, 13, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 2, 190801, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 0, 483, 11, 13, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "あうー？", 0, 484, 37, 13, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("みんな", 0, "105901,34,106001,38,190801,12", 4, 105801, "あ…", 1, 485, 37, 13, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 0, 486, 39, 13, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 4, 105901, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 0, 487, 35, 13, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 0, 488, 38, 13, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 2, 190801, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 0, 489, 13, 13, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 0, 490, 38, 14, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 0, 491, 39, 14, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 0, 492, 40, 14, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 0, 493, 36, 14, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 0, 494, 39, 14, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 0, 495, 40, 14, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 1, 105901, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 0, 496, 37, 14, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 0, 106001, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 0, 497, 41, 14, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "へ、へぇ…", 0, 498, 40, 14, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 0, 499, 41, 14, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 0, 500, 41, 14, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 2, 106401, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 0, 501, 42, 14, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 0, 502, 42, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "祝！", 0, 503, 42, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 4, 105901, "3.5周年！", 0, 504, 38, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "おめでとうございます！", 0, 505, 43, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 2, 190801, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 0, 506, 14, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 2, 190801, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 0, 507, 15, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 0, 508, 43, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 0, 509, 44, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 0, 510, 43, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャル", 0, "", 1, 106001, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 0, 511, 44, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 0, 512, 44, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィ", 0, "", 0, 106401, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 0, 513, 45, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 4, 105901, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 0, 514, 39, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 4, 105901, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 0, 515, 40, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("コッコロ", 0, "", 4, 105901, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 0, 516, 41, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌ", 0, "", 3, 105801, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 0, 517, 45, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("みんな", 0, "105901,42,106001,45,106401,46,190801,16", 4, 105801, "これからも\nよろしくおねがいします！", 1, 518, 46, 15, 0, 80005);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カリン", 0, "", 1, 190804, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 519, 1, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0, 520, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 521, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 0, 522, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 0, 523, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 1, "", 2, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0, 524, 0, 0, 2, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0, 525, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0, 526, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 0, 527, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 0, 528, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 2, "", 2, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0, 529, 0, 0, 2, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0, 530, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0, 531, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 532, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0, 533, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", 0, 534, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 535, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 3, "", 2, 0, "今日の運勢は中吉！　悩まず行動すれば強運に恵まれる！　　　　守るよりも攻めるが吉だ！　勝負事をするにはいい運勢だな！", 0, 536, 0, 0, 2, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 0, 537, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 0, 538, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 0, 539, 0, 0, 1, 60003);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", 4, "", 2, 0, "今日の運勢は小吉！　難しい頼み事をされるかもしれません！　　うまくやれればきっと成長につながります、挑戦してみましょう！", 0, 540, 0, 0, 1, 60003);
