INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 1, 60000, 190804, 0, 1, 1, "カリン", 0, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0, 2, 60000, 0, 0, 1, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 3, 60000, 0, 0, 1, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0, 4, 60000, 0, 0, 2, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0, 5, 60000, 0, 0, 1, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0, 6, 60000, 0, 0, 1, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0, 7, 60000, 0, 0, 2, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0, 8, 60000, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0, 9, 60000, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 10, 60000, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 11, 60000, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0, 12, 60000, 0, 0, 2, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0, 13, 60000, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0, 14, 60000, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0, 15, 60000, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 16, 60000, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 17, 60000, 0, 0, 2, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 2, 18, 60000, 190804, 0, 0, 3, "カリン", 0, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 3, 19, 60000, 190804, 0, 0, 3, "カリン", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 1, 20, 80001, 101201, 0, 0, 0, "ハツネ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 2, 21, 80001, 101201, 0, 0, 0, "ハツネ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 1, 22, 80001, 103801, 0, 0, 1, "シオリ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "来年も、\n一緒にいられたら…\nうれしいな", 2, 23, 80001, 103801, 0, 0, 1, "シオリ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あなたと一緒にいるようになって、\nもう1年ですか…", 1, 24, 80001, 103601, 0, 0, 0, "キョウカ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 2, 25, 80001, 103601, 0, 0, 0, "キョウカ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "おにいちゃんとあってから、\n1年たったよ～！", 1, 26, 80001, 102001, 0, 0, 1, "ミミ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 2, 27, 80001, 102001, 0, 0, 1, "ミミ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 1, 28, 80001, 100401, 0, 0, 2, "ミソギ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 2, 29, 80001, 100401, 0, 0, 2, "ミソギ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 1, 30, 80001, 101001, 0, 0, 0, "マホ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 2, 31, 80001, 101001, 0, 0, 0, "マホ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 1, 32, 80001, 104401, 0, 0, 1, "イリヤ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 2, 33, 80001, 104401, 0, 0, 1, "イリヤ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 1, 34, 80001, 101701, 0, 0, 2, "カオリ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "これからも、どうぞよろしく\nお願いするさ～！", 2, 35, 80001, 101701, 0, 0, 2, "カオリ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あんたと出会ってからもうそんなに\nたつのね～", 1, 36, 80001, 102801, 0, 0, 0, "サレン", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 2, 37, 80001, 102801, 0, 0, 0, "サレン", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 1, 38, 80001, 102501, 0, 0, 1, "スズメ", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 2, 39, 80001, 102501, 0, 0, 1, "スズメ", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "振り返れば、今までいろんなことが\nあったにゃあ…", 1, 40, 80001, 104601, 0, 0, 0, "タマキ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 2, 41, 80001, 104601, 0, 0, 0, "タマキ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 1, 42, 80001, 104801, 0, 0, 1, "ミフユ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 2, 43, 80001, 104801, 0, 0, 1, "ミフユ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あなたさまと出会ってもう1年…", 1, 44, 80001, 103201, 0, 0, 0, "アキノ", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 2, 45, 80001, 103201, 0, 0, 0, "アキノ", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 1, 46, 80001, 103401, 0, 0, 1, "ユカリ", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 2, 47, 80001, 103401, 0, 0, 1, "ユカリ", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 1, 48, 80001, 107101, 0, 0, 0, "クリスティーナ", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ともかくこれからも\n愉しませてくれよ☆", 2, 49, 80001, 107101, 0, 0, 0, "クリスティーナ", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 1, 50, 80001, 104701, 0, 0, 1, "ジュン", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "見えないから分からないって？\nそれもそうだな…", 2, 51, 80001, 104701, 0, 0, 1, "ジュン", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 1, 52, 80001, 103701, 0, 0, 0, "トモ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "これからもよろしくね！", 2, 53, 80001, 103701, 0, 0, 0, "トモ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 1, 54, 80001, 100501, 0, 0, 1, "マツリ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 2, 55, 80001, 100501, 0, 0, 1, "マツリ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "オマエと会って1年なの！\n全然成仏もできなかったし、", 1, 56, 80001, 100701, 0, 0, 0, "ミヤコ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 2, 57, 80001, 100701, 0, 0, 0, "ミヤコ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 1, 58, 80001, 103101, 0, 0, 1, "シノブ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 2, 59, 80001, 103101, 0, 0, 1, "シノブ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 1, 60, 80001, 100901, 0, 0, 0, "アンナ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 2, 61, 80001, 100901, 0, 0, 0, "アンナ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "君と出会ってもう1年だって！\n早いねー！", 1, 62, 80001, 101301, 0, 0, 1, "ナナカ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 2, 63, 80001, 101301, 0, 0, 1, "ナナカ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 1, 64, 80001, 105101, 0, 0, 0, "ミツキ", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "これからも、いろんなところを\n調べさせてね？", 2, 65, 80001, 105101, 0, 0, 0, "ミツキ", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "お前さんと出会ってもう1年か\n助太刀するつもりが", 1, 66, 80001, 105601, 0, 0, 1, "ルカ", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 2, 67, 80001, 105601, 0, 0, 1, "ルカ", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "早いものですわね、あなた様と\nお会いして、もう1年…", 1, 68, 80001, 102701, 0, 0, 2, "エリコ", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 2, 69, 80001, 102701, 0, 0, 2, "エリコ", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 1, 70, 80001, 102301, 0, 0, 0, "アヤネ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 2, 71, 80001, 102301, 0, 0, 0, "アヤネ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 1, 72, 80001, 104201, 0, 0, 1, "チカ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "私のことを見守ってください\nできることなら、ずっと…", 2, 73, 80001, 104201, 0, 0, 1, "チカ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 1, 74, 80001, 102101, 0, 0, 2, "クルミ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "で、でもこれからもずっと…\n一緒にいたいなぁ…", 2, 75, 80001, 102101, 0, 0, 2, "クルミ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 1, 76, 80001, 104901, 0, 0, 0, "シズル", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 2, 77, 80001, 104901, 0, 0, 0, "シズル", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 1, 78, 80001, 101101, 0, 0, 1, "リノ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 2, 79, 80001, 101101, 0, 0, 1, "リノ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 1, 80, 80001, 100201, 0, 0, 0, "ユイ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 2, 81, 80001, 100201, 0, 0, 0, "ユイ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 1, 82, 80001, 100101, 0, 0, 1, "ヒヨリ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 2, 83, 80001, 100101, 0, 0, 1, "ヒヨリ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 1, 84, 80001, 100301, 0, 0, 2, "レイ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "キミに背中を預けられる日が、\n早くきますように", 2, 85, 80001, 100301, 0, 0, 2, "レイ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 1, 86, 80001, 105801, 0, 0, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "これからもずっと、覚えていて\nくださいね！", 2, 87, 80001, 105801, 0, 0, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 1, 88, 80001, 105901, 0, 0, 1, "コッコロ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 2, 89, 80001, 105901, 0, 0, 1, "コッコロ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 1, 90, 80001, 106001, 0, 0, 2, "キャル", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 2, 91, 80001, 106001, 0, 0, 2, "キャル", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 1, 92, 60001, 112101, 0, 1, 1, "スズメ", 0, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0, 93, 60001, 0, 0, 1, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 94, 60001, 0, 0, 1, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0, 95, 60001, 0, 0, 1, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0, 96, 60001, 0, 0, 1, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0, 97, 60001, 0, 0, 2, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0, 98, 60001, 0, 0, 1, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0, 99, 60001, 0, 0, 1, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0, 100, 60001, 0, 0, 1, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0, 101, 60001, 0, 0, 1, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0, 102, 60001, 0, 0, 2, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0, 103, 60001, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0, 104, 60001, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 105, 60001, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 106, 60001, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 0, 107, 60001, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 0, 108, 60001, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0, 109, 60001, 0, 0, 2, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0, 110, 60001, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0, 111, 60001, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0, 112, 60001, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 113, 60001, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0, 114, 60001, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 0, 115, 60001, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 116, 60001, 0, 0, 2, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 2, 117, 60001, 112101, 0, 0, 3, "スズメ", 0, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 3, 118, 60001, 112101, 0, 0, 3, "スズメ", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ヒューヒュー♪\nドンドンパフパフー♪", 1, 119, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 1, 120, 80003, 105901, 0, 0, 1, "コッコロ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 2, 121, 80003, 105901, 0, 0, 1, "コッコロ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "え？　ちょっと\nいきなりなにっ？", 1, 122, 80003, 106001, 0, 0, 2, "キャル", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 2, 123, 80003, 106001, 0, 0, 2, "キャル", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 2, 124, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 3, 125, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ですからその日に向けて\n今から気分を盛り上げているのです", 3, 126, 80003, 105901, 0, 0, 1, "コッコロ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 3, 127, 80003, 106001, 0, 0, 2, "キャル", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 4, 128, 80003, 105901, 0, 0, 1, "コッコロ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 4, 129, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 4, 130, 80003, 106001, 0, 0, 2, "キャル", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 5, 131, 80003, 105901, 0, 0, 1, "コッコロ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 5, 132, 80003, 106001, 0, 0, 0, "キャル", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 1, 133, 80003, 190801, 0, 0, 1, "カリン", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "げっ…カリンじゃない\nなんでもないわよ！", 6, 134, 80003, 106001, 0, 0, 0, "キャル", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 2, 135, 80003, 190801, 0, 0, 1, "カリン", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 7, 136, 80003, 106001, 0, 0, 0, "キャル", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 3, 137, 80003, 190801, 0, 0, 1, "カリン", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 8, 138, 80003, 106001, 0, 0, 0, "キャル", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 9, 139, 80003, 106001, 0, 0, 0, "キャル", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 4, 140, 80003, 190801, 0, 0, 1, "カリン", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 10, 141, 80003, 106001, 0, 0, 0, "キャル", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 5, 142, 80003, 190801, 0, 0, 1, "カリン", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 11, 143, 80003, 106001, 0, 0, 0, "キャル", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 6, 144, 80003, 105901, 0, 0, 0, "コッコロ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 5, 145, 80003, 105801, 0, 0, 1, "ペコリーヌ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 7, 146, 80003, 105901, 0, 0, 0, "コッコロ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 8, 147, 80003, 105901, 0, 0, 0, "コッコロ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 6, 148, 80003, 105801, 0, 0, 1, "ペコリーヌ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 7, 149, 80003, 105801, 0, 0, 1, "ペコリーヌ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あとは、牧場に素敵な温泉が\n出来たとか…", 9, 150, 80003, 105901, 0, 0, 0, "コッコロ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 8, 151, 80003, 105801, 0, 0, 1, "ペコリーヌ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 10, 152, 80003, 105901, 0, 0, 0, "コッコロ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "えへへ、ありがとうございます\nコッコロちゃん！", 9, 153, 80003, 105801, 0, 0, 1, "ペコリーヌ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 11, 154, 80003, 105901, 0, 0, 0, "コッコロ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 10, 155, 80003, 105801, 0, 0, 1, "ペコリーヌ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 11, 156, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 12, 157, 80003, 106001, 0, 0, 1, "キャル", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 12, 158, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 13, 159, 80003, 106001, 0, 0, 1, "キャル", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 13, 160, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…積極的？\nなんの話よ？", 14, 161, 80003, 106001, 0, 0, 1, "キャル", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 14, 162, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ムーサの町で\nきゃるきゃる～ん☆　って！", 15, 163, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 15, 164, 80003, 106001, 0, 0, 1, "キャル", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 16, 165, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 16, 166, 80003, 106001, 0, 0, 1, "キャル", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 17, 167, 80003, 106001, 0, 0, 1, "キャル", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 18, 168, 80003, 106001, 0, 0, 0, "キャル", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 12, 169, 80003, 105901, 0, 0, 1, "コッコロ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 19, 170, 80003, 106001, 0, 0, 0, "キャル", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 13, 171, 80003, 105901, 0, 0, 1, "コッコロ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 20, 172, 80003, 106001, 0, 0, 0, "キャル", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 14, 173, 80003, 105901, 0, 0, 1, "コッコロ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 21, 174, 80003, 106001, 0, 0, 0, "キャル", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 15, 175, 80003, 105901, 0, 0, 1, "コッコロ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "かくし芸？", 22, 176, 80003, 106001, 0, 0, 0, "キャル", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 16, 177, 80003, 105901, 0, 0, 1, "コッコロ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 23, 178, 80003, 106001, 0, 0, 0, "キャル", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 17, 179, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 24, 180, 80003, 106001, 0, 0, 2, "キャル", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 17, 181, 80003, 105901, 0, 0, 1, "コッコロ", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 18, 182, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 25, 183, 80003, 106001, 0, 0, 2, "キャル", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 18, 184, 80003, 105901, 0, 0, 1, "コッコロ", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 26, 185, 80003, 106001, 0, 0, 2, "キャル", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 27, 186, 80003, 106001, 0, 0, 2, "キャル", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 28, 187, 80003, 106001, 0, 0, 2, "キャル", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 19, 188, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 19, 189, 80003, 105901, 0, 0, 1, "コッコロ", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 29, 190, 80003, 106001, 0, 0, 2, "キャル", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 30, 191, 80003, 106001, 0, 0, 0, "キャル", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 31, 192, 80003, 106001, 0, 0, 0, "キャル", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 32, 193, 80003, 106001, 0, 0, 0, "キャル", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あー\nあー\n…声、これで大丈夫かしら？", 33, 194, 80003, 106001, 0, 0, 0, "キャル", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 34, 195, 80003, 106001, 0, 0, 0, "キャル", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "オイッスー☆", 35, 196, 80003, 106001, 0, 0, 0, "キャル", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "……\nいや、ないわ\nいつもどおりが一番ね、うん", 36, 197, 80003, 106001, 0, 0, 0, "キャル", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 37, 198, 80003, 106001, 0, 0, 0, "キャル", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 38, 199, 80003, 106001, 0, 0, 0, "キャル", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 20, 200, 80003, 105901, 0, 0, 0, "コッコロ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 21, 201, 80003, 105901, 0, 0, 0, "コッコロ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 22, 202, 80003, 105901, 0, 0, 0, "コッコロ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 23, 203, 80003, 105901, 0, 0, 0, "コッコロ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 24, 204, 80003, 105901, 0, 0, 0, "コッコロ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "オイッスー☆", 25, 205, 80003, 105901, 0, 0, 0, "コッコロ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 26, 206, 80003, 105901, 0, 0, 0, "コッコロ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 27, 207, 80003, 105901, 0, 0, 0, "コッコロ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 28, 208, 80003, 105901, 0, 0, 0, "コッコロ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "オイッスー☆", 20, 209, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 21, 210, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あと６日で2.5周年ですか…", 22, 211, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 23, 212, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 24, 213, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "オイッスー☆", 25, 214, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 26, 215, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 27, 216, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 28, 217, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 29, 218, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 30, 219, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 31, 220, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 29, 221, 80003, 105901, 0, 0, 1, "コッコロ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 32, 222, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…………", 39, 223, 80003, 106001, 0, 0, 2, "キャル", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 30, 224, 80003, 105901, 0, 0, 1, "コッコロ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 40, 225, 80003, 106001, 0, 0, 2, "キャル", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 33, 226, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 41, 227, 80003, 106001, 0, 0, 2, "キャル", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 34, 228, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 35, 229, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 42, 230, 80003, 106001, 0, 0, 2, "キャル", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 31, 231, 80003, 105901, 0, 0, 1, "コッコロ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 43, 232, 80003, 106001, 0, 0, 2, "キャル", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 36, 233, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 6, 234, 80003, 190801, 0, 0, 0, "カリン", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 7, 235, 80003, 190801, 0, 0, 0, "カリン", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 8, 236, 80003, 190801, 0, 0, 0, "カリン", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 9, 237, 80003, 190801, 0, 0, 0, "カリン", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 10, 238, 80003, 190801, 0, 0, 0, "カリン", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "次にコッコロさんですが\nこちらは発声練習をしています", 11, 239, 80003, 190801, 0, 0, 0, "カリン", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 12, 240, 80003, 190801, 0, 0, 0, "カリン", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 13, 241, 80003, 190801, 0, 0, 0, "カリン", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あれ？\nもしかして緊張で気を失って…？", 14, 242, 80003, 190801, 0, 0, 0, "カリン", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 15, 243, 80003, 190801, 0, 0, 0, "カリン", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 16, 244, 80003, 190801, 0, 0, 0, "カリン", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "それでは\nまた明日お会いしましょう♪", 17, 245, 80003, 190801, 0, 0, 0, "カリン", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 44, 246, 80003, 106001, 0, 0, 0, "キャル", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 37, 247, 80003, 105801, 0, 0, 2, "ペコリーヌ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 32, 248, 80003, 105901, 0, 0, 1, "コッコロ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 45, 249, 80003, 106001, 0, 0, 0, "キャル", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ひどいですよぉキャルちゃ～ん！", 38, 250, 80003, 105801, 0, 0, 2, "ペコリーヌ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 33, 251, 80003, 105901, 0, 0, 1, "コッコロ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 39, 252, 80003, 105801, 0, 0, 2, "ペコリーヌ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 46, 253, 80003, 106001, 0, 0, 0, "キャル", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 34, 254, 80003, 105901, 0, 0, 1, "コッコロ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 40, 255, 80003, 105801, 0, 0, 2, "ペコリーヌ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 41, 256, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 47, 257, 80003, 106001, 0, 0, 2, "キャル", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "忘れてきちゃいました☆", 42, 258, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 48, 259, 80003, 106001, 0, 0, 2, "キャル", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 35, 260, 80003, 105901, 0, 0, 1, "コッコロ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 49, 261, 80003, 106001, 0, 0, 2, "キャル", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 43, 262, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 50, 263, 80003, 106001, 0, 0, 2, "キャル", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 36, 264, 80003, 105901, 0, 0, 1, "コッコロ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 51, 265, 80003, 106001, 0, 0, 2, "キャル", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 37, 266, 80003, 105901, 0, 0, 1, "コッコロ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 52, 267, 80003, 106001, 0, 0, 2, "キャル", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 44, 268, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 38, 269, 80003, 105901, 0, 0, 1, "コッコロ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 45, 270, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 53, 271, 80003, 106001, 0, 0, 2, "キャル", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 46, 272, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "こんにちは\n【ギルド管理協会】のカリンです", 18, 273, 80003, 190801, 0, 0, 0, "カリン", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "昨日の生放送は\nいかがだったでしょうか？", 19, 274, 80003, 190801, 0, 0, 0, "カリン", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 20, 275, 80003, 190801, 0, 0, 0, "カリン", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 21, 276, 80003, 190801, 0, 0, 0, "カリン", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 22, 277, 80003, 190801, 0, 0, 0, "カリン", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 23, 278, 80003, 190801, 0, 0, 0, "カリン", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 24, 279, 80003, 190801, 0, 0, 0, "カリン", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 25, 280, 80003, 190801, 0, 0, 0, "カリン", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 26, 281, 80003, 190801, 0, 0, 0, "カリン", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 27, 282, 80003, 190801, 0, 0, 0, "カリン", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "準備はいいですかっ？\n…いっきますよー！", 47, 283, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "祝！", 54, 284, 80003, 106001, 0, 0, 2, "キャル", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "2.5周年！", 39, 285, 80003, 105901, 0, 0, 1, "コッコロ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "おめでとうございま～す☆", 48, 286, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 55, 287, 80003, 106001, 0, 0, 2, "キャル", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 40, 288, 80003, 105901, 0, 0, 1, "コッコロ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 49, 289, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 56, 290, 80003, 106001, 0, 0, 2, "キャル", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 41, 291, 80003, 105901, 0, 0, 1, "コッコロ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 57, 292, 80003, 106001, 0, 0, 2, "キャル", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 50, 293, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 58, 294, 80003, 106001, 0, 0, 2, "キャル", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "記念日だから\n平気平気♪\nやばいですね☆", 51, 295, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "意味分かんないわよ！", 59, 296, 80003, 106001, 0, 0, 2, "キャル", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 42, 297, 80003, 105901, 0, 0, 1, "コッコロ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 43, 298, 80003, 105901, 0, 0, 1, "コッコロ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 52, 299, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 60, 300, 80003, 106001, 0, 0, 2, "キャル", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 44, 301, 80003, 105901, 0, 0, 1, "コッコロ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 53, 302, 80003, 105801, 0, 0, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("105901,45,106001,61", "2.5周年\nありがとうございます！", 54, 303, 80003, 105801, 1, 0, 0, "ペコリーヌ・コッコロ・キャル", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 304, 60002, 190804, 0, 1, 1, "カリン", 0, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0, 305, 60002, 0, 0, 1, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 306, 60002, 0, 0, 1, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0, 307, 60002, 0, 0, 1, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0, 308, 60002, 0, 0, 1, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0, 309, 60002, 0, 0, 2, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0, 310, 60002, 0, 0, 1, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0, 311, 60002, 0, 0, 1, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0, 312, 60002, 0, 0, 1, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0, 313, 60002, 0, 0, 1, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0, 314, 60002, 0, 0, 2, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0, 315, 60002, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0, 316, 60002, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 317, 60002, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0, 318, 60002, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 0, 319, 60002, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 320, 60002, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 0, 321, 60002, 0, 0, 2, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 0, 322, 60002, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 0, 323, 60002, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 0, 324, 60002, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 325, 60002, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0, 326, 60002, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 0, 327, 60002, 0, 0, 1, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 328, 60002, 0, 0, 2, 2, "", 4, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 2, 329, 60002, 190804, 0, 0, 3, "カリン", 0, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 3, 330, 60002, 190804, 0, 0, 3, "カリン", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ささ、シェフィさまはこちらへ…", 1, 331, 80005, 105901, 0, 0, 4, "コッコロ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "コッコロさん、どこへ行くの…？\n…え？", 1, 332, 80005, 106401, 0, 0, 0, "シェフィ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 1, 333, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 2, 334, 80005, 105901, 0, 0, 4, "コッコロ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ええと…\n二人とも何をしているの？", 2, 335, 80005, 106401, 0, 0, 0, "シェフィ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 1, 336, 80005, 106001, 0, 0, 1, "キャル", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あの日…？", 3, 337, 80005, 106401, 0, 0, 0, "シェフィ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 2, 338, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "『プリンセスコネクト！\nRe:Dive』3.5周年！", 3, 339, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "やばいですね☆", 1, 340, 80005, 190801, 0, 0, 2, "カリン", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "って、なにしれっと\n参加してんのよカリン！", 2, 341, 80005, 106001, 0, 0, 1, "キャル", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 2, 342, 80005, 190801, 0, 0, 2, "カリン", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 3, 343, 80005, 190801, 0, 0, 2, "カリン", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 4, 344, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "えっ！？　ええとええと…！\nあ、あと14日…！", 4, 345, 80005, 106401, 0, 0, 0, "シェフィ", 0, 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 5, 346, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 3, 347, 80005, 105901, 0, 0, 1, "コッコロ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 4, 348, 80005, 105901, 0, 0, 1, "コッコロ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 6, 349, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "さすがでございます\nペコリーヌさま", 5, 350, 80005, 105901, 0, 0, 1, "コッコロ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 3, 351, 80005, 106001, 0, 0, 0, "キャル", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "しゃなり、しゃなり…\nふふ、案外できるものね", 5, 352, 80005, 106401, 0, 0, 2, "シェフィ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 7, 353, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "さ、次はキャルさまの\n番でございますよ", 6, 354, 80005, 105901, 0, 0, 1, "コッコロ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 4, 355, 80005, 106001, 0, 0, 0, "キャル", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 6, 356, 80005, 106401, 0, 0, 2, "シェフィ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 5, 357, 80005, 106001, 0, 0, 0, "キャル", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "いたたた…もぉお～！\nなんであたしがこんな目に～！", 6, 358, 80005, 106001, 0, 0, 0, "キャル", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 7, 359, 80005, 105901, 0, 0, 1, "コッコロ", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 7, 360, 80005, 106001, 0, 0, 0, "キャル", 0, 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 8, 361, 80005, 105901, 0, 0, 1, "コッコロ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 8, 362, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 9, 363, 80005, 105901, 0, 0, 1, "コッコロ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "聖学祭…って、なにかしら？", 7, 364, 80005, 106401, 0, 0, 2, "シェフィ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 10, 365, 80005, 105901, 0, 0, 1, "コッコロ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 8, 366, 80005, 106001, 0, 0, 0, "キャル", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 9, 367, 80005, 106001, 0, 0, 0, "キャル", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 9, 368, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "いや、そのまんますぎでしょ…", 10, 369, 80005, 106001, 0, 0, 0, "キャル", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 8, 370, 80005, 106401, 0, 0, 2, "シェフィ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 10, 371, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "えっ？　ええ…\nぎゅーっ…？", 9, 372, 80005, 106401, 0, 0, 2, "シェフィ", 0, 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 11, 373, 80005, 106001, 0, 0, 0, "キャル", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 10, 374, 80005, 106401, 0, 0, 2, "シェフィ", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 12, 375, 80005, 106001, 0, 0, 0, "キャル", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 11, 376, 80005, 105901, 0, 0, 1, "コッコロ", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 13, 377, 80005, 106001, 0, 0, 0, "キャル", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 11, 378, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 14, 379, 80005, 106001, 0, 0, 0, "キャル", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 15, 380, 80005, 106001, 0, 0, 0, "キャル", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 11, 381, 80005, 106401, 0, 0, 2, "シェフィ", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 12, 382, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 12, 383, 80005, 105901, 0, 0, 1, "コッコロ", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 16, 384, 80005, 106001, 0, 0, 0, "キャル", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 12, 385, 80005, 106401, 0, 0, 2, "シェフィ", 0, 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 13, 386, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 14, 387, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 15, 388, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 17, 389, 80005, 106001, 0, 0, 0, "キャル", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 18, 390, 80005, 106001, 0, 0, 0, "キャル", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 13, 391, 80005, 106401, 0, 0, 2, "シェフィ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 13, 392, 80005, 105901, 0, 0, 1, "コッコロ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 16, 393, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 19, 394, 80005, 106001, 0, 0, 0, "キャル", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 17, 395, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 18, 396, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 19, 397, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 5);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 4, 398, 80005, 190801, 0, 0, 2, "カリン", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 5, 399, 80005, 190801, 0, 0, 2, "カリン", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…えっ！？", 20, 400, 80005, 106001, 0, 0, 1, "キャル", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 20, 401, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 14, 402, 80005, 106401, 0, 0, 0, "シェフィ", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 14, 403, 80005, 105901, 0, 0, 4, "コッコロ", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 15, 404, 80005, 106401, 0, 0, 0, "シェフィ", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 21, 405, 80005, 106001, 0, 0, 1, "キャル", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 21, 406, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ぶっ殺すぞ！？", 22, 407, 80005, 106001, 0, 0, 1, "キャル", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 6, 408, 80005, 190801, 0, 0, 2, "カリン", 0, 6);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 15, 409, 80005, 105901, 0, 0, 1, "コッコロ", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 23, 410, 80005, 106001, 0, 0, 0, "キャル", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 16, 411, 80005, 106401, 0, 0, 2, "シェフィ", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 22, 412, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 24, 413, 80005, 106001, 0, 0, 0, "キャル", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 17, 414, 80005, 106401, 0, 0, 2, "シェフィ", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 16, 415, 80005, 105901, 0, 0, 1, "コッコロ", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 18, 416, 80005, 106401, 0, 0, 2, "シェフィ", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "もちろんでございます\n仲良くお勉強いたしましょう", 17, 417, 80005, 105901, 0, 0, 1, "コッコロ", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "しれっと教育ママが\n生まれてる…？", 25, 418, 80005, 106001, 0, 0, 0, "キャル", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 23, 419, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "え～～～～！？", 26, 420, 80005, 106001, 0, 0, 0, "キャル", 0, 7);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 27, 421, 80005, 106001, 0, 0, 0, "キャル", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 19, 422, 80005, 106401, 0, 0, 2, "シェフィ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 18, 423, 80005, 105901, 0, 0, 1, "コッコロ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 24, 424, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 20, 425, 80005, 106401, 0, 0, 2, "シェフィ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 28, 426, 80005, 106001, 0, 0, 0, "キャル", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 19, 427, 80005, 105901, 0, 0, 1, "コッコロ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 21, 428, 80005, 106401, 0, 0, 2, "シェフィ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 29, 429, 80005, 106001, 0, 0, 0, "キャル", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 30, 430, 80005, 106001, 0, 0, 0, "キャル", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 25, 431, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 31, 432, 80005, 106001, 0, 0, 0, "キャル", 0, 8);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 22, 433, 80005, 106401, 0, 0, 0, "シェフィ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…えっ？\n生放送の意気込み！？\n突然なに！？", 23, 434, 80005, 106401, 0, 0, 0, "シェフィ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 24, 435, 80005, 106401, 0, 0, 0, "シェフィ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 25, 436, 80005, 106401, 0, 0, 0, "シェフィ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 26, 437, 80005, 106401, 0, 0, 0, "シェフィ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "それから、それから…！\nぅ、ぅぅっ…！", 27, 438, 80005, 106401, 0, 0, 0, "シェフィ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "―びえぇぇ～んっ！！！", 28, 439, 80005, 106401, 0, 0, 0, "シェフィ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "どうされましたか、シェフィさま", 20, 440, 80005, 105901, 0, 0, 1, "コッコロ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ううっ…ひっく…\nママッ…！", 29, 441, 80005, 106401, 0, 0, 0, "シェフィ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 21, 442, 80005, 105901, 0, 0, 1, "コッコロ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 22, 443, 80005, 105901, 0, 0, 1, "コッコロ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 30, 444, 80005, 106401, 0, 0, 0, "シェフィ", 0, 9);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "主さま\n3.5周年まであと５日\nでございます", 23, 445, 80005, 105901, 0, 0, 0, "コッコロ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ですがその前に\n生放送がありますね", 24, 446, 80005, 105901, 0, 0, 0, "コッコロ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…生放送に向けた\n意気込みでございますか？", 25, 447, 80005, 105901, 0, 0, 0, "コッコロ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 26, 448, 80005, 105901, 0, 0, 0, "コッコロ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 27, 449, 80005, 105901, 0, 0, 0, "コッコロ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 28, 450, 80005, 105901, 0, 0, 0, "コッコロ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 29, 451, 80005, 105901, 0, 0, 0, "コッコロ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 30, 452, 80005, 105901, 0, 0, 0, "コッコロ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 31, 453, 80005, 105901, 0, 0, 0, "コッコロ", 0, 10);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "3.5周年まであと４日！\nやばいですね☆", 26, 454, 80005, 105801, 0, 0, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 32, 455, 80005, 106001, 0, 0, 1, "キャル", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 27, 456, 80005, 105801, 0, 0, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 28, 457, 80005, 105801, 0, 0, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 29, 458, 80005, 105801, 0, 0, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 33, 459, 80005, 106001, 0, 0, 1, "キャル", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 30, 460, 80005, 105801, 0, 0, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 31, 461, 80005, 105801, 0, 0, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 32, 462, 80005, 105801, 0, 0, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "だからあたしはやるなんて一言も…", 34, 463, 80005, 106001, 0, 0, 1, "キャル", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "オイッスー☆", 33, 464, 80005, 105801, 0, 0, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "聞きなさいっての！！", 35, 465, 80005, 106001, 0, 0, 1, "キャル", 0, 11);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "3.5周年まであと３日…\nそして生放送は、明日…", 31, 466, 80005, 106401, 0, 0, 2, "シェフィ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 32, 467, 80005, 106401, 0, 0, 2, "シェフィ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "おいっす！\n…こうじゃなかったわね\nええと確か…", 33, 468, 80005, 106401, 0, 0, 2, "シェフィ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 34, 469, 80005, 106401, 0, 0, 2, "シェフィ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 35, 470, 80005, 106401, 0, 0, 2, "シェフィ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "やってますねシェフィちゃん！", 34, 471, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "なによ、考えることは\nみんな同じってこと？", 36, 472, 80005, 106001, 0, 0, 0, "キャル", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 32, 473, 80005, 105901, 0, 0, 1, "コッコロ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "みんな…もちろんよ！", 36, 474, 80005, 106401, 0, 0, 2, "シェフィ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 35, 475, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 12);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 7, 476, 80005, 190801, 0, 0, 2, "カリン", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 8, 477, 80005, 190801, 0, 0, 2, "カリン", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 36, 478, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 9, 479, 80005, 190801, 0, 0, 2, "カリン", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 33, 480, 80005, 105901, 0, 0, 4, "コッコロ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "さすが【美食殿】のママですね！\nそしてキャルさんは…", 10, 481, 80005, 190801, 0, 0, 2, "カリン", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "大丈夫…大丈夫…\nあたしは大丈夫…", 37, 482, 80005, 106001, 0, 0, 1, "キャル", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 11, 483, 80005, 190801, 0, 0, 2, "カリン", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "あうー？", 37, 484, 80005, 106401, 0, 0, 0, "シェフィ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("105901,34,106001,38,190801,12", "あ…", 37, 485, 80005, 105801, 1, 0, 4, "みんな", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 39, 486, 80005, 106001, 0, 0, 1, "キャル", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 35, 487, 80005, 105901, 0, 0, 4, "コッコロ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 38, 488, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 13, 489, 80005, 190801, 0, 0, 2, "カリン", 0, 13);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 38, 490, 80005, 106401, 0, 0, 2, "シェフィ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 39, 491, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 40, 492, 80005, 106001, 0, 0, 0, "キャル", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 36, 493, 80005, 105901, 0, 0, 1, "コッコロ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 39, 494, 80005, 106401, 0, 0, 2, "シェフィ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 40, 495, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 37, 496, 80005, 105901, 0, 0, 1, "コッコロ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 41, 497, 80005, 106001, 0, 0, 0, "キャル", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "へ、へぇ…", 40, 498, 80005, 106401, 0, 0, 2, "シェフィ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 41, 499, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 41, 500, 80005, 106401, 0, 0, 2, "シェフィ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 42, 501, 80005, 106401, 0, 0, 2, "シェフィ", 0, 14);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 42, 502, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "祝！", 42, 503, 80005, 106001, 0, 0, 1, "キャル", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "3.5周年！", 38, 504, 80005, 105901, 0, 0, 4, "コッコロ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "おめでとうございます！", 43, 505, 80005, 106401, 0, 0, 0, "シェフィ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 14, 506, 80005, 190801, 0, 0, 2, "カリン", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 15, 507, 80005, 190801, 0, 0, 2, "カリン", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 43, 508, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 44, 509, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 43, 510, 80005, 106001, 0, 0, 1, "キャル", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 44, 511, 80005, 106001, 0, 0, 1, "キャル", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 44, 512, 80005, 106401, 0, 0, 0, "シェフィ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 45, 513, 80005, 106401, 0, 0, 0, "シェフィ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 39, 514, 80005, 105901, 0, 0, 4, "コッコロ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 40, 515, 80005, 105901, 0, 0, 4, "コッコロ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 41, 516, 80005, 105901, 0, 0, 4, "コッコロ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 45, 517, 80005, 105801, 0, 0, 3, "ペコリーヌ", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("105901,42,106001,45,106401,46,190801,16", "これからも\nよろしくおねがいします！", 46, 518, 80005, 105801, 1, 0, 4, "みんな", 0, 15);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 519, 60003, 190804, 0, 1, 1, "カリン", 0, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0, 520, 60003, 0, 0, 1, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 521, 60003, 0, 0, 1, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 0, 522, 60003, 0, 0, 1, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 0, 523, 60003, 0, 0, 1, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0, 524, 60003, 0, 0, 2, 2, "", 1, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0, 525, 60003, 0, 0, 1, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0, 526, 60003, 0, 0, 1, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 0, 527, 60003, 0, 0, 1, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 0, 528, 60003, 0, 0, 1, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0, 529, 60003, 0, 0, 2, 2, "", 2, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0, 530, 60003, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0, 531, 60003, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 532, 60003, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0, 533, 60003, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", 0, 534, 60003, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 535, 60003, 0, 0, 1, 2, "", 3, 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("", "今日の運勢は中吉！　悩まず行動すれば強運に恵まれる！　　　　守るよりも攻めるが吉だ！　勝負事をするにはいい運勢だな！", 0, 536, 60003, 0, 0, 2, 2, "", 3, 0);
