INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ヒューヒュー♪\nドンドンパフパフー♪", 80003, 0, 105801, 119, 1, "ペコリーヌ", 1, 0, 0, "", 0);
