INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 80003, 0, 106001, 123, 1, "キャル", 2, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 80003, 0, 105801, 124, 1, "ペコリーヌ", 2, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 80003, 0, 105801, 125, 1, "ペコリーヌ", 3, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ですからその日に向けて\n今から気分を盛り上げているのです", 80003, 0, 105901, 126, 1, "コッコロ", 3, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 80003, 0, 106001, 127, 1, "キャル", 3, 0, 0, "", 2);
