INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 80003, 0, 190801, 276, 14, "カリン", 21, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 80003, 0, 190801, 277, 14, "カリン", 22, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 80003, 0, 190801, 278, 14, "カリン", 23, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 80003, 0, 190801, 279, 14, "カリン", 24, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 80003, 0, 190801, 280, 14, "カリン", 25, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 80003, 0, 190801, 281, 14, "カリン", 26, 0, 0, "", 0);
