INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 80003, 0, 106001, 292, 15, "キャル", 57, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 80003, 0, 105801, 293, 15, "ペコリーヌ", 50, 0, 0, "", 0);
