INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("意味分かんないわよ！", 80003, 0, 106001, 296, 15, "キャル", 59, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 80003, 0, 105901, 297, 15, "コッコロ", 42, 0, 0, "", 1);
