INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 80003, 0, 105801, 299, 15, "ペコリーヌ", 52, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 80003, 0, 106001, 300, 15, "キャル", 60, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 80003, 0, 105901, 301, 15, "コッコロ", 44, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 80003, 0, 105801, 302, 15, "ペコリーヌ", 53, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("2.5周年\nありがとうございます！", 80003, 0, 105801, 303, 15, "ペコリーヌ・コッコロ・キャル", 54, 1, 0, "105901,45,106001,61", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 60002, 0, 190804, 304, 0, "カリン", 1, 0, 1, "", 1);
