INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ええと…\n二人とも何をしているの？", 80005, 0, 106401, 335, 1, "シェフィ", 2, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 80005, 0, 106001, 336, 1, "キャル", 1, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("あの日…？", 80005, 0, 106401, 337, 1, "シェフィ", 3, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 80005, 0, 105801, 338, 1, "ペコリーヌ", 2, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("『プリンセスコネクト！\nRe:Dive』3.5周年！", 80005, 0, 105801, 339, 1, "ペコリーヌ", 3, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("やばいですね☆", 80005, 0, 190801, 340, 1, "カリン", 1, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("って、なにしれっと\n参加してんのよカリン！", 80005, 0, 106001, 341, 1, "キャル", 2, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 80005, 0, 190801, 342, 1, "カリン", 2, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 80005, 0, 190801, 343, 1, "カリン", 3, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 80005, 0, 105801, 344, 1, "ペコリーヌ", 4, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("えっ！？　ええとええと…！\nあ、あと14日…！", 80005, 0, 106401, 345, 1, "シェフィ", 4, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 80005, 0, 105801, 346, 2, "ペコリーヌ", 5, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 80005, 0, 105901, 347, 2, "コッコロ", 3, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 80005, 0, 105901, 348, 2, "コッコロ", 4, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 80005, 0, 105801, 349, 2, "ペコリーヌ", 6, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("さすがでございます\nペコリーヌさま", 80005, 0, 105901, 350, 2, "コッコロ", 5, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 80005, 0, 106001, 351, 2, "キャル", 3, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("しゃなり、しゃなり…\nふふ、案外できるものね", 80005, 0, 106401, 352, 2, "シェフィ", 5, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 80005, 0, 105801, 353, 2, "ペコリーヌ", 7, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("さ、次はキャルさまの\n番でございますよ", 80005, 0, 105901, 354, 2, "コッコロ", 6, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("あ、あたしはいいわよ！\nそういうの全然分かんないし…", 80005, 0, 106001, 355, 2, "キャル", 4, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 80005, 0, 106401, 356, 2, "シェフィ", 6, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 80005, 0, 106001, 357, 2, "キャル", 5, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("いたたた…もぉお～！\nなんであたしがこんな目に～！", 80005, 0, 106001, 358, 2, "キャル", 6, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 80005, 0, 105901, 359, 2, "コッコロ", 7, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 80005, 0, 106001, 360, 2, "キャル", 7, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 80005, 0, 105901, 361, 3, "コッコロ", 8, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 80005, 0, 105801, 362, 3, "ペコリーヌ", 8, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 80005, 0, 105901, 363, 3, "コッコロ", 9, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("聖学祭…って、なにかしら？", 80005, 0, 106401, 364, 3, "シェフィ", 7, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 80005, 0, 105901, 365, 3, "コッコロ", 10, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 80005, 0, 106001, 366, 3, "キャル", 8, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 80005, 0, 106001, 367, 3, "キャル", 9, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 80005, 0, 105801, 368, 3, "ペコリーヌ", 9, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("いや、そのまんますぎでしょ…", 80005, 0, 106001, 369, 3, "キャル", 10, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 80005, 0, 106401, 370, 3, "シェフィ", 8, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 80005, 0, 105801, 371, 3, "ペコリーヌ", 10, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("えっ？　ええ…\nぎゅーっ…？", 80005, 0, 106401, 372, 3, "シェフィ", 9, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 80005, 0, 106001, 373, 4, "キャル", 11, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 80005, 0, 106401, 374, 4, "シェフィ", 10, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 80005, 0, 106001, 375, 4, "キャル", 12, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 80005, 0, 105901, 376, 4, "コッコロ", 11, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 80005, 0, 106001, 377, 4, "キャル", 13, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 80005, 0, 105801, 378, 4, "ペコリーヌ", 11, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 80005, 0, 106001, 379, 4, "キャル", 14, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 80005, 0, 106001, 380, 4, "キャル", 15, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 80005, 0, 106401, 381, 4, "シェフィ", 11, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 80005, 0, 105801, 382, 4, "ペコリーヌ", 12, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 80005, 0, 105901, 383, 4, "コッコロ", 12, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 80005, 0, 106001, 384, 4, "キャル", 16, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 80005, 0, 106401, 385, 4, "シェフィ", 12, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 80005, 0, 105801, 386, 5, "ペコリーヌ", 13, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 80005, 0, 105801, 387, 5, "ペコリーヌ", 14, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 80005, 0, 105801, 388, 5, "ペコリーヌ", 15, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 80005, 0, 106001, 389, 5, "キャル", 17, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 80005, 0, 106001, 390, 5, "キャル", 18, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 80005, 0, 106401, 391, 5, "シェフィ", 13, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 80005, 0, 105901, 392, 5, "コッコロ", 13, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 80005, 0, 105801, 393, 5, "ペコリーヌ", 16, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 80005, 0, 106001, 394, 5, "キャル", 19, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 80005, 0, 105801, 395, 5, "ペコリーヌ", 17, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 80005, 0, 105801, 396, 5, "ペコリーヌ", 18, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 80005, 0, 105801, 397, 5, "ペコリーヌ", 19, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 80005, 0, 190801, 398, 6, "カリン", 4, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 80005, 0, 190801, 399, 6, "カリン", 5, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("…えっ！？", 80005, 0, 106001, 400, 6, "キャル", 20, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 80005, 0, 105801, 401, 6, "ペコリーヌ", 20, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 80005, 0, 106401, 402, 6, "シェフィ", 14, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 80005, 0, 105901, 403, 6, "コッコロ", 14, 0, 0, "", 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 80005, 0, 106401, 404, 6, "シェフィ", 15, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 80005, 0, 106001, 405, 6, "キャル", 21, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 80005, 0, 105801, 406, 6, "ペコリーヌ", 21, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ぶっ殺すぞ！？", 80005, 0, 106001, 407, 6, "キャル", 22, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 80005, 0, 190801, 408, 6, "カリン", 6, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 80005, 0, 105901, 409, 7, "コッコロ", 15, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 80005, 0, 106001, 410, 7, "キャル", 23, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 80005, 0, 106401, 411, 7, "シェフィ", 16, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 80005, 0, 105801, 412, 7, "ペコリーヌ", 22, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 80005, 0, 106001, 413, 7, "キャル", 24, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 80005, 0, 106401, 414, 7, "シェフィ", 17, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 80005, 0, 105901, 415, 7, "コッコロ", 16, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 80005, 0, 106401, 416, 7, "シェフィ", 18, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("もちろんでございます\n仲良くお勉強いたしましょう", 80005, 0, 105901, 417, 7, "コッコロ", 17, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("しれっと教育ママが\n生まれてる…？", 80005, 0, 106001, 418, 7, "キャル", 25, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 80005, 0, 105801, 419, 7, "ペコリーヌ", 23, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("え～～～～！？", 80005, 0, 106001, 420, 7, "キャル", 26, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 80005, 0, 106001, 421, 8, "キャル", 27, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("…\nねぇコッコロさん\nキャル、どうしちゃったの？", 80005, 0, 106401, 422, 8, "シェフィ", 19, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 80005, 0, 105901, 423, 8, "コッコロ", 18, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 80005, 0, 105801, 424, 8, "ペコリーヌ", 24, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 80005, 0, 106401, 425, 8, "シェフィ", 20, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 80005, 0, 106001, 426, 8, "キャル", 28, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 80005, 0, 105901, 427, 8, "コッコロ", 19, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 80005, 0, 106401, 428, 8, "シェフィ", 21, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 80005, 0, 106001, 429, 8, "キャル", 29, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 80005, 0, 106001, 430, 8, "キャル", 30, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 80005, 0, 105801, 431, 8, "ペコリーヌ", 25, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 80005, 0, 106001, 432, 8, "キャル", 31, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 80005, 0, 106401, 433, 9, "シェフィ", 22, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("…えっ？\n生放送の意気込み！？\n突然なに！？", 80005, 0, 106401, 434, 9, "シェフィ", 23, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 80005, 0, 106401, 435, 9, "シェフィ", 24, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 80005, 0, 106401, 436, 9, "シェフィ", 25, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("とにかく落ち着いて\nスタッフさんの指示を聞いて…", 80005, 0, 106401, 437, 9, "シェフィ", 26, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("それから、それから…！\nぅ、ぅぅっ…！", 80005, 0, 106401, 438, 9, "シェフィ", 27, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("―びえぇぇ～んっ！！！", 80005, 0, 106401, 439, 9, "シェフィ", 28, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("どうされましたか、シェフィさま", 80005, 0, 105901, 440, 9, "コッコロ", 20, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ううっ…ひっく…\nママッ…！", 80005, 0, 106401, 441, 9, "シェフィ", 29, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 80005, 0, 105901, 442, 9, "コッコロ", 21, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 80005, 0, 105901, 443, 9, "コッコロ", 22, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 80005, 0, 106401, 444, 9, "シェフィ", 30, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("主さま\n3.5周年まであと５日\nでございます", 80005, 0, 105901, 445, 10, "コッコロ", 23, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ですがその前に\n生放送がありますね", 80005, 0, 105901, 446, 10, "コッコロ", 24, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("…生放送に向けた\n意気込みでございますか？", 80005, 0, 105901, 447, 10, "コッコロ", 25, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 80005, 0, 105901, 448, 10, "コッコロ", 26, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 80005, 0, 105901, 449, 10, "コッコロ", 27, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 80005, 0, 105901, 450, 10, "コッコロ", 28, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 80005, 0, 105901, 451, 10, "コッコロ", 29, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 80005, 0, 105901, 452, 10, "コッコロ", 30, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 80005, 0, 105901, 453, 10, "コッコロ", 31, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("3.5周年まであと４日！\nやばいですね☆", 80005, 0, 105801, 454, 11, "ペコリーヌ", 26, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 80005, 0, 106001, 455, 11, "キャル", 32, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 80005, 0, 105801, 456, 11, "ペコリーヌ", 27, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 80005, 0, 105801, 457, 11, "ペコリーヌ", 28, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 80005, 0, 105801, 458, 11, "ペコリーヌ", 29, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 80005, 0, 106001, 459, 11, "キャル", 33, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 80005, 0, 105801, 460, 11, "ペコリーヌ", 30, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 80005, 0, 105801, 461, 11, "ペコリーヌ", 31, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 80005, 0, 105801, 462, 11, "ペコリーヌ", 32, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("だからあたしはやるなんて一言も…", 80005, 0, 106001, 463, 11, "キャル", 34, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("オイッスー☆", 80005, 0, 105801, 464, 11, "ペコリーヌ", 33, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("聞きなさいっての！！", 80005, 0, 106001, 465, 11, "キャル", 35, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("3.5周年まであと３日…\nそして生放送は、明日…", 80005, 0, 106401, 466, 12, "シェフィ", 31, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 80005, 0, 106401, 467, 12, "シェフィ", 32, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("おいっす！\n…こうじゃなかったわね\nええと確か…", 80005, 0, 106401, 468, 12, "シェフィ", 33, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 80005, 0, 106401, 469, 12, "シェフィ", 34, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 80005, 0, 106401, 470, 12, "シェフィ", 35, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("やってますねシェフィちゃん！", 80005, 0, 105801, 471, 12, "ペコリーヌ", 34, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("なによ、考えることは\nみんな同じってこと？", 80005, 0, 106001, 472, 12, "キャル", 36, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 80005, 0, 105901, 473, 12, "コッコロ", 32, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("みんな…もちろんよ！", 80005, 0, 106401, 474, 12, "シェフィ", 36, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 80005, 0, 105801, 475, 12, "ペコリーヌ", 35, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 80005, 0, 190801, 476, 13, "カリン", 7, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 80005, 0, 190801, 477, 13, "カリン", 8, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 80005, 0, 105801, 478, 13, "ペコリーヌ", 36, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 80005, 0, 190801, 479, 13, "カリン", 9, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 80005, 0, 105901, 480, 13, "コッコロ", 33, 0, 0, "", 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("さすが【美食殿】のママですね！\nそしてキャルさんは…", 80005, 0, 190801, 481, 13, "カリン", 10, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("大丈夫…大丈夫…\nあたしは大丈夫…", 80005, 0, 106001, 482, 13, "キャル", 37, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 80005, 0, 190801, 483, 13, "カリン", 11, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("あうー？", 80005, 0, 106401, 484, 13, "シェフィ", 37, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("あ…", 80005, 0, 105801, 485, 13, "みんな", 37, 1, 0, "105901,34,106001,38,190801,12", 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 80005, 0, 106001, 486, 13, "キャル", 39, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 80005, 0, 105901, 487, 13, "コッコロ", 35, 0, 0, "", 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 80005, 0, 105801, 488, 13, "ペコリーヌ", 38, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 80005, 0, 190801, 489, 13, "カリン", 13, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 80005, 0, 106401, 490, 14, "シェフィ", 38, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 80005, 0, 105801, 491, 14, "ペコリーヌ", 39, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 80005, 0, 106001, 492, 14, "キャル", 40, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 80005, 0, 105901, 493, 14, "コッコロ", 36, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 80005, 0, 106401, 494, 14, "シェフィ", 39, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 80005, 0, 105801, 495, 14, "ペコリーヌ", 40, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 80005, 0, 105901, 496, 14, "コッコロ", 37, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 80005, 0, 106001, 497, 14, "キャル", 41, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("へ、へぇ…", 80005, 0, 106401, 498, 14, "シェフィ", 40, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 80005, 0, 105801, 499, 14, "ペコリーヌ", 41, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 80005, 0, 106401, 500, 14, "シェフィ", 41, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 80005, 0, 106401, 501, 14, "シェフィ", 42, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 80005, 0, 105801, 502, 15, "ペコリーヌ", 42, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("祝！", 80005, 0, 106001, 503, 15, "キャル", 42, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("3.5周年！", 80005, 0, 105901, 504, 15, "コッコロ", 38, 0, 0, "", 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("おめでとうございます！", 80005, 0, 106401, 505, 15, "シェフィ", 43, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 80005, 0, 190801, 506, 15, "カリン", 14, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 80005, 0, 190801, 507, 15, "カリン", 15, 0, 0, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 80005, 0, 105801, 508, 15, "ペコリーヌ", 43, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 80005, 0, 105801, 509, 15, "ペコリーヌ", 44, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 80005, 0, 106001, 510, 15, "キャル", 43, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 80005, 0, 106001, 511, 15, "キャル", 44, 0, 0, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 80005, 0, 106401, 512, 15, "シェフィ", 44, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 80005, 0, 106401, 513, 15, "シェフィ", 45, 0, 0, "", 0);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 80005, 0, 105901, 514, 15, "コッコロ", 39, 0, 0, "", 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 80005, 0, 105901, 515, 15, "コッコロ", 40, 0, 0, "", 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 80005, 0, 105901, 516, 15, "コッコロ", 41, 0, 0, "", 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 80005, 0, 105801, 517, 15, "ペコリーヌ", 45, 0, 0, "", 3);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("これからも\nよろしくおねがいします！", 80005, 0, 105801, 518, 15, "みんな", 46, 1, 0, "105901,42,106001,45,106401,46,190801,16", 4);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 60003, 0, 190804, 519, 0, "カリン", 1, 0, 1, "", 1);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 60003, 1, 0, 520, 0, "", 0, 0, 1, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 60003, 1, 0, 521, 0, "", 0, 0, 1, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 60003, 1, 0, 522, 0, "", 0, 0, 1, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 60003, 1, 0, 523, 0, "", 0, 0, 1, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 60003, 1, 0, 524, 0, "", 0, 0, 2, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 60003, 2, 0, 525, 0, "", 0, 0, 1, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 60003, 2, 0, 526, 0, "", 0, 0, 1, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 60003, 2, 0, 527, 0, "", 0, 0, 1, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 60003, 2, 0, 528, 0, "", 0, 0, 1, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 60003, 2, 0, 529, 0, "", 0, 0, 2, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 60003, 3, 0, 530, 0, "", 0, 0, 1, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 60003, 3, 0, 531, 0, "", 0, 0, 1, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 60003, 3, 0, 532, 0, "", 0, 0, 1, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 60003, 3, 0, 533, 0, "", 0, 0, 1, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", 60003, 3, 0, 534, 0, "", 0, 0, 1, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 60003, 3, 0, 535, 0, "", 0, 0, 1, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は中吉！　悩まず行動すれば強運に恵まれる！　　　　守るよりも攻めるが吉だ！　勝負事をするにはいい運勢だな！", 60003, 3, 0, 536, 0, "", 0, 0, 2, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 60003, 4, 0, 537, 0, "", 0, 0, 1, "", 2);
INSERT INTO `v1_51740ed02d5c29173d7a268113c76045fde32c8f44817c09b48d544e535e5246` VALUES ("今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 60003, 4, 0, 538, 0, "", 0, 0, 1, "", 2);
