INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (1003, 0, 1003001, 190801, "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！", 11, "2020-02-15 15:00:00", "2030-04-01 14:00:00");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (1004, 0, 1004001, 190801, "左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！", 3, "2020-02-15 15:00:00", "2030-04-01 14:00:00");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (1005, 0, 1005001, 190801, "パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。", 7, "2020-02-15 15:00:00", "2030-04-01 14:00:00");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (1006, 0, 1006001, 190801, "素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！", 2, "2020-05-31 12:00:00", "2030-04-01 14:00:00");
