INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (1010, 0, 1010001, 190801, "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪", 4, "2022-02-15 15:00:00", "2030-04-01 14:00:00");
INSERT INTO `v1_5461d31ed2a3e8faeb11e0643d48f699962c782d8e946f5942c469970f6b7bfa` VALUES (1011, 0, 1011001, 190801, "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪", 6, "2022-07-18 15:00:00", "2030-04-01 14:00:00");
